UPDATE command SET help='Syntax: .account\r\n\r\nZeigt die Berechtigungsstufe Ihres Accounts an.' WHERE name='account';
UPDATE command SET help='Syntax: .account characters [#accountId|$accountName]\r\n\r\nZeigt eine Liste aller Charaktere für einen angegebenen Account #accountId oder $accountName, oder für ausgwählten Spieler.' WHERE name='account characters';
UPDATE command SET help='Syntax: .account create $account $password\r\n\r\nErstellt einen Account mit angegebenem Passwort.' WHERE name='account create';
UPDATE command SET help='Syntax: .account delete $account\r\n\r\nLöscht den Account mit allen Charakteren.' WHERE name='account delete';
UPDATE command SET help='Syntax: .account lock [on|off]\r\n\r\nErlaubt den Login für den Account nur mit der derzeitig gebrauchten IP oder widerruft diesen Befehl.' WHERE name='account lock';
UPDATE command SET help='Syntax: .account onlinelist\r\n\r\nZeigt eine Liste aller Acccounts, die derzeit online sind.' WHERE name='account onlinelist';
UPDATE command SET help='Syntax: .account password $old_password $new_password $new_password\r\n\r\nÄndert das Passwort für Euren Account.' WHERE name='account password';
UPDATE command SET help='Syntax: .account set addon [#accountId|$accountName] #addon\r\n\r\nErlaubt dem angegebenen Benutzer (möglicherweise dem angewählten Charakter) die Erweiterungen. Mögliche Werte: 0 - normal, 1 - tbc, 2 - wotlk.' WHERE name='account set addon';
UPDATE command SET help='Syntax: .account set gmlevel [#accountId|$accountName] #level\r\n\r\nSetzt die Berechtigungsstufe des ausgewählten Spielers (kann nicht auf sich selbst angewendet werden) oder für den Account $accountName auf das Level #level.\r\n\r\n#level kann von 0 bis 3 reichen.' WHERE name='account set gmlevel';
UPDATE command SET help='Syntax: .account set password (#accountId|$accountName) $password $password\r\n\r\nSetzt ein Passwort für den angegebenen Account.' WHERE name='account set password';
UPDATE command SET help='Syntax: .achievement $playername #achivementid\r\n\r\nZeigt den Status des Erfolgs #achivementid und deren Kriterien mit Status für den angewählten Spieler oder über den Spieler-Namen.' WHERE name='achievement';
UPDATE command SET help='Syntax: .achievement add $playername #achivementid\r\n\r\nSchließt den Erfolg mit allen Kriterien für den ausgewählten Spieler oder eingegebenen Namen ab. Dieser Befehl kann nicht für Zähler-Erfolge genutzt werden.' WHERE name='achievement add';
UPDATE command SET help='Syntax: .achievement criteria add $playername #criteriaid #change\r\n\r\nSetzt den Status für nicht vollendete Kriterien auf #change für den ausgewählten Speiler oder über den Spieler-Namen. Wird #change nicht unterstützt dann werden nicht-Zähler-Kriterien auf abgeschlossen gesetzt. Bei Zähler-Kriterien auf 1.' WHERE name='achievement criteria add';
UPDATE command SET help='Syntax: .achievement criteria remove $playername #criteriaid #change\r\n\r\nEntfernt den Fortschritt des Kriteriums #criteriaid bei #change für den angewählten Spieler oder über den Spieler-Namen. Wird #change nicht unterstützt, wird das Kriterium auf 0 gesetzt.' WHERE name='achievement criteria remove';
UPDATE command SET help='Syntax: .achievement remove $playername #achivementid\r\n\r\nEntfernt den Status für den Erfolg #achivementid für den angewählten Spieler oder üben den Spieler-Namen und setzt alle Kriterien für den Erfolg zurück. Der Befehl kann auch für das Zurücksetzen von Zähler-Erfolgen verwendet werden.' WHERE name='achievement remove';
UPDATE command SET help='Syntax: .additem #itemid/[#itemname]/#shift-click-item-link #itemcount\r\n\r\nFügt die festgelegte Anzahl von Gegenständen der id #itemid (oder exakter (!) Name $itemname in Klammern, oder mit shift-click generiertem Link eines Gegenstands aus dem Inventar oder Rezept) zu Eurem oder dem Inventar des ausgewählten Charakters, hinzu. Bleibt #itemcount leer, so wird nur ein Gegenstand hinzugefügt.' WHERE name='additem';
UPDATE command SET help='Syntax: .additemset #itemsetid\r\n\r\nFügt Gegenstände eines Sets der id #itemsetid zu Eurem oder dem Inventar des ausgewählten Charakters, hinzu. Jedes Teil des Sets wird einmal hinzugefügt.' WHERE name='additemset';
-- UPDATE command SET help='Syntax: .ahbot item #itemid [$itemvalue [$addchance [$minamount [$maxamount]]]] [reset]\r\n\r\nShow/modify AHBot item. Setting $itemvalue to 0 bans item. Setting $addchance greater than 0 (0-100, default 0) overrides normal loot sources in favor of a fixed add chance. Min/max amount defaults to item stack size. Parameter \"reset\" resets item configuration.' WHERE name='ahbot item';
-- UPDATE command SET help='Syntax: .ahbot rebuild [all]\r\n\r\nExpire all auctions by ahbot except those bidded on by a player. Bidded auctions can be forced expired by using the \"all\" option. AHBot will re-fill auctions using current settings.' WHERE name='ahbot rebuild';
-- UPDATE command SET help='Syntax: .ahbot reload\r\n\r\nReload AHBot settings from configuration file.' WHERE name='ahbot reload';
-- UPDATE command SET help='Syntax: .ahbot status\r\n\r\nShow current amount of items added to the auction house by AHBot.' WHERE name='ahbot status';
UPDATE command SET help='Syntax: .announce $MessageToBroadcast\r\n\r\nSendet eine Systemnachricht im Chatfenster an alle Spieler, die online sind.' WHERE name='announce';
UPDATE command SET help='Syntax: .auction\r\n\r\nZeigt das Auktionshaus Eurer Fraktion.' WHERE name='auction';
UPDATE command SET help='Syntax: .auction alliance\r\n\r\nZeigt das Auktionshaus der Allianz, unabhängig Eurer Fraktion.' WHERE name='auction alliance';
UPDATE command SET help='Syntax: .auction goblin\r\n\r\nZeigt das neutrale Auktionshaus (der Goblins).' WHERE name='auction goblin';
UPDATE command SET help='Syntax: .auction horde\r\n\r\nZeigt das Auktionshaus der Horde, unabhängig Eurer Fraktion.' WHERE name='auction horde';
-- UPDATE command SET help='Syntax: .auction item (alliance|horde|goblin) #itemid[:#itemcount] [[[#minbid] #buyout] [short|long|verylong]\r\n\r\nAdd new item (in many stackes if amount grater stack size) to specific auction house at short|long|verylogn perios similar same settings in auction in game dialog. Created auction not have owner.' WHERE name='auction item';
UPDATE command SET help='Syntax: .aura #spellid\r\n\r\nFügt die Aura von dem Spell #spellid der angewählten Einheit hinzu.' WHERE name='aura';
UPDATE command SET help='Syntax: .ban account $Name $bantime $reason\r\nBan account kicked gleichzeitig den betroffenen Spieler.\r\n$bantime: Eine negative Zahl bewirkt einen permanenten Bann, anderenfalls verwende ein Format wie "4d20h3s".' WHERE name='ban account';
UPDATE command SET help='Syntax: .ban character $Name $bantime $reason\r\nBanned den Account und kicked den Spieler.\r\n$bantime: Eine negative Zahl bewirkt einen permanenten Bann, anderenfalls verwende ein Format wie "4d20h3s".' WHERE name='ban character';
UPDATE command SET help='Syntax: .ban ip $Ip $bantime $reason\r\nBanned die angegebene IP.\r\n$bantime: Eine negative Zahl bewirkt einen permanenten Bann, anderenfalls verwende ein Format wie "4d20h3s".' WHERE name='ban ip';
UPDATE command SET help='Syntax: .baninfo account $accountid\r\nZeigt Euch detailierte Informationen über einen speziellen Bann an.' WHERE name='baninfo account';
UPDATE command SET help='Syntax: .baninfo character $charactername\r\nZeigt Euch detailierte Informationen über einen speziellen Bann an.' WHERE name='baninfo character';
UPDATE command SET help='Syntax: .baninfo ip $ip\r\nZeigt Euch detailierte Informationen über einen speziellen Bann an.' WHERE name='baninfo ip';
UPDATE command SET help='Syntax: .bank\r\n\r\nZeigt Euch Euer Bankschliessfach an.' WHERE name='bank';
UPDATE command SET help='Syntax: .banlist account [$Name]\r\nSucht in der Bannliste nach einem Accountnamen mit einem bestimmten Muster oder zeigt eine Liste aller gebannten Accounts.' WHERE name='banlist account';
UPDATE command SET help='Syntax: .banlist character $Name\r\nSucht in der Bannliste nach einem Charakternamen mit einem bestimmten Muster. Die Angabe eines Musters wird zwingend benötigt.' WHERE name='banlist character';
UPDATE command SET help='Syntax: .banlist ip [$Ip]\r\nSucht in der Bannliste nach einer IP mit einem einem bestimmten Muster oder zeigt eine Liste aller gebannten IPs.' WHERE name='banlist ip';
UPDATE command SET help='Syntax: .cast #spellid [triggered]\r\n  Der Zauber #spellid wird auf das angewählte Ziel gewirkt. Falls kein Ziel angewählt ist, wird der Zauber auf euch ausgeführt. Falls \'triggered\' oder ein Teil davon angegeben wurde, wird der Zauber mit einer \'triggered flag\' gewirkt.' WHERE name='cast';
UPDATE command SET help='Syntax: .cast back #spellid [triggered]\r\n  Das angewählte Ziel wirkt den Zauber #spellid auf Euren Charakter. Falls \'triggered\' oder ein Teil davon angegeben wurde, wird der Zauber mit einer \'triggered flag\' gewirkt.' WHERE name='cast back';
UPDATE command SET help='Syntax: .cast dist #spellid [#dist [triggered]]\r\n  Ihr werdet den Zauber auf einen Punkt in der Distanz #dist wirken. Falls \'trigered\' oder nur ein Teil angegeben wurde, dann wird der Zauber mit einer \'triggered flag\' gewirkt. Nicht alle Zauber können als Distanzzauber gewirkt werden.' WHERE name='cast dist';
UPDATE command SET help='Syntax: .cast self #spellid [triggered]\r\nWirkt #spellid vom Ziel auf das Ziel selbst aus. Falls \'triggered\' oder ein Teil davon angegeben wurde, wird der Zauber mit einer \'triggered flag\' gewirkt.' WHERE name='cast self';
UPDATE command SET help='Syntax: .cast target #spellid [triggered]\r\n  Das angewählte Ziel wirkt den Zauber #spellid auf sein Ziel. Falls \'triggered\' oder ein Teil davon angegeben wurde, wird der Zauber mit einer \'triggered flag\' gewirkt.' WHERE name='cast target';
-- UPDATE command SET help='Syntax: .channel list [#max] [static]\r\n\r\nShow list of custom channels with amounts of players joined.' WHERE name='channel list';
-- UPDATE command SET help='Syntax: .channel static $channelname on|off\r\n\r\nEnable or disable static mode for a custom channel with name $channelname. Static custom channel upon conversion acquires a set of properties identical to global channes.' WHERE name='channel static';
UPDATE command SET help='Syntax: .character achievements [$player_name]\r\n\r\nZeigt alle abgeschlossenen Erfolge des angewählten Charakters oder des Charakters $player_name.' WHERE name='character achievements';
UPDATE command SET help='Syntax: .character customize [$name]\r\n\r\nMarkiert den angewählten Spieler oder den Spieler mit dem Namen $name um diesen beim nächsten Login anzupassen.' WHERE name='character customize';
UPDATE command SET help='Syntax: .character deleted delete #guid|$name\r\n\r\nLöscht den angewählten Charakter komplett.\r\nWird $name angegeben, werden alle Charaktere mit dem Wortlaut im Namen gelöscht. Wird $guid angegeben, wird nur der Charakter mit der GUID gelöscht.' WHERE name='character deleted delete';
UPDATE command SET help='Syntax: .character deleted list [#guid|$name]\r\n\r\nZeigt die Liste aller gelöschten Charaktere.\r\nWird $name angegeben, werden alle Charaktere mit dem Wortlaut im Namen angezeigt. Wird $guid angegeben, wird nur der Charakter mit der GUID angezeigt.' WHERE name='character deleted list';
UPDATE command SET help='Syntax: .character deleted old [#keepDays]\r\n\r\nLöscht alle Charaktere komplett, deren Löschzeit länger als $keepDays zurückliegt. Wenn $keepDays nicht angegeben wird, dann wird der vordefinierte Wert aus der mangosd.conf option \'CharDelete.KeepDays\' verwendet. Wenn der Wert in der config deaktiviert (auf 0 gesetzt) wurde, kann das command nicht ohne $keepDays benutzt werden.' WHERE name='character deleted old';
UPDATE command SET help='Syntax: .character deleted restore #guid|$name [$newname] [#new account]\r\n\r\nStellt gelöschten Charakter wieder her.\r\nWird $name angegeben, werden alle Charaktere mit dem Wortlaut im Namen wiederhergestellt. Wird $guid angegeben, wird nur der Charakter mit der GUID wiederhergestellt.\r\nWenn $newname angegeben wird, so bekommt der wiederhergestellte Character diesen Namen anstelle des Originalen. Wenn #newaccount angegeben wird, so wird der wiederhergestellte Charakter diesem Account zugewiesen. Diese Funktionen sind nur bei jeweils einem Charakter möglich!' WHERE name='character deleted restore';
UPDATE command SET help='Syntax: .character erase $name\r\n\r\nLöscht Charakter $name. Charakter wird vollständig gelöscht.' WHERE name='character erase';
UPDATE command SET help='Syntax: .character level [$playername] [#level]\r\n\r\nSetzt das Level von dem Charakter $playername (oder dem ausgewähltes Ziel, wenn kein Name angegeben wurde) um #numberoflevels (oder +1, wenn kein #numberoflevels angegeben wurde) nach oben. Wenn #numberoflevels nicht angegeben wurde, wird das Level um 1 angehoben. Wenn #numberoflevels 0 ist, wird das begonnene Level zurückgesetzt. Wenn kein Charakter angewählt wurde und kein Name angegeben wurde, wird Euer Level angehoben. Kann auch bei Charakteren, die offline sind, benutzt werden. Alle Statistiken und abhänginge Werte werden neu berechnet. Beim Levelreduzieren können gegebensfalls die Talente zurückgesetzt werden. Ebenfalls können beim Levelreduzieren auch angelegte Items, die ein höheres Level erfordern, verloren gehen.' WHERE name='character level';
UPDATE command SET help='Syntax: .character rename [$name]\r\n\r\nMarkiert den angewählten Spieler oder den Spieler mit dem namen $name um diesen beim nächsten Login umzubenennen.' WHERE name='character rename';
UPDATE command SET help='Syntax: .character reputation [$player_name]\r\n\r\nZeigt die Rufinformation für den angewählten Spieler oder den Spieler, der mit $player_name gefunden wurde, an.' WHERE name='character reputation';
UPDATE command SET help='Syntax: .character titles [$player_name]\r\n\r\nZeigt die Liste der bekannten Titel für den ausgewählenten Spieler.' WHERE name='character titles';
UPDATE command SET help='Syntax: .combatstop [$playername]\r\nStoppt den Nahkampf des angewählten Charakters. Falls kein Spieler angewählt wurde, wird der Befehl auf Euch gewirkt. Falls $playername angegeben wurde, dann wird der Befehl auf den Spieler mit $playername, der online ist, gewirkt.' WHERE name='combat stop';
UPDATE command SET help='Syntax: .commands\r\n\r\nZeigt eine Liste der verfügbaren Befehle für Eure Berechtigungsstufe an.' WHERE name='commands';
-- UPDATE command SET help='Syntax: .cooldown clear [spell id] Remove cooldown from selected unit.' WHERE name='cooldown clear';
-- UPDATE command SET help='Syntax: .cooldown clearclientside  Clear all cooldown client side only.' WHERE name='cooldown clearclientside';
-- UPDATE command SET help='Syntax: .cooldown list  Active cooldown from selected unit.' WHERE name='cooldown list';
UPDATE command SET help='Syntax: .damage $damage_amount [$school [$spellid]]\r\n\r\nFügt dem Ziel $damage zu. Falls $school und $spellid nicht angegeben wurde, dann wird normaler Meleedamage ohne irgendwelche Modifikationen ausgeführt. Falls $school angegeben wurde, wird der Schaden mit Rüstungsreduzierung modifiziert (falls school physisch ist), und mit der Absorbationsmodifikation des Zieles und das Ergebnis als Meleedamager auf das Ziel gewirkt. Falls spell angegeben wurde, wird der Schaden mofiziert und als Zauberschaden gewirkt. $spellid kann als Shift-Link verwendet werden.' WHERE name='damage';
UPDATE command SET help='Syntax: .debug anim #emoteid\r\n\r\nSpielt die Animation #emoteid für Ihren Charakter ab.' WHERE name='debug anim';
UPDATE command SET help='Syntax: .debug arena\r\n\r\nSchaltet um auf Debug Mode für Arenen. Im Debug Mode kann der GM eine Arena mit einem einzigen Spieler starten.' WHERE name='debug arena';
-- UPDATE command SET help='Syntax: .debug areatriggers\n\nToggle debug mode for areatriggers. In debug mode GM will be notified if reaching an areatrigger.' WHERE name='debug areatriggers';
UPDATE command SET help='Syntax: .debug bg\r\n\r\nSchaltet um auf Debug Mode für Schlachtfelder. Im Debug Mode kann der GM ein Schlachtfeld mit einem einzigen Spieler starten.' WHERE name='debug bg';
UPDATE command SET help='Syntax: .debug getitemvalue #itemguid #field [int|hex|bit|float]\r\n\r\nZeigt das Feld #field des Gegenstands #itemguid Eures Inventars.\r\n\r\nGib einen gültigen Typen an um das Ausgabeformat zu bestimmen: int (Dezimal-Zahl), hex (Hex-Wert), bit (Bit-String), float (Zahl). Standard-Typ ist int (Dezimal-Zahl).' WHERE name='debug getitemvalue';
UPDATE command SET help='Syntax: .debug getvaluebyindex #field [int|hex|bit|float] #value\r\n\r\nLiest den Feld-Index #field (integer) des markierten Ziels aus. Ist kein Ziel ausgewählt, wird der Wert Eures Feldes ausgelesen.\r\n\r\nGib einen gültigen Typen an um das Ausgabeformat zu bestimmen: int (Dezimal-Zahl), hex (Hex-Wert), bit (Bit-String), float (Zahl). Standard-Typ ist int (Dezimal-Zahl).' WHERE name='debug getvaluebyindex';
UPDATE command SET help='Syntax: .debug getvaluebyname #fieldname\r\n\r\nLiest den Feld-Namen #fieldname (string) des markierten Ziels aus. Ist kein Ziel ausgewählt, wird der Wert Eures Feldes ausgelesen.' WHERE name='debug getvaluebyname';
UPDATE command SET help='Syntax: .debug moditemvalue #guid #field [int|float| &= | |= | &=~ ] #value\r\n\r\nÄndert das Feld #field des Gegenstands #itemguid in deinem Inventar auf den Wert #value.\r\n\r\nGib einen gültigen Typen für die Änderung an: int (fügt oder entfernt #value als Dezimal-Zahl), float (fügt oder entfernt #value als Zahl), &= (binärisches und, setzt alle Bits im Wert auf 0 sofern der Wert nicht durch eine 1 im #value als Hex-Zahl gesetzt wurde), |= (binärisches order, setzt alle Bits im Wert auf 1 sofern der Wert auch im #value als Hex-Zahl auf 1 gesetzt wurde), &=~ (binärisches NOT, setzt alle Bits im Wert auf 0 sofern der Wert durch eine 1 im #value als Hex-Zahl gesetzt wurde). Standardvorgehen ist das Hinzufügen und Entfernen einer Zahl (int).' WHERE name='debug moditemvalue';
UPDATE command SET help='Syntax: .debug modvalue #field [int|float| &= | |= | &=~ ] #value\r\n\r\nÄndert das Feld #field des markierten Ziels auf den Wert #value. Ist kein Ziel ausgewählt, wird der Wert Eures Feldes geändert.\r\n\r\nGib einen gültigen Typen für die Änderung an: int (fügt oder entfernt #value als Dezimal-Zahl), float (fügt oder entfernt #value als Zahl), &= (binärisches und, setzt alle Bits im Wert auf 0 sofern der Wert nicht durch eine 1 im #value als Hex-Zahl gesetzt wurde), |= (binärisches order, setzt alle Bits im Wert auf 1 sofern der Wert auch im #value als Hex-Zahl auf 1 gesetzt wurde), &=~ (binärisches NOT, setzt alle Bits im Wert auf 0 sofern der Wert durch eine 1 im #value als Hex-Zahl gesetzt wurde). Standardvorgehen ist das Hinzufügen und Entfernen einer Zahl (int).' WHERE name='debug modvalue';
UPDATE command SET help='Syntax: .debug play cinematic #cinematicid\r\n\r\nSpielt die Kinematik #cinematicid für Euch. Ihr bleibt an Ort und stelle, während Eurer Geist fliegt.\r\n' WHERE name='debug play cinematic';
UPDATE command SET help='Syntax: .debug play movie #movieid\r\n\r\nSpielt den Film #movieid für Euch.' WHERE name='debug play movie';
UPDATE command SET help='Syntax: .debug play sound #soundid\r\n\r\nSpielt die Musik mit #soundid.\r\nDie Musik wird nur für Euch gespielt werden. Andere Spieler werden dies nicht hören.\r\nVorsicht: Der Client hat warscheinlich mehr als 5000 Musikstücke...' WHERE name='debug play sound';
UPDATE command SET help='Syntax: .debug setitemvalue #guid #field [int|hex|bit|float] #value\r\n\r\nSetzt das Feld #field des Items #itemguid Eures Inventars auf den Wert #value.\r\n\r\nGib einen gültigen Typen an um das Ausgabeformat zu bestimmen: int (Dezimal-Zahl), hex (Hex-Wert), bit (Bit-String), float (Zahl). Standard-Typ ist int (Dezimal-Zahl).' WHERE name='debug setitemvalue';
UPDATE command SET help='Syntax: .debug setvaluebyindex #field [int|hex|bit|float] #value\r\n\r\nSetzt den Feld-Index #field (integer) des markierten Ziels auf den Wert #value. Ist kein Ziel ausgewählt, wird der Wert Eures Feldes gesetzt.\r\n\r\nGib einen gültigen Typen an um das Ausgabeformat zu bestimmen: int (Dezimal-Zahl), hex (Hex-Wert), bit (Bit-String), float (Zahl). Standard-Typ ist int (Dezimal-Zahl).' WHERE name='debug setvaluebyindex';
UPDATE command SET help='Syntax: .debug setvaluebyname #fieldname #values\r\n\r\nSetzt den Feld-Namen #fieldname (string) des markierten Ziels auf den Wert #values. Ist kein Ziel ausgewählt, wird der Wert Eures Feldes gesetzt.' WHERE name='debug setvaluebyname';
-- UPDATE command SET help='Syntax: .debug spellcoefs #spellid\r\n\r\nShow default calculated and DB stored coefficients for direct/dot heal/damage.' WHERE name='debug spellcoefs';
UPDATE command SET help='Syntax: .debug spellmods (flat|pct) #spellMaskBitIndex #spellModOp #value\r\n\r\nSetzt clientseitig den spellmod für den spell, welches an der Stelle #spellMaskBitIndex in der spell family Maske abhängig ist, vom spellmod #spellModOp auf den Wert #value.' WHERE name='debug spellmods';
-- UPDATE command SET help='Syntax: .debug taxi\r\n\r\nToggle debug mode for taxi flights. In debug mode GM receive additional on-screen information during taxi flights.' WHERE name='debug taxi';
UPDATE command SET help='Syntax: .demorph\r\n\r\nGibt dem angewählten Spieler sein ursprüngliches Aussehen wieder.' WHERE name='demorph';
UPDATE command SET help='Syntax: .die\r\n\r\nTötet den ausgewählten Spieler. Wenn kein Spieler angewählt ist, wird der Befehl Euch töten.' WHERE name='die';
UPDATE command SET help='Syntax: .dismount\r\n\r\nLässt Euch von Eurem Reittier steigen, sofern Ihr auf einem sitzt.' WHERE name='dismount';
UPDATE command SET help='Syntax: .distance [$name/$link]\r\n\r\nZeigt den Abstand von Eurem Charakter zu der angewählten Kreatur/dem angewählten Spieler, oder dem Spieler mit dem Namen $name, oder dem Spieler/der Kreatur/dem Gameobject auf den/die/das mit einem Shift-Link mit guid verwiesen wurde, an.' WHERE name='distance';
UPDATE command SET help='Syntax: .event #event_id\r\nZeigt die Details des Events mit #event_id an.' WHERE name='event';
UPDATE command SET help='Syntax: .event list\r\nZeigt eine Liste mit den derzeitig aktiven Events an.\r\nZeigt eine Liste mit allen Events.' WHERE name='event list';
UPDATE command SET help='Syntax: .event start #event_id\r\nStartet das Event #event_id. Setzt die Startzeit des Events auf den jetzigen Moment (die Änderung wird nicht in der DB gespeichert).' WHERE name='event start';
UPDATE command SET help='Syntax: .event stop #event_id\r\nStoppt das Event #event_id. Setzt die Startzeit des Events auf eine Zeit in der Vergangenheit, so ist der jetzige Moment die Endzeit des Events (die Änderung wird nicht in der DB gespeichert).' WHERE name='event stop';
UPDATE command SET help='Syntax: .explorecheat #flag\r\n\r\nDeckt alle Karten für den angewählten Spieler auf oder versteckt sie für Ihn. Wenn kein Spieler angewählt ist, werden alle Karten für Euch versteckt oder aufgedeckt.\r\n\r\nBenutzt einen Wert von 1 für #flag um alle Karten aufzudecken, benutzt einen Wert von 0 für #flag um sie zu verstecken.' WHERE name='explorecheat';
UPDATE command SET help='Syntax: .flusharenapoints\r\n\r\nBenutzt dies, um die Arenapunkte, basierend auf den Arenateamwertungen, zu verteilen, und eine neue Woche zu starten.' WHERE name='flusharenapoints';
-- UPDATE command SET help='Syntax: .gearscore [#withBags] [#withBank]\r\n\r\nShow selected player's gear score. Check items in bags if #withBags != 0 and check items in Bank if #withBank != 0. Default: 1 for bags and 0 for bank' WHERE name='gearscore';
