-- UPDATE command SET help='Syntax: .ticket go #id\r\n\r\nAttempt to teleport to the location where GM ticket with number #id was originally created.' WHERE name='ticket go';
-- UPDATE command SET help='Syntax: .ticket goname #id\r\n\r\nAttempt to teleport to the author of the GM ticket with number #id.' WHERE name='ticket goname';
