-- UPDATE command SET help='Syntax: .ticket sort #id #categoryid\r\n\r\nAttempt to assign the GM ticket with number #id with a category by id #categoryid.' WHERE name='ticket sort';
-- UPDATE command SET help='Syntax: .ticket whisper #id $message\r\n\r\nAttempt to answer in-game GM ticket with number #id by sending whisper with $message. Ticket will be assigned regardless of author's online status.' WHERE name='ticket whisper';
-- UPDATE command SET help='Syntax: .tickets [on|off|[#categoryid #max|#max] [online]]\r\n\r\nIf "on"/"off" provided, enable or disable in-game GM ticket queue notifications and GM ticket alerts. Acts as an alias of ".tickets list" otherwise.' WHERE name='tickets';
-- UPDATE command SET help='Syntax: .tickets list [#categoryid #max|#max] [online]\r\n\r\nShow current GM tickets queue. If #categoryid is provided, show only GM tickets from that category.' WHERE name='tickets list';
-- UPDATE command SET help='Syntax: .tickets queue on|off\r\n\r\nEnable or disable GM tickets queue until next restart or administrator's command.' WHERE name='tickets queue';
UPDATE command SET help='Syntax: .titles add #title\r\nFügt den Titel #title (id oder shift-link) zur Liste der bekannten Titel des ausgwählen Spielers hinzu.' WHERE name='titles add';
UPDATE command SET help='Syntax: .titles current #title\r\nSetzt den Titel #title (id oder shift-link) als derzeitigen Titel für den ausgewählten Spieler. Wenn der Titel noch nicht bekannt ist, wird er der Liste hinzugefügt.' WHERE name='titles current';
UPDATE command SET help='Syntax: .titles remove #title\r\nLöscht den Titel #title (id oder shift-link) aus der Liste der bekannten Titel des ausgewählten Spielers.' WHERE name='titles remove';
UPDATE command SET help='Syntax: .titles setmask #mask\r\n\r\nErlaubt dem Spieler alle Titel der #mask zu benutzen.\r\n\r\n #mask=0 deaktiviert das Titel-Auswahl-Feld' WHERE name='titles setmask';
UPDATE command SET help='Syntax: .trigger [#trigger_id|$trigger_shift-link|$trigger_target_shift-link]\r\n\r\nZeigt Informationen über Areatigger mit der Id #tigger_id order zugehörigen #tigger_id (mit/über shift-link). Wird die Areatigger-Id oder Shift-Link nicht unterstützt wird der nächstliegende auf der Karte ausgewählt.' WHERE name='trigger';
UPDATE command SET help='Syntax: .trigger active\r\n\r\nZeigt eine liste mit Areatigger mit Aktivierungs-Zone einschließlich aktueller Position Eures Charakters.' WHERE name='trigger active';
UPDATE command SET help='Syntax: .trigger near [#distance]\r\n\r\nGibt alles Areatigger inm Umkreis von #distance des Charaters aus. Wird #distance nicht unterstützt wird 10 als Standard-Wert verwendet.' WHERE name='trigger near';
UPDATE command SET help='Syntax: .unaura #spellid\r\n\r\nEntfernt Aura die vom Spell #spellid hervorgerufen wird von der angewählten Einheit.' WHERE name='unaura';
UPDATE command SET help='Syntax: .unban account $Name\r\nEntbannt einen Account nach einem bestimmten Muster.' WHERE name='unban account';
UPDATE command SET help='Syntax: .unban character $Name\r\nEntbannt einen Account mit dem Charakter des angegebenen Musters.' WHERE name='unban character';
UPDATE command SET help='Syntax: .unban ip $Ip\r\nEntbannt einen Account mit der IP des angegebenen Musters.' WHERE name='unban ip';
UPDATE command SET help='Syntax: .unlearn #spell [all]\r\n\r\nVerlernt dem angewählten Spieler einen Zauber (spell) #spell. Wenn \'all\' angegeben wird, werden alle Ränge verlernt.' WHERE name='unlearn';
UPDATE command SET help='Syntax: .unmute $playerName\r\n\r\nGibt die Chatnachrichten für jeden Charakter des Accounts des Charakters $playerName.' WHERE name='unmute';
UPDATE command SET help='Syntax: .waterwalk on/off\r\n\r\nSetzt den Waterwalk-Status für den angewählten Spieler auf [on/off].' WHERE name='waterwalk';
UPDATE command SET help='Syntax: .wchange #weathertype #status\r\n\r\nSetzt das derzeitge Wetter auf #weathertype mit einer Intensität von #status.\r\n\r\n#weathertype kann sein 1 für Regen, 2 für Schnee, und 3 für Sand. #status kann von 0 für deaktiviert, und 1 für aktiviert sein.' WHERE name='wchange';
UPDATE command SET help='Syntax: .whispers on|off\r\n\r\nAktiviert/Deaktiviert der Möglichkeit eines Spielers einen GM anzuflüstern. Standardmäßig werden die Angaben von mangosd.conf verwendet.' WHERE name='whispers';
UPDATE command SET help='Syntax: .wp add [Ausgewählte Kreatur oder dbGuid] [pathId [wpOrigin] ]' WHERE name='wp add';
UPDATE command SET help='Syntax: .wp export [#creature_guid oder wähle eine Kreatur aus] $Dateiname' WHERE name='wp export';
-- LÖSCHEN UPDATE command SET help='Syntax: .wp modify [#creature_guid oder wählt eine Kreatur an]\r\nadd - Fügt einen Wegpunkt (waypoint) hinter den angewählte visuellen\r\nWartezeit $time\r\nemote ID\r\nspell ID\r\ntext1| text2| text3| text4| text5 <text>\r\nmodel1 ID\r\nmodel2 ID\r\nmove(bewegt den WP zu der Position des Spielers)\r\ndel (löscht den WP)\r\n\r\nNur ein Parameter pro Befehl!' WHERE name='wp modify';
-- UPDATE command SET help='Syntax: .wp modify command [dbGuid, id] [value]\r\nwhere command must be one of: waittime  | scriptid | orientation | del | move\r\nIf no waypoint was selected, one can be chosen with dbGuid and id.\r\nThe commands have the following meaning:\r\n waittime (Set the time the npc will wait at a point (in ms))\r\n scriptid (Set the DB-Script that will be executed when the wp is reached)\r\n orientation (Set the orientation of this point) \r\n del (Remove the waypoint from the path)\r\n move (Move the wayoint to the current position of the player)' WHERE name='wp modify';
-- LÖSCHEN UPDATE command SET help='Syntax: .wp show [#creature_guid oder wählt eine Kreatur an]\r\nOn\r\nFirst\r\nLast\r\nOff\r\nInfo\r\n\r\nUm eine Information zu gebrauchen müsst Ihr zuerst show auf "on" setzen und dann einen Visuellen-Wegpunkt auswählen und dann show info!' WHERE name='wp show';
-- UPDATE command SET help='Syntax: .wp show command [dbGuid] [pathId [wpOrigin] ]\r\nwhere command can have one of the following values\r\non (to show all related wp)\r\nfirst (to see only first one)\r\nlast (to see only last one)\r\noff (to hide all related wp)\r\ninfo (to get more info about theses wp)\r\n\r\nFor using info you have to do first show on and than select a Visual-Waypoint and do the show info!\r\nwith pathId and wpOrigin you can specify which path to show (optional)' WHERE name='wp show';
-- UPDATE command SET help='Syntax: .wr [on/off]\r\n\r\nEnable or disable whisper restriction. Whisper restriction prevents players from whispering you that are not in your friends list, or a member of your guild or party.' WHERE name='wr';
