UPDATE dbscript_string SET content_loc3='Es hätte ohne Euch Jahre gedauert das Buch des Raben zu finden, junger Sperber. Kehrt mit unserer Dankbarkeit in Eure Heimat nach Nagrand zurück.' WHERE entry=2000000070;
