UPDATE dbscript_string SET content_loc3='Der heutige Tag bringt die erste von vielen Niederlagen der Geißel! Todesritter, einst im Dienste des Lichkönigs, haben sich von seinem Griff befreit und eine neue Allianz gegen seine Tyrannei gebildet!' WHERE entry=2000000564;
