UPDATE dbscript_string SET content_loc3='Hm, das sieht schon viel besser aus. Sehen wir uns mal das Innere an.' WHERE entry=2000000649;
UPDATE dbscript_string SET content_loc3='Bei der faulen Warze von Kil\'Jaedans stinkendem Leichnam, die gesamte Rückseite fehlt! Wie kann man von jemanden erwarten, hier zu wohnen? Ich habe genug gesehen!' WHERE entry=2000000650;
UPDATE dbscript_string SET content_loc3='Dyvuun, seid Ihr das?' WHERE entry=2000000651;
UPDATE dbscript_string SET content_loc3='Ja, Zhanaa, ich bin es! Euer Signal ist sehr schwach.' WHERE entry=2000000652;
UPDATE dbscript_string SET content_loc3='Ich kann Euch kaum verstehen... <Rauschen> ...bricht ab!' WHERE entry=2000000653;
UPDATE dbscript_string SET content_loc3='Zhanaa! Ach, ich muss das hier reparieren!' WHERE entry=2000000654;
