UPDATE dbscript_string SET content_loc3='Die uralte Glyphe fällt zu Boden.' WHERE entry=2000001146;
UPDATE dbscript_string SET content_loc3='Es ist genug, Irulon.' WHERE entry=2000001147;
UPDATE dbscript_string SET content_loc3='%s entfernt seine Verkleidung.' WHERE entry=2000001148;
UPDATE dbscript_string SET content_loc3='Lord Fordring, Ihr dürft Euch nicht offenbaren!' WHERE entry=2000001149;
UPDATE dbscript_string SET content_loc3='Der Tod des Jungen und genau genommen der Tod aller Ritter, die an der Rettung der Klinge beteiligt waren, hätte verhindert werden können.' WHERE entry=2000001150;
UPDATE dbscript_string SET content_loc3='Ihr Dahinscheiden liegt mir schwer auf der Seele.' WHERE entry=2000001151;
UPDATE dbscript_string SET content_loc3='Der Weg der Freiheit war schon immer ein tragisches Spiel, Sire. Wir konnten es nicht riskieren, Euch zu verlieren. Der Kreuzzug hätte solch einen Rückschlag nicht überstanden.' WHERE entry=2000001152;
UPDATE dbscript_string SET content_loc3='%s schneidet eine Grimasse.' WHERE entry=2000001153;
UPDATE dbscript_string SET content_loc3='Ich hätte die Klinge nach Nordend tragen sollen. Die Bürde war mir auferlegt worden.' WHERE entry=2000001154;
UPDATE dbscript_string SET content_loc3='Ihr habt jedoch Recht, Irulon. Unsere Freiheit wird zweifellos noch Tausende ihrer Leben fordern.' WHERE entry=2000001155;
UPDATE dbscript_string SET content_loc3='%s hebt Aschenbringer auf.' WHERE entry=2000001156;
UPDATE dbscript_string SET content_loc3='Hört Ihr mich, Arthas? Der Argentumkreuzzug wird Euch holen! Euer Königreich wird unter dem Gewicht der Gerechtigkeit zusammenbrechen! Für das Licht!' WHERE entry=2000001157;
UPDATE dbscript_string SET content_loc3='Dank Euch, Geister.' WHERE entry=2000001158;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000001159;
UPDATE dbscript_string SET content_loc3='Diese Oger sollen verdammt sein. Im Nether sollen sie schmoren. Kann uns nicht jemand von diesen Wilden erlösen! Dies ist die fünfte Entführung in genauso vielen Tagen.' WHERE entry=2000001160;
UPDATE dbscript_string SET content_loc3='Dies ist die beste Belohnung, die wir anbieten können. Ich hoffe, jemand akzeptiert die Mission.' WHERE entry=2000001161;
UPDATE dbscript_string SET content_loc3='Großmutter, kann ich etwas für Euch tun?' WHERE entry=2000001162;
UPDATE dbscript_string SET content_loc3='Großmutter, bitte trinkt dieses Wasser.' WHERE entry=2000001163;
UPDATE dbscript_string SET content_loc3='Großmutter, Garrosh hat seinen Mut verloren. Ohne Euch wissen wir nicht weiter.' WHERE entry=2000001164;
UPDATE dbscript_string SET content_loc3='Ich werde mein bestes tun und mich um die Kinder kümmern, Großmutter.' WHERE entry=2000001165;
UPDATE dbscript_string SET content_loc3='Sagt Bescheid, General. In zwei Monaten werde ich zwei Regimenter zu Eurer Verfügung stellen.' WHERE entry=2000001166;
UPDATE dbscript_string SET content_loc3='Unsere Truppen bestehen größtenteils aus Dorfbewohnen und Bauern. Gute Männer, doch noch nicht frei von den Vorurteilen und Aberglauben ihrer Erziehung. Sie sind noch nicht bereit, mit unseren exotischeren Verbündeten Seite and Seite zu kämpfen.' WHERE entry=2000001167;
UPDATE dbscript_string SET content_loc3='Euer Angebot wird vorsichtig abgewogen werden, Herold. In der Zwischenzeit wird Eure Delegation eine beratende Rolle einnehmen.' WHERE entry=2000001168;
UPDATE dbscript_string SET content_loc3='Nun gut, General. Solltet Ihr Eure Meinung ändern, werden meine Leute mehr als bereit sein, Euch militärische Unterstützung zu leisten.' WHERE entry=2000001169;
UPDATE dbscript_string SET content_loc3='Hmm...' WHERE entry=2000001170;
UPDATE dbscript_string SET content_loc3='Das ist echt nicht leicht.' WHERE entry=2000001171;
UPDATE dbscript_string SET content_loc3='Sah das okay aus?' WHERE entry=2000001172;
UPDATE dbscript_string SET content_loc3='Das ist schwieriger, als es aussieht.' WHERE entry=2000001173;
UPDATE dbscript_string SET content_loc3='Mein Arm tut langsam weh.' WHERE entry=2000001174;
UPDATE dbscript_string SET content_loc3='Ich glaube, langsam habe ich den Dreh raus.' WHERE entry=2000001175;
UPDATE dbscript_string SET content_loc3='Könntet Ihr versuchen, etwas leiser zu sein?' WHERE entry=2000001176;
UPDATE dbscript_string SET content_loc3='Ihr lasst mich nicht schlafen, oder?' WHERE entry=2000001177;
UPDATE dbscript_string SET content_loc3='Werdet Ihr damit noch sehr viel länger weitermachen?' WHERE entry=2000001178;
UPDATE dbscript_string SET content_loc3='Ich bin einfach nur nervös. Tut mir leid.' WHERE entry=2000001179;
UPDATE dbscript_string SET content_loc3='Ich will einfach niemanden im Stich lassen.' WHERE entry=2000001180;
UPDATE dbscript_string SET content_loc3='Ich muss mich irgendwie selbst beschäftigen.' WHERE entry=2000001181;
UPDATE dbscript_string SET content_loc3='Ja, also. Was ist Euer Gewerk?' WHERE entry=2000001182;
UPDATE dbscript_string SET content_loc3='Nennt Euren Beruf.' WHERE entry=2000001183;
UPDATE dbscript_string SET content_loc3='Euer Beruf?' WHERE entry=2000001184;
UPDATE dbscript_string SET content_loc3='Eure frühere Arbeitslaufbahn, Rekrut?' WHERE entry=2000001185;
UPDATE dbscript_string SET content_loc3='Was habt Ihr getan, bevor Ihr nach Nordend gekommen seid?' WHERE entry=2000001186;
UPDATE dbscript_string SET content_loc3='Zimmermann.' WHERE entry=2000001187;
UPDATE dbscript_string SET content_loc3='Zimmermann, Sir.' WHERE entry=2000001188;
