UPDATE dbscript_string SET content_loc3='Na toll, zurück zum Zeichenbrett.' WHERE entry=2000005398;
UPDATE dbscript_string SET content_loc3='Verdammt. Wie sollen wir nur ihre Verteidigungsmaßnahmen überwinden?' WHERE entry=2000005399;
