UPDATE dbscript_string SET content_loc3='Ich habe ein Auge auf Euch, Faranell.' WHERE entry=2000005722;
UPDATE dbscript_string SET content_loc3='Keine Monströsitäten die Euch bewachen. Keine Heimlichkeiten. Keine Verschwörungen im Dunkeln.' WHERE entry=2000005723;
UPDATE dbscript_string SET content_loc3='Wenn Ihr ohne meine Erlaubnis auch nur spuckt, dann wird dieser Ort dicht gemacht. Gewaltsam.' WHERE entry=2000005724;
UPDATE dbscript_string SET content_loc3='Ich versichere Euch, Aufseher, die Königliche Apothekervereinigung wünscht sich nichts sehnlicheres als Wiedergutmachung für das tragische Missverständnis bei dem soviele Leben umgekommen sind.' WHERE entry=2000005725;
UPDATE dbscript_string SET content_loc3='Wir wollen keinen Ärger machen. Wir möchten nur unsere Untersuchungen in Ruhe fortzuführen.' WHERE entry=2000005726;
UPDATE dbscript_string SET content_loc3='Wir werden ja sehen.' WHERE entry=2000005727;
UPDATE dbscript_string SET content_loc3='Stopft dem verotteten Idioten endlich seine Klappe!' WHERE entry=2000005728;
