UPDATE dbscript_string SET content_loc3='James? James... Nein, Ihr seid nicht James, aber ich weiß wer Ihr seid...' WHERE entry=2000005746;
UPDATE dbscript_string SET content_loc3='Ihr seid die Person, die diejenigen ausfindig gemacht hat, die uns das hier angetan haben.' WHERE entry=2000005747;
UPDATE dbscript_string SET content_loc3='Ich habe es immer und immer wieder versucht, Jim zu sagen... allen zu sagen... wer hinter der ganzen Sache steckt, aber ich habe es einfach nicht geschafft...' WHERE entry=2000005748;
UPDATE dbscript_string SET content_loc3='Danke, dass Ihr uns und Jim geholfen habt. Falls Ihr ihn seht, richtet ihm bitte aus, dass der kleine Jimmy und ich ihn lieben und dass wir auf ihn warten.' WHERE entry=2000005749;
UPDATE dbscript_string SET content_loc3='Mami, wann werden wir Papa wiedersehen?' WHERE entry=2000005750;
