UPDATE dbscript_string SET content_loc3='Das Reich der Schatten wartet...' WHERE entry=2000000410;
UPDATE dbscript_string SET content_loc3='Was sollen wir machen? Wir haben alles verloren?' WHERE entry=2000000411;
UPDATE dbscript_string SET content_loc3='Havenau ist verloren!' WHERE entry=2000000412;
UPDATE dbscript_string SET content_loc3='Wir kämpfen! Wir drängen sie zurück, so wie wir es immer getan haben!' WHERE entry=2000000413;
UPDATE dbscript_string SET content_loc3='So viel also zum Scharlachroten \"Paradies\"!' WHERE entry=2000000414;
UPDATE dbscript_string SET content_loc3='So viel kann ich Euch jetzt schon versprechen, Quimby, meine Stimme bekommt Ihr bei der nächsten Wahl nicht!' WHERE entry=2000000415;
UPDATE dbscript_string SET content_loc3='Das ist ein Schlag ins Gesicht! Wir fordern Antworten!' WHERE entry=2000000416;
UPDATE dbscript_string SET content_loc3='Wohin fahren diese Schiffe, Quimby? Sagt uns die Wahrheit!' WHERE entry=2000000417;
UPDATE dbscript_string SET content_loc3='Wenn Ihr nicht herauskommt, werden wir diesen Ort auseinandernehmen!' WHERE entry=2000000418;
UPDATE dbscript_string SET content_loc3='Ich will auf das letzte Schiff! Verlasst uns nicht!' WHERE entry=2000000419;
UPDATE dbscript_string SET content_loc3='Alle sterben! Helft uns!' WHERE entry=2000000420;
UPDATE dbscript_string SET content_loc3='Hört mit dem Lügen auf, Quimby! Wir wollen Antworten!' WHERE entry=2000000421;
UPDATE dbscript_string SET content_loc3='Was ist der \"Scharlachrote Morgen\"? Wir verdienen es, die Wahrheit zu erfahren, Quimby!' WHERE entry=2000000422;
UPDATE dbscript_string SET content_loc3='Ihr seid nutzlos, Quimby!' WHERE entry=2000000423;
UPDATE dbscript_string SET content_loc3='QUIMBY!!!!!!' WHERE entry=2000000424;
UPDATE dbscript_string SET content_loc3='Uns wurde Schutz versprochen! Sicherheit!' WHERE entry=2000000425;
