UPDATE dbscript_string SET content_loc3='Ihr seid ein Monster!' WHERE entry=2000000496;
UPDATE dbscript_string SET content_loc3='Bitte! Verschont mich! Ich...' WHERE entry=2000000497;
UPDATE dbscript_string SET content_loc3='Wi... Wird es wehtun?' WHERE entry=2000000498;
UPDATE dbscript_string SET content_loc3='Es prickelt...' WHERE entry=2000000499;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000000500;
UPDATE dbscript_string SET content_loc3='Ist es zu spät, meine Meinug zu ändern? Wie wär\'s wenn Ihr mich stattdessen tötet?' WHERE entry=2000000501;
UPDATE dbscript_string SET content_loc3='Der Schrecken! DER SCHRECKEN!' WHERE entry=2000000502;
UPDATE dbscript_string SET content_loc3='Mit Schmeicheleien erreicht Ihr gar nichts.' WHERE entry=2000000503;
UPDATE dbscript_string SET content_loc3='Werdet nicht albern. Wo würdet Ihr hingehen, wenn ich Euch verschonte? Wir haben gerade erst Eure Zivilisation ausgelöscht, erinnert Ihr Euch?' WHERE entry=2000000504;
UPDATE dbscript_string SET content_loc3='So wisst Ihr, dass es funktioniert.' WHERE entry=2000000505;
UPDATE dbscript_string SET content_loc3='Oh ja. Sehr sogar. Der Schmerz wird Euch vermutlich ohnmächtig werden lassen - daher der Bottich voll Schleim, in dem Ihr schwimmt!' WHERE entry=2000000506;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000000507;
UPDATE dbscript_string SET content_loc3='Ich WERDE Euch töten. Wozu die Eile?' WHERE entry=2000000508;
UPDATE dbscript_string SET content_loc3='Was... sein geschehen...' WHERE entry=2000000509;
