UPDATE dbscript_string SET content_loc3='Mami lässt nicht zu, dass dir etwas zustößt, Liebchen.' WHERE entry=2000001096;
UPDATE dbscript_string SET content_loc3='Vater, gib mir die Kraft, weiterzumachen.' WHERE entry=2000001097;
