UPDATE dbscript_string SET content_loc3='Juhu! Wir lieben dich! Du hast uns gerettet!' WHERE entry=2000001402;
UPDATE dbscript_string SET content_loc3='Ich glaube, diese seltsamen großen Leute wollten uns essen! Igitt!' WHERE entry=2000001403;
UPDATE dbscript_string SET content_loc3='Eklig!' WHERE entry=2000001404;
UPDATE dbscript_string SET content_loc3='Jippie!' WHERE entry=2000001405;
UPDATE dbscript_string SET content_loc3='Juhu, wir sind frei!' WHERE entry=2000001406;
UPDATE dbscript_string SET content_loc3='Strengt euch mehr an, ihr faulen Bastarde!' WHERE entry=2000001407;
UPDATE dbscript_string SET content_loc3='Diese Katapulte sollten am besten GESTERN fertig gestellt werden!' WHERE entry=2000001408;
UPDATE dbscript_string SET content_loc3='Arbeite, du faules Schwein!' WHERE entry=2000001409;
UPDATE dbscript_string SET content_loc3='Beeilt euch! Arbeit, ARBEIT!' WHERE entry=2000001410;
UPDATE dbscript_string SET content_loc3='ARBEITET SCHNELLER!' WHERE entry=2000001411;
UPDATE dbscript_string SET content_loc3='Was sollen wir überhaupt mit diesen Dingern beschießen?' WHERE entry=2000001412;
UPDATE dbscript_string SET content_loc3='Ich nichts wissen. Aufseher vielleicht tun, ich hoffen jedenfalls!' WHERE entry=2000001413;
UPDATE dbscript_string SET content_loc3='Niemals wieder! Ich werde es nicht nochmal zulassen, dass ein verletzter Soldat an die Front zurückkehrt! Dieses Mal meine ich es ernst!' WHERE entry=2000001414;
UPDATE dbscript_string SET content_loc3='Legt einen Schritt zu! Diese Ballisten werden sich nicht von selbst reparieren!' WHERE entry=2000001415;
UPDATE dbscript_string SET content_loc3='Macht so weiter, Jungs! Die Vorräte müssen alle verteilt und die Waffen repariert werden!' WHERE entry=2000001416;
UPDATE dbscript_string SET content_loc3='Sire! Wozu bin ich in dieser Armee, wenn ich nicht ausrücken darf, um für Azeroth zu kämpfen? Dafür habe ich mich freiwillig gemeldet und genau das werde ich jetzt tun!' WHERE entry=2000001417;
UPDATE dbscript_string SET content_loc3='Nun macht schon! Unsere Jungs da draußen sind auf den Nachschub angewiesen!' WHERE entry=2000001418;
UPDATE dbscript_string SET content_loc3='%s tritt hinter Euch ein, die Axt kampfbereit in der Hand.' WHERE entry=2000001419;
UPDATE dbscript_string SET content_loc3='%s öffnet das faule Ei und beginnt zum Entsetzen und Ekel aller den verfaulten Inhalt zu trinken.' WHERE entry=2000001420;
UPDATE dbscript_string SET content_loc3='Das war weltbewegend... Bitte entschuldigt mich für eine Minute.' WHERE entry=2000001421;
UPDATE dbscript_string SET content_loc3='%s entfährt der übelriechendste Rülpser, der je gerochen oder gehört wurde.' WHERE entry=2000001422;
UPDATE dbscript_string SET content_loc3='Gut, dann zurück zum Geschäft.' WHERE entry=2000001423;
