UPDATE dbscript_string SET content_loc3='Hallo, Pahu.' WHERE entry=2000001681;
UPDATE dbscript_string SET content_loc3='Hallo! Ich habe Eure Rationen direkt hier.' WHERE entry=2000001682;
UPDATE dbscript_string SET content_loc3='Ein Paket wechselt den Besitzer zwischen Pahu und dem Fernläufer, der es an einem sicheren Ort verstaut.' WHERE entry=2000001683;
UPDATE dbscript_string SET content_loc3='Keine Gnade.' WHERE entry=2000001684;
