UPDATE dbscript_string SET content_loc3='Zögert nicht.' WHERE entry=2000001691;
UPDATE dbscript_string SET content_loc3='Gebt Euch Eurer Wut hin!' WHERE entry=2000001692;
UPDATE dbscript_string SET content_loc3='Schlagt es nieder!' WHERE entry=2000001693;
