UPDATE dbscript_string SET content_loc3='Kommt, Landgren, rückt Eure Seele raus, damit ich Euch eine wirklich wichtige Frage stellen kann.' WHERE entry=2000001697;
UPDATE dbscript_string SET content_loc3='IHR WERDET MIR SAGEN, WO WESTWIND IST, ODER ICH WERDE EURE SEELE VERNICHTEN!' WHERE entry=2000001698;
UPDATE dbscript_string SET content_loc3='Nein.' WHERE entry=2000001699;
UPDATE dbscript_string SET content_loc3='Dann lasst Ihr mir keine andere Wahl. Ich sage nicht, dass es mir keinen Spaß machen wird.' WHERE entry=2000001700;
UPDATE dbscript_string SET content_loc3='STOP! Das ist es nicht wert. Ich sage Euch, wo er ist.' WHERE entry=2000001701;
UPDATE dbscript_string SET content_loc3='Sehr gut. Sagt es mir.' WHERE entry=2000001702;
UPDATE dbscript_string SET content_loc3='Am südlichen Ende der Insel gibt es eine Höhle... eine versteckte Vertiefung. Der Großadmiral hat sich dort eingegraben und bereitet sich auf die letzte Schlacht mit dem Lichkönig vor.' WHERE entry=2000001703;
UPDATE dbscript_string SET content_loc3='Eine versteckte Vertiefung? Wie interessant. Ihr habt Euren Zweck erfüllt, Landgren, aber ich fürchte, diesmal wird es keine Wiederbelebung geben!' WHERE entry=2000001704;
UPDATE dbscript_string SET content_loc3='AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................' WHERE entry=2000001705;
UPDATE dbscript_string SET content_loc3='[Rohes Vrykul] Bjorns mottar haelgond sygar!' WHERE entry=2000001706;
UPDATE dbscript_string SET content_loc3='Niemand spricht so über meine Mutter!' WHERE entry=2000001707;
UPDATE dbscript_string SET content_loc3='Zum Glück war ich in der Nähe. Wie kann ich helfen?' WHERE entry=2000001708;
UPDATE dbscript_string SET content_loc3='Ich habe mich in der Nähe aufgehalten, falls Ihr reden wollt.' WHERE entry=2000001709;
UPDATE dbscript_string SET content_loc3='Ihr habt gerufen?' WHERE entry=2000001710;
UPDATE dbscript_string SET content_loc3='Wie kann ich Euch helfen, $N?' WHERE entry=2000001711;
UPDATE dbscript_string SET content_loc3='Hallo.' WHERE entry=2000001712;
UPDATE dbscript_string SET content_loc3='Lebt wohl, $N.' WHERE entry=2000001713;
UPDATE dbscript_string SET content_loc3='Der Wind ruft mich.' WHERE entry=2000001714;
UPDATE dbscript_string SET content_loc3='Es gibt etwas anderes, um das ich mich kümmern muss.' WHERE entry=2000001715;
UPDATE dbscript_string SET content_loc3='Ich muss gehen. Viel Glück!' WHERE entry=2000001716;
UPDATE dbscript_string SET content_loc3='Ich muss gehen. Seid vorsichtig.' WHERE entry=2000001717;
UPDATE dbscript_string SET content_loc3='Bis bald.' WHERE entry=2000001718;
UPDATE dbscript_string SET content_loc3='Was für ein Wesen ist das? Sieht aus wie ein lila Goblin.' WHERE entry=2000001719;
UPDATE dbscript_string SET content_loc3='Was genau werden wir damit denn beschießen?' WHERE entry=2000001720;
UPDATE dbscript_string SET content_loc3='Ich frage mich, wie weit das diese weiße Katze schleudern würde...' WHERE entry=2000001721;
UPDATE dbscript_string SET content_loc3='Haben wir überhaupt Munition dafür?' WHERE entry=2000001722;
UPDATE dbscript_string SET content_loc3='Eure Überreste werden an die Haie in der Dolchbucht verfüttert werden!' WHERE entry=2000001723;
UPDATE dbscript_string SET content_loc3='Die Opfer kommen jetzt schon von ganz alleine zu uns? Habt Ihr völlig den Verstand verloren!?' WHERE entry=2000001724;
UPDATE dbscript_string SET content_loc3='Ihr wagt es, die Winterskorn herauszufordern?! Ich werde Euch mit Eurer eigenen, kümmerlichen Flagge pfählen!' WHERE entry=2000001725;
UPDATE dbscript_string SET content_loc3='Geht nun! Folgt dem Geheiß Eurer Meister!' WHERE entry=2000001726;
UPDATE dbscript_string SET content_loc3='Noch nicht, Ymiron. Für Euch habe ich andere Pläne. Im Turm Utgarde nützt Ihr mir mehr. Und wenn das Ungeziefer lange genug lebt, um Euch erneut zu finden, könnt Ihr auch Eure Frau rächen.' WHERE entry=2000001727;
UPDATE dbscript_string SET content_loc3='Bogenschützen, bereithalten! Nicht feuern!' WHERE entry=2000001728;
UPDATE dbscript_string SET content_loc3='Welcher Narr wagt es, das Herrschaftsgebiet Seiner Majestät unangekündigt zu betreten?' WHERE entry=2000001729;
UPDATE dbscript_string SET content_loc3='Ah, jedoch seid Ihr es, $gder:die; in unseres Meisters Territorium eindringt. Allein für diesen Verstoß könnte er Euch im Nu auslöschen! Arthas hegt wenig Geduld und Liebe für seine entlaufenen Sklaven.' WHERE entry=2000001730;
UPDATE dbscript_string SET content_loc3='Allerdings... hat er von Eurem Sieg über Sturmwinds Nordflotte erfahren und ihm dünkt, als hättet Ihr Potenzial - Potenzial, auf dass Ihr Vernunft annehmet und Sylvanas kindischer Rebellion den Rücken kehrt.' WHERE entry=2000001731;
UPDATE dbscript_string SET content_loc3='Arthas ist willens, Euch Macht anzubieten, die jenseits Eurer Vorstellung liegt. Die mickrige Armee, die Ihr hier anführt, würde angesichts der Phalanx verblassen, die Ihr befehlet, solltet Ihr in den Schoß der Geißel zurückkehren.' WHERE entry=2000001732;
UPDATE dbscript_string SET content_loc3='Seht die Vrykul! Ein Volk, dessen Beherrschung von Krieg und Zerstörung einer Kunstform gleicht. Schon haben sie sich auf die Seite des Lichkönigs geschlagen! Ihre Wohnstätten umgeben Euch und ihre Zahl ist mit Leichtigkeit fünf Mal die Eure.' WHERE entry=2000001733;
UPDATE dbscript_string SET content_loc3='Die Wahl liegt bei Euch, Anselm. Kehrt zu des Lichkönigs Armee zurück und kämpft an seiner Seite oder bleibt Eurer so genannten Königin treu und bekommt ihren Zorn zu spüren, wenn sie Euch aus ihrem Heimatland verjagen!' WHERE entry=2000001734;
UPDATE dbscript_string SET content_loc3='Ist das schon alles, was Ihr sagen wolltet?' WHERE entry=2000001735;
UPDATE dbscript_string SET content_loc3='Schickt diese Mistkerle zurück in die Hölle! Feuer nach eigenem Ermessen!' WHERE entry=2000001736;
UPDATE dbscript_string SET content_loc3='Solch eine unnütze Geste.' WHERE entry=2000001737;
UPDATE dbscript_string SET content_loc3='Hört die letzten Atemzüge Eurer Männer, während ich mich an ihren Seelen labe!' WHERE entry=2000001738;
UPDATE dbscript_string SET content_loc3='Das wird nicht das letzte Mal sein, dass Ihr von mir hört. Ich werde wiederkommen und auf Euren Leichnam spucken, nachdem Utgardes Armeen über Euch hereingebrochen sind!' WHERE entry=2000001739;
UPDATE dbscript_string SET content_loc3='Berichte über den Test, der an der feindlichen Flotte verübt wurde, sind eingetroffen, Sir!' WHERE entry=2000001740;
UPDATE dbscript_string SET content_loc3='Ausgezeichnet. Wie sieht\'s aus? Wie viele Tote haben wir?' WHERE entry=2000001741;
UPDATE dbscript_string SET content_loc3='Tja... Die gute Nachricht ist, dass wir die Unterstützungstruppen zum Umdrehen gezwungen haben.' WHERE entry=2000001742;
UPDATE dbscript_string SET content_loc3='Na, spuckt es aus! Was sind die schlechten Neuigkeiten?' WHERE entry=2000001743;
UPDATE dbscript_string SET content_loc3='Die Seuche war nicht so ganz... tödlich. Jedoch berichtet man von schädlichen Wirkungen wie Übelkeit, Gleichgewichtsverlust und schweren Magenschmerzen.' WHERE entry=2000001744;
UPDATE dbscript_string SET content_loc3='Heftige Bauchschmerzen?' WHERE entry=2000001745;
UPDATE dbscript_string SET content_loc3='HEFTIGE BAUCHSCHMERZEN?' WHERE entry=2000001746;
UPDATE dbscript_string SET content_loc3='Wir sind die Königliche Apothekervereinigung! Das Verursachen von Bauchschmerzen ist nicht Teil unseres Aufgabengebietes!' WHERE entry=2000001747;
UPDATE dbscript_string SET content_loc3='Bin ich denn nur von Amateuren umgeben? Bis morgen früh will ich eine komplette Analyse des neuen Stranges haben! Auf geht\'s, Ravien!' WHERE entry=2000001748;
UPDATE dbscript_string SET content_loc3='Ja, Sir!' WHERE entry=2000001749;
UPDATE dbscript_string SET content_loc3='Das Langhaus wurde zerstört! In der Ferne ertönt der Alarm.' WHERE entry=2000001750;
UPDATE dbscript_string SET content_loc3='Verteidigt Euch, Soldat! Holt sie mit der feurigen Lanze vom Himmel! Lasst sie ihre eigene Medizin kosten!' WHERE entry=2000001751;
UPDATE dbscript_string SET content_loc3='Das Dockhaus wurde direkt getroffen! Die Verteidiger der Drachenschinder wurden alarmiert!' WHERE entry=2000001752;
UPDATE dbscript_string SET content_loc3='Weicht keinen Zentimeter zurück, Soldat! Pustet sie vom Himmel!' WHERE entry=2000001753;
UPDATE dbscript_string SET content_loc3='Das Vrykullager steht in Flammen! Die Verteidiger der Drachenschinder wurden alarmiert!' WHERE entry=2000001754;
UPDATE dbscript_string SET content_loc3='Blast sie vom Himmel! Lasst sie nicht in Reichweite kommen, Soldat!' WHERE entry=2000001755;
UPDATE dbscript_string SET content_loc3='%s schüttelt ihren Kopf.' WHERE entry=2000001756;
UPDATE dbscript_string SET content_loc3='Eine echte Schande, was sie mit dem Teppich gemacht haben.' WHERE entry=2000001757;
UPDATE dbscript_string SET content_loc3='Abschuss.' WHERE entry=2000001758;
