UPDATE dbscript_string SET content_loc3='JUNGE, JUNGE, JUNGE! Momentchen mal, Leute!' WHERE entry=2000001760;
UPDATE dbscript_string SET content_loc3='Die Zwerge fangen alle an, lauthals zu lachen.' WHERE entry=2000001761;
UPDATE dbscript_string SET content_loc3='Oh, Ihr guten Herren aus Stein und Stahl, was... Ich wiederhole... WAS wollt Ihr damit machen?' WHERE entry=2000001762;
UPDATE dbscript_string SET content_loc3='Ich brech\' zusammen! Bitte macht, dass es aufhört!' WHERE entry=2000001763;
UPDATE dbscript_string SET content_loc3='Wenn doch nur unser Papa noch leben würde, um dies zu sehen!' WHERE entry=2000001764;
UPDATE dbscript_string SET content_loc3='Mann, das funktioniert doch nie. Diese Eisenzwerge werden niemals auf dieses Stück Schrott reinfallen.' WHERE entry=2000001765;
UPDATE dbscript_string SET content_loc3='Ihr seid hier nicht in Eurem Element, Lebronski! Und außerdem noch unerwünscht! Diese Eisenzwerge werden nicht einmal wissen, wie ihnen geschieht! Warum macht Ihr nicht damit weiter, das Mysterium des riesigen Haufens auf Eurem Teppich zu lüften, und lasst die echten Wissenschaftler weiter ihrer Arbeit nachgehen.' WHERE entry=2000001766;
UPDATE dbscript_string SET content_loc3='Was glaubt Ihr, was Ihr da macht, Mann? Lebronski gefällt das GAR NICHT, wenn Ihr Eure losen Metallteile über seinen Teppich schleppt.' WHERE entry=2000001767;
UPDATE dbscript_string SET content_loc3='Super, Mann. Dieser Blechkasten schafft es vielleicht doch noch...' WHERE entry=2000001768;
UPDATE dbscript_string SET content_loc3='Eisenrunenkonstrukt - Modell U-9207 - gefällt der Ton Eurer Stimme nicht. Beginne mit der totalen Vernichtung Eures Teppichs, Lebronski.' WHERE entry=2000001769;
