UPDATE dbscript_string SET content_loc3='Kommt! Seht Eurem Tod ins Gesicht... wenn Ihr den Mut habt!' WHERE entry=2000003249;
UPDATE dbscript_string SET content_loc3='Ja, kommt. Mein Meister und ich werden warten.' WHERE entry=2000003250;
