UPDATE dbscript_string SET content_loc3='Ihr müsst unbedingt dem Elektrodingsda-Apparat einen Besuch abstatten und Euch dort einen Nachschlüssel und einen R.C.V.K. besorgen, denn sonst könnt Ihr Euren Auftrag nicht erfüllen!' WHERE entry=2000003310;
