UPDATE dbscript_string SET content_loc3='Lose! Bei mir könnt Ihr Eure Dunkelmond-Gewinnlose einlösen! Wunderbare und exotische Preise warten nur darauf von Euch abgeholt zu werden!' WHERE entry=2000005212;
UPDATE dbscript_string SET content_loc3='Fürchtet Euch nicht! Alle Schicksale können für diejenigen, die wissen wie, wargenommen werden.' WHERE entry=2000005213;
UPDATE dbscript_string SET content_loc3='Hey! Schmeckt euch die Sauce? Die Sauce ist gut, oder etwa nicht?' WHERE entry=2000005214;
UPDATE dbscript_string SET content_loc3='Der was-bringer? Oh, ASCHENbringer? Nie davon gehört... Was? Hört auf mich zu nerven!' WHERE entry=2000005215;
UPDATE dbscript_string SET content_loc3='Amüsiert sich auch jeder? Fantastisch! Willkommen auf dem Dunkelmond-Jahrmarkt, dem größten Ereignis in ganz Azeroth! Sprecht unbedingt mit Yebb und seinen Freunden, wenn ihr Yebb Nabetands Kuriositätenkabinett besucht.' WHERE entry=2000005216;
UPDATE dbscript_string SET content_loc3='Hallo Freunde und willkommen auf dem unglaublichsten Jahrmarkt, den Azeroth je gesehen hat!$B$BKommt herbei und seht Euch um. Erlebt das, wovon die meisten nur träumen! Erfreut Euch an den Wundern und Mysterien aus aller Welt! Der Dunkelmond-Jahrmarkt hat keine Kosten und Mühen gescheut, um Alt und Jung eine Show sondergleichen zu bieten!' WHERE entry=2000005217;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005218; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Das sollte Telaar die Hilfe bringen, die sie benötigen!' WHERE entry=2000005219;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005220; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Der Dunkelmond-Jahrmarkt ist das größte Ereignis in der ganzen Scherbenwelt' WHERE entry=2000005221;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005222; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Vergesst nicht Erfrischungen und Souvenirs zu kaufen!' WHERE entry=2000005223;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005224; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005225; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005226; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Kommt zu mir Ihr wahren Gläubigen, und seht was die Zukunft Euch bringen wird!' WHERE entry=2000005227;
UPDATE dbscript_string SET content_loc3='Ich hätte die durchsichtige Pille nehmen sollen.' WHERE entry=2000005228;
UPDATE dbscript_string SET content_loc3='Ich werde nicht gut genug bezahlt für diese Ausscheidungen des Stiers!' WHERE entry=2000005229;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005230; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005231; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Je länger Ihr wartet, umso weniger Zukunft habt Ihr für mich zum vorhersagen.' WHERE entry=2000005232;
UPDATE dbscript_string SET content_loc3='Lehnt Euch einfach zurück und hört dieses ta... *hic* dieses tai... *hic* dieses taiii *hic* Verdammt!' WHERE entry=2000005233;
UPDATE dbscript_string SET content_loc3='Ich kann diese Steuern nicht annehmen! F.I.C.A. für mich? F.I.C.A. für EUCH!' WHERE entry=2000005234;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005235; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005236; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005237; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005238; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005239; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005240; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005241; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Hmm... ich frage mich...' WHERE entry=2000005242;
UPDATE dbscript_string SET content_loc3='Nun, wo ist es?' WHERE entry=2000005243;
UPDATE dbscript_string SET content_loc3='Dumme Magier... verbrauchen alle Reagenzien und besorgen nie Ersatz.' WHERE entry=2000005244;
UPDATE dbscript_string SET content_loc3='Wir täten gut daran, nicht übermütig zu werden. Es gibt noch viel zu lernen über das Land und den Gegner.' WHERE entry=2000005245;
UPDATE dbscript_string SET content_loc3='Vorsicht sollte nicht bedeuten, dass wir unserem Feind unnötige Vorteile verschaffen.' WHERE entry=2000005246;
UPDATE dbscript_string SET content_loc3='Wir werden handeln, wenn wir wissen, was wir brauchen. Die Druiden haben bewiesen, dass sie weise sind und in der Lage, ihre eigenen Entscheidungen zu treffen.' WHERE entry=2000005247;
UPDATE dbscript_string SET content_loc3='Meine Koffer sind gepackt, ich bin abmarschbereit.' WHERE entry=2000005248;
UPDATE dbscript_string SET content_loc3='Das Schiff ist noch nicht bereit. Wir müssen den Zündzeitraum noch einstellen, damit Ihr da oben nicht plötzlich alleine in der Luft hängt.' WHERE entry=2000005249;
UPDATE dbscript_string SET content_loc3='Was wollt Ihr tun? All diese Wissenschaft... Ich verstehe das nicht. Ich mache das hier doch nur als Arbeit. Fünf Tage die Woche.' WHERE entry=2000005250;
UPDATE dbscript_string SET content_loc3='Tatsächlich wird es lange, lange Zeit dauern, bis wir starten können. Vielleicht solltet Ihr zu Eurer Familie zurückkehren.' WHERE entry=2000005251;
UPDATE dbscript_string SET content_loc3='Ich kann nicht. Ich bin nicht der Mann, für den sie mich zuhause halten. Und ich habe sie nicht mit hierher gebracht.' WHERE entry=2000005252;
UPDATE dbscript_string SET content_loc3='Warum nicht? Eine Familie kann Euch Kraft geben.' WHERE entry=2000005253;
UPDATE dbscript_string SET content_loc3='Das ist nicht der richtige Ort, um Kinder aufzuziehen. Es ist kalt und keiner kann sich um sie kümmern.' WHERE entry=2000005254;
UPDATE dbscript_string SET content_loc3='Könntet Ihr sie nicht aufziehen?' WHERE entry=2000005255;
UPDATE dbscript_string SET content_loc3='Oh nein, nein, nein... ich bin ein Düsentrieb.' WHERE entry=2000005256;
UPDATE dbscript_string SET content_loc3='Nun, wie ich bereits sagte; es wird eine Weile dauern, bis wir das Schiff abflugbereit haben. Wir halten Euch auf dem Laufenden.' WHERE entry=2000005257;
UPDATE dbscript_string SET content_loc3='Ich entdecke eine Spur von Chronitonpartikeln. Hmm, vielleicht, wenn wir einen umgekehrten Phaseninverter verwenden, während wir unsere eigene Chronossphäre umkehren, sollten wir in der Lage sein, die Partikel zu eliminieren. Das setzt natürlich voraus, dass wir erst einmal eine Chronossphäre konstruieren.' WHERE entry=2000005258;
UPDATE dbscript_string SET content_loc3='Hmm... Wie es scheint, hat die von uns verwendete Syncotroneinheit keinen nennenswerten Effekt auf die gravimetrischen und gravitonischen Verzerrungen in diesem Sektor.' WHERE entry=2000005259;
UPDATE dbscript_string SET content_loc3='Hmm... die Werte der gravimetrischen Verzerrung haben sich stabilisiert. Wir haben es geschafft!' WHERE entry=2000005260;
UPDATE dbscript_string SET content_loc3='Ich werde eine Analyse vornehmen.' WHERE entry=2000005261;
UPDATE dbscript_string SET content_loc3='Laut diesen Messungen haben wir nichts zu befürchten. Zumindestens nicht in naher Zukunft. Wir werden mehr Daten brauchen, um sicher zu sein.' WHERE entry=2000005262;
UPDATE dbscript_string SET content_loc3='Sind viele von ihnen dort oben?' WHERE entry=2000005263;
UPDATE dbscript_string SET content_loc3='Was seht ihr? Etwas Interessantes?' WHERE entry=2000005264;
UPDATE dbscript_string SET content_loc3='Sind Wyrmkultisten voraus?' WHERE entry=2000005265;
UPDATE dbscript_string SET content_loc3='%s schaut zurück und gibt ein tiefes, leises Knurren von sich.' WHERE entry=2000005266;
UPDATE dbscript_string SET content_loc3='Wer würde denn jemals diesen Unholden des Wyrmkults dienen?' WHERE entry=2000005267;
UPDATE dbscript_string SET content_loc3='Wie hat der Wyrmkult so viele Anhänger bekommen? Wo kommen sie her?' WHERE entry=2000005268;
UPDATE dbscript_string SET content_loc3='Der Wyrmkult muss einen anderen Pfad benutzen. Wir finden sie immer noch im Ruuanwald.' WHERE entry=2000005269;
UPDATE dbscript_string SET content_loc3='Wir sollen hier warten. Sie haben andere voraus geschickt, um das Tunnelinnere zu überprüfen.' WHERE entry=2000005270;
UPDATE dbscript_string SET content_loc3='Bei allem Respekt, A\'dal hat uns hierher geschickt, um Kaels Pläne herauszufinden, nicht um einen ausgewachsenen Angriff zu starten.' WHERE entry=2000005271;
UPDATE dbscript_string SET content_loc3='Schau, Herzchen. Ich sage ja nicht, dass wir die Festung der Stürme selbst stürmen sollen. Aber wenn man eine Chance sieht, sollte man sie nutzen.' WHERE entry=2000005272;
UPDATE dbscript_string SET content_loc3='Hütet Eure Zunge, Knabe! Wagt es nicht, in diesem Ton mit einer Priesterin des Lichts zu sprechen!' WHERE entry=2000005273;
UPDATE dbscript_string SET content_loc3='Ich bin von Idioten umgeben!' WHERE entry=2000005274;
UPDATE dbscript_string SET content_loc3='Die Magistrix wird erfreut sein, zu hören, dass ich fließend Thalassisch spreche. Eure Zuvorkommenheit ist mir nicht entgangen.' WHERE entry=2000005275;
UPDATE dbscript_string SET content_loc3='Zurück zum Thema... wir sollten unsere Aufmerksamkeit auf die Manaschmieden konzentrieren. Wir müssen in Erfahrung bringen, was der Feind dort tut, und warum.' WHERE entry=2000005276;
UPDATE dbscript_string SET content_loc3='Wir haben Euch alles gesagt, was es zu wissen gilt! Habt Ihr vergessen, dass die meisten von uns treue Anhänger Kael\'thas\' waren?' WHERE entry=2000005277;
UPDATE dbscript_string SET content_loc3='...und schaut, wohin es uns gebracht hat...' WHERE entry=2000005278;
UPDATE dbscript_string SET content_loc3='Sogar Ihr müsst zugeben, dass Eure Informationen von den Abtrünnigen veraltet und unzuverlässig sind. Lasst uns diese Manaschmieden gezielt angreifen und alle Informationen sammeln, die wir bekommen können.' WHERE entry=2000005279;
UPDATE dbscript_string SET content_loc3='Endlich redet der Große mal von Taten!' WHERE entry=2000005280;
UPDATE dbscript_string SET content_loc3='Ich kann nicht glauben, dass ihr Pfuscher für so etwas extra die Schule besucht habt! Weiß eigentlich überhaupt irgendwer was er hier tut?' WHERE entry=2000005281;
UPDATE dbscript_string SET content_loc3='Seid ihr fertig? Was dauert da so verdammt lange?' WHERE entry=2000005282;
UPDATE dbscript_string SET content_loc3='Ist es jetzt sicher, dieses verdammte Ding zu betreten?' WHERE entry=2000005283;
UPDATE dbscript_string SET content_loc3='Nicht wirklich.' WHERE entry=2000005284;
UPDATE dbscript_string SET content_loc3='Hört Ihr bald auf, mich das zu fragen?' WHERE entry=2000005285;
UPDATE dbscript_string SET content_loc3='Ok, sieht sicher aus. Rein da, Ingenieur!' WHERE entry=2000005286;
UPDATE dbscript_string SET content_loc3='Hier rührt sich gar nichts!' WHERE entry=2000005287;
UPDATE dbscript_string SET content_loc3='Das wird eine Schramme geben...' WHERE entry=2000005288;
UPDATE dbscript_string SET content_loc3='%s tastet sich ab, um sicherzugehen, dass alles heil ist.' WHERE entry=2000005289;
UPDATE dbscript_string SET content_loc3='Ich habe es geschafft, ich habe es geschafft!' WHERE entry=2000005290;
UPDATE dbscript_string SET content_loc3='%s schüttelt ablehnend den Kopf.' WHERE entry=2000005291;
UPDATE dbscript_string SET content_loc3='Ich konnte ihn eh nie richtig leiden. Hat zuviel geredet und zu wenig gehandelt.' WHERE entry=2000005292;
UPDATE dbscript_string SET content_loc3='Fantastisch, ihr nutzlosen Taugenichtse! Wieder ein Ingenieur zum Teufel!' WHERE entry=2000005293;
UPDATE dbscript_string SET content_loc3='Vielen Dank, dass Ihr alle heute Abend gekommen seid. Und nun klatscht in die Hände für Shattraths Liebling, Hel\'gesh Neider!' WHERE entry=2000005300;
UPDATE dbscript_string SET content_loc3='Hel\'gesh Neider! Was ein Künstler! Das Wirtshaus ist die ganze Nacht offen, also bleibt und macht es Euch gemütlich!' WHERE entry=2000005301;
UPDATE dbscript_string SET content_loc3='Genießen sie die komödiantischen Einlagen von Hel\'gesh Neider in ein paar Minuten!' WHERE entry=2000005302;
UPDATE dbscript_string SET content_loc3='Wenn Ihr bereit für einen Lacher seid, geht in die Taverne Weltenend!' WHERE entry=2000005303;
UPDATE dbscript_string SET content_loc3='Jetzt in Shattrath: die Tauren Chieftains! Sie spielen zur vollen Stunde in der Taverne Weltenend.' WHERE entry=2000005304;
UPDATE dbscript_string SET content_loc3='Wer? Die Tauren Chieftains! Was? Ein Livekonzert! Wo? In der Taverne Weltenend! Wann? Zur vollen Stunde! LASST EUCH DAS NICHT ENTGEHEN!' WHERE entry=2000005305;
UPDATE dbscript_string SET content_loc3='Ihr habt gesagt, dass Brogg hier bleiben kann! Ihr habt versprochen, dass Ihr Brogg bei der Rückeroberung des Horts der Steinbrecher helft!' WHERE entry=2000005306;
UPDATE dbscript_string SET content_loc3='Hmmm... wo ist mein Angelhaken? Oh, da ist er ja.' WHERE entry=2000005307;
UPDATE dbscript_string SET content_loc3='Mal sehen, ob die Fische anbeißen.' WHERE entry=2000005308;
UPDATE dbscript_string SET content_loc3='Hmmm... ich glaube ich brauche einen besseren Köder. Ich werde mal im Wagen meines Vaters nachsehen.' WHERE entry=2000005309;
UPDATE dbscript_string SET content_loc3='Stacheleberfetzen! Die dürften den Zweck erfüllen. Fische lieben Stacheleber.' WHERE entry=2000005310;
UPDATE dbscript_string SET content_loc3='Hab\' einen gefangen! Werde mal sehen, ob eine der Wachen hungrig ist. Komm\' mit Reißzahn!' WHERE entry=2000005311;
UPDATE dbscript_string SET content_loc3='Falls Ihr hungrig sein solltet, ich habe einen Fisch gefangen...' WHERE entry=2000005312;
UPDATE dbscript_string SET content_loc3='Das nenne ich einen guten Fang! Ihr ehrt die Horde, Junge, wir danken dir!' WHERE entry=2000005313;
UPDATE dbscript_string SET content_loc3='Darf es noch etwas mehr zu Essen sein?' WHERE entry=2000005314;
UPDATE dbscript_string SET content_loc3='Mehr Brot, Junge!' WHERE entry=2000005315;
UPDATE dbscript_string SET content_loc3='Sofort, Sire.' WHERE entry=2000005316;
UPDATE dbscript_string SET content_loc3='Hier ist Euer Brot, mein Herr.' WHERE entry=2000005317;
UPDATE dbscript_string SET content_loc3='Aaahh... sehr gut. Jetzt aber los!' WHERE entry=2000005318;
UPDATE dbscript_string SET content_loc3='Wie Ihr wünscht, Sire.' WHERE entry=2000005319;
UPDATE dbscript_string SET content_loc3='Kann ich noch mehr Steine für meine Halskette sammeln gehen, Papa?' WHERE entry=2000005320;
UPDATE dbscript_string SET content_loc3='Ja, du kannst, Okla. Treib dich aber nicht zu weit von zu Hause umher.' WHERE entry=2000005321;
UPDATE dbscript_string SET content_loc3='Dabu! Ich werde vorsichtig sein, Papa!' WHERE entry=2000005322;
UPDATE dbscript_string SET content_loc3='Ooohh... dieser Stein ist so schön und bunt. Vielleicht kann ich ja noch mehr davon finden...' WHERE entry=2000005323;
UPDATE dbscript_string SET content_loc3='Hmm... dieser Stein ist schön, aber zu klein.' WHERE entry=2000005324;
UPDATE dbscript_string SET content_loc3='Das hier ist ein glänzender Stein, ich werde ihn in meinen Eimer legen. Ok, ich brauche nur noch ein paar mehr...' WHERE entry=2000005325;
UPDATE dbscript_string SET content_loc3='Ich bin mir sicher, dass mein Freund Gruk diesen Stein gerne für den Speer hätte, an dem er gerade bastelt! Er ist sehr scharfkantig... ich hoffe er gefällt ihm.' WHERE entry=2000005326;
UPDATE dbscript_string SET content_loc3='Na also... diese Steine dürften für eine Halskette genügen. Ich sollte jetzt wohl sowieso nach Hause gehen.' WHERE entry=2000005327;
UPDATE dbscript_string SET content_loc3='Ich habe dir ein Einweihungsgeschenk mitgebracht, Jarl.' WHERE entry=2000005328;
UPDATE dbscript_string SET content_loc3='Na los, pack\'s aus. Ich glaube, es wird dir gefallen.' WHERE entry=2000005329;
UPDATE dbscript_string SET content_loc3='Jarl?' WHERE entry=2000005330;
UPDATE dbscript_string SET content_loc3='Wenn ich es nicht essen kann, mag ich es nicht.' WHERE entry=2000005331;
UPDATE dbscript_string SET content_loc3='Jarl, seit ich dich das letzte Mal gesehen habe, bist du wie verwandelt. Was ist los?' WHERE entry=2000005332;
UPDATE dbscript_string SET content_loc3='Nichts. Hilfst du mir, ein wenig Froschschenkeleintopf sämig zu rühren?' WHERE entry=2000005333;
UPDATE dbscript_string SET content_loc3='Ich bin hierhergekommen, um dich zu besuchen, Jarl! Und was finde ich? Dich, in einer leeren Hütte mitten im Nirgendwo, wie du über Frösche plapperst? Ich geb\'s auf!' WHERE entry=2000005334;
UPDATE dbscript_string SET content_loc3='Und ich bin fest entschlossen, mehr herauszufinden... Solltet Ihr also meine Hilfe wieder benötigen, dann wisst Ihr ja, wo Ihr mich finden könnt.' WHERE entry=2000005335;
UPDATE dbscript_string SET content_loc3='Anaya...? Kann ich meinen Augen trauen? Bist du es wirklich?' WHERE entry=2000005336;
UPDATE dbscript_string SET content_loc3='Die Zeit war schrecklich zu dir, und ich, Geliebter, bin überzeugt, das wir irgendwann endlich wieder vereint sein werden.' WHERE entry=2000005337;
UPDATE dbscript_string SET content_loc3='Das Schicksal kann so grausam sein, das uns nur dieser Moment gestattet ist, nach Tausenden Jahren Trennung...' WHERE entry=2000005338;
UPDATE dbscript_string SET content_loc3='Hasst du mich, Liebes? Das ich gezwungen war deine lebende Form zu zerstören, auf das dein Geist frei sei von der unglücklichen Gefangenschaft.' WHERE entry=2000005339;
UPDATE dbscript_string SET content_loc3='Belaste nicht weiter dein Herz damit, Geliebter. Du hast mich aus der Gefangenschaft befreit und deshalb liebe ich dich mehr denn je.' WHERE entry=2000005340;
UPDATE dbscript_string SET content_loc3='Traurig, meine Zeit hier ist um... Die Bande die mich an diese Welt bindet wird schwächer, sie zieht mich davon...' WHERE entry=2000005341;
UPDATE dbscript_string SET content_loc3='Nein! Anaya... Anaya! Verlass mich nicht! Bitte...' WHERE entry=2000005342;
UPDATE dbscript_string SET content_loc3='Lebe wohl, Cerellean, bis zu dem Tage an dem wir wieder zusammen sein werden...' WHERE entry=2000005343;
UPDATE dbscript_string SET content_loc3='Anayas sanfte Stimme gleitet zurück in den Nebel. \"Wisse, das ich dich für immer lieben werde...\"' WHERE entry=2000005344;
UPDATE dbscript_string SET content_loc3='Wie, Liebes? Wie soll ich die Kraft finden, mich den Zeiten dieser Welt zu stellen, ohne dich an meiner Seite...' WHERE entry=2000005345;
UPDATE dbscript_string SET content_loc3='Danke, $N. Viel Glück.' WHERE entry=2000005346;
UPDATE dbscript_string SET content_loc3='Die Klippenquellfälle liegt entlang der Berge im Osten und Norden.' WHERE entry=2000005347;
UPDATE dbscript_string SET content_loc3='Was soll das bedeuten, Mor\'ghor?' WHERE entry=2000005348;
UPDATE dbscript_string SET content_loc3='SCHWEIGT!' WHERE entry=2000005349;
UPDATE dbscript_string SET content_loc3='Stammelnder Idiot. Unfassbar inkompetenter Tölpel...' WHERE entry=2000005350;
UPDATE dbscript_string SET content_loc3='DIES ist Euer Held?' WHERE entry=2000005351;
UPDATE dbscript_string SET content_loc3='Ihr wurdet getäuscht, Schwachkopf.' WHERE entry=2000005352;
UPDATE dbscript_string SET content_loc3='Diese... gesamte... Operation WURDE KOMPROMITTIERT!' WHERE entry=2000005353;
UPDATE dbscript_string SET content_loc3='Ich erwarte, dieses Insekt innerhalb einer Stunde in Einzelteilen in meinem Gewölbe zu sehen. Versagt, und Euer Schicksal wird viel schlimmer als der Tod sein.' WHERE entry=2000005354;
UPDATE dbscript_string SET content_loc3='Ihr werdet dem $gJungen:Mädchen; keinen Schaden zufügen, Mor\'ghor! Schnell, $N, klettert auf meinen Rücken.' WHERE entry=2000005355;
UPDATE dbscript_string SET content_loc3='%s beginnt, den Stein von Relu über den beiden Fossilien zu benutzen.' WHERE entry=2000005356;
UPDATE dbscript_string SET content_loc3='Wer hat Aman heraufbeschworen?' WHERE entry=2000005357;
UPDATE dbscript_string SET content_loc3='Ah, Ihr beschäftigt Euch mit den Relikten der Vergangenheit, wie ich sehe.' WHERE entry=2000005358;
UPDATE dbscript_string SET content_loc3='Seid gewarnt, auch Eure Schöpfer sind fehlbar.' WHERE entry=2000005359;
UPDATE dbscript_string SET content_loc3='Grabt Ihr zu tief in Eurer Vergangenheit, könnte das Eurer Zukunft ein abruptes Ende bringen.' WHERE entry=2000005360;
UPDATE dbscript_string SET content_loc3='Aman löst sich vor Euren Augen auf.' WHERE entry=2000005361;
UPDATE dbscript_string SET content_loc3='Gruß, Gezhe! Siebzehn neue Bestellungen aus dem Hara\'samidsektor. Der Markt für schweres Veldarit ist alles andere als zusammengebrochen, wie Ihr es vorausgesagt habt.' WHERE entry=2000005362;
UPDATE dbscript_string SET content_loc3='Heraaziexporte steigen, Herr. Wir haben schon die zuständigen Steuerbeamten bestochen. Die Schmuggelware wird in ein paar Tagen eintreffen.' WHERE entry=2000005363;
UPDATE dbscript_string SET content_loc3='Das überrascht mich nicht. Kommt das nächste Mal mit echten Nachrichten, hört Ihr?' WHERE entry=2000005364;
UPDATE dbscript_string SET content_loc3='Wie ich erwartet habe. Ihr könnt gehen.' WHERE entry=2000005365;
UPDATE dbscript_string SET content_loc3='Wenn Ihr gestattet.' WHERE entry=2000005366;
UPDATE dbscript_string SET content_loc3='Eure Anwesenheit hier bedeutet wohl, dass Ihr bereit seid, das Studium des Beschwörens von Dämonenkohorten, die Euch gehorchen, zu beginnen. Wir werden mit der niedersten Kreatur beginnen, die ihr rufen könnt, und uns dann weiter vorarbeiten. Lasst uns beginnen.' WHERE entry=2000005367;
UPDATE dbscript_string SET content_loc3='Die einfachste Kreatur zum Beschwören ist der Wichtel. Ihr solltet bereits in der Lage sein, diesen Diener hervorzubringen, aber der Vollständigkeit halber möchte ich mit ihm beginnen.' WHERE entry=2000005368;
UPDATE dbscript_string SET content_loc3='Diese üble kleine Kreatur ist ein Wichtel. Er ist klein und schwach, was ihn als lebendigen Schild beinahe unbrauchbar macht, und sein Schadenspotenzial ist bestenfalls mittelmäßig. Diese Kreatur wird am besten als Unterstützung für eine größere Gruppe eingesetzt.' WHERE entry=2000005369;
UPDATE dbscript_string SET content_loc3='Da Ihr jetzt Gelegenheit hattet, den Wichtel zu studieren, wenden wir uns jetzt dem nächsten Diener zu, den Ihr beschwören könnt, dem Leerwandler.' WHERE entry=2000005370;
UPDATE dbscript_string SET content_loc3='Dieses dämonische Wesen wird Leerwandler genannt. Seine Kraft und Belastbarkeit sind beeindruckend, was ihn ideal für Verteidigungszwecke macht. Schickt ihn aus, um Feinde anzugreifen, und benutzt ihn dann als Schild, während Ihr Eure Zauber und Fähigkeiten einsetzt, um Eurem Gegner Leben zu entziehen.' WHERE entry=2000005371;
UPDATE dbscript_string SET content_loc3='Wenn Ihr noch nie einen gesehen habt, ist es ein beeindruckender Anblick. Eine sehr eindrucksvolle Kreatur, sowohl auf dem Schlachtfeld als auch außerhalb davon. Als Nächstes werfen wir einen Blick auf das, worauf alle männlichen Studenten wohl schon lange gewartet haben. Den Sukkubus.' WHERE entry=2000005372;
UPDATE dbscript_string SET content_loc3='Nun gut. Neben der offensichtlichen Ablenkung Eurer männlichen Feinde, die ein solcher Diener mit sich bringt, ist sie auch in der Lage, erheblichen Schaden zuzufügen. Ihre schwache Belastbarkeit macht sie allerdings als Schild fast nutzlos.' WHERE entry=2000005373;
UPDATE dbscript_string SET content_loc3='Studiert eifrig und Ihr werdet vielleicht eines Tages selbst in der Lage sein, einen zu beschwören, aber jetzt ist es an der Zeit, mit dem Teufelsjäger weiterzumachen.' WHERE entry=2000005374;
UPDATE dbscript_string SET content_loc3='Was Ihr vor Euch seht, ist ein Teufelsjäger. Die natürlichen Talente dieser Kreatur umfassen \'Zaubersperre\' und andere Fähigkeiten, die ihn angesichts eines magisch begabten Gegners unübertreffbar machen.' WHERE entry=2000005375;
UPDATE dbscript_string SET content_loc3='Wenn Ihr Euch einem Zauberer gegenüberseht, ist diese wilde Bestie Euer bester Freund. Doch schauen wir uns jetzt etwas ganz anderes an. Die nächste Kreatur hilft Euch, wenn Ihr unterwegs seid, und wird Eure zukünftigen Reisen um einiges einfacher machen. Werfen wir mal einen Blick auf ein Teufelsross.' WHERE entry=2000005376;
UPDATE dbscript_string SET content_loc3='Ich bezweifle, dass Ihr schon oft Gelegenheit hattet, solch eine Kreatur zu sehen. Diese dämonischen Pferdewesen beschleunigen Eure Reisen, indem sie Euch als Reittier dienen, solange Ihr die Kontrolle über sie habt. Jedoch sind sie schwierig zu kontrollieren, also vergewissert Euch vorher, dass Ihr bereit seid, bevor Ihr es versucht.' WHERE entry=2000005377;
UPDATE dbscript_string SET content_loc3='Das war\'s. Unsere Lektion im Beschwören ist zu Ende. Eine neue Klasse wird in Kürze beginnen. Wenn Ihr das Gelernte auffrischen möchtet, könnt Ihr in der Nähe bleiben.' WHERE entry=2000005378;
UPDATE dbscript_string SET content_loc3='Puh! Da ist ja meine Glückskappe. Ich habe schon überall danach gesucht.' WHERE entry=2000005379;
UPDATE dbscript_string SET content_loc3='Was habe ich mir nur dabei gedacht, ohne mein Schwert aus dem Haus zu gehen. Ich hätte es mitgenommen, wenn ich es hier gesehen hätte...' WHERE entry=2000005380;
UPDATE dbscript_string SET content_loc3='Ich kann ohne meinen Mantel einfach nicht aus dem Haus gehen. Ich hoffe, er ist hier drin...' WHERE entry=2000005381;
UPDATE dbscript_string SET content_loc3='Da ist er ja! Ich hätte schwören können, dass er das letzte Mal, als ich nachgesehen habe, nicht da war.' WHERE entry=2000005382;
UPDATE dbscript_string SET content_loc3='Ich kann nicht ohne Gutenachtgeschichte einschlafen. Jetzt kann ich sicher gut ruhen.' WHERE entry=2000005383;
UPDATE dbscript_string SET content_loc3='Was war das für ein Geräusch? Hörte sich wie ein brüllen an.' WHERE entry=2000005384;
UPDATE dbscript_string SET content_loc3='Grimmklaue brüllt Terenthis um Aufmerksamkeit an.' WHERE entry=2000005385;
UPDATE dbscript_string SET content_loc3='Grimmklaue?! Ganz ruhig, mein Freund... wo ist Volcor,dein Meister?' WHERE entry=2000005386;
UPDATE dbscript_string SET content_loc3='Grimmklaue ächzt und brüllt Terenthis an, während er mit seinen Tatzen auf den Boden stampft.' WHERE entry=2000005387;
UPDATE dbscript_string SET content_loc3='Wow, wow, mein Freund. Einen Moment...' WHERE entry=2000005388;
UPDATE dbscript_string SET content_loc3='Terenthis beginnt einen Zauber auf Grimmklaue zu sprechen.' WHERE entry=2000005389;
UPDATE dbscript_string SET content_loc3='So... das sollte helfen. Jetzt erzähl mir was passiert ist, Grimmklaue.' WHERE entry=2000005390;
UPDATE dbscript_string SET content_loc3='Grimmklaue brüllt Terenthis weiter an, aber diesmal scheint der Druide den Bär zu verstehen.' WHERE entry=2000005391;
UPDATE dbscript_string SET content_loc3='Ich verstehe, mein Freund. Ich werde jemanden finden der deinem Meister helfen kann.' WHERE entry=2000005392;
UPDATE dbscript_string SET content_loc3='Grimmklaue brüllt zustimmend Terenthis an.' WHERE entry=2000005393;
UPDATE dbscript_string SET content_loc3='Wenn Ihr Zeit habt, Grimmklaue und sein Meister Volcor könnten Eure Hilfe benötigen. Wenn Ihr interessiert seid, dann sprecht mit mir...' WHERE entry=2000005394;
UPDATE dbscript_string SET content_loc3='Ich habe genug von diesen andauernden rumgejammere, Kapitän. Von was seid ihr nochmal der Kapitän? Ich denke mal, Der Nervosität!' WHERE entry=2000005395;
UPDATE dbscript_string SET content_loc3='Warum nehmt ihr euch nicht einen Hammer und haut auf diese Strukturen ein, anstatt uns aller 5 Minuten zu fragen ob wir fertig sind?' WHERE entry=2000005396;
UPDATE dbscript_string SET content_loc3='%s zuckt mit den Schultern.' WHERE entry=2000005397;
UPDATE dbscript_string SET content_loc3='Na toll, zurück zum Zeichenbrett.' WHERE entry=2000005398;
UPDATE dbscript_string SET content_loc3='Verdammt. Wie sollen wir nur ihre Verteidigungsmaßnahmen überwinden?' WHERE entry=2000005399;
UPDATE dbscript_string SET content_loc3='Bitte entschuldigt mich, während ich meine Tests beginne...' WHERE entry=2000005400;
UPDATE dbscript_string SET content_loc3='Interessant...' WHERE entry=2000005401;
UPDATE dbscript_string SET content_loc3='Aber was ist das?' WHERE entry=2000005402;
UPDATE dbscript_string SET content_loc3='Oh je!' WHERE entry=2000005403;
UPDATE dbscript_string SET content_loc3='Wenn mein Blut nicht schon vor langer Zeit geronnen wäre, würde es jetzt vor Zorn kochen...' WHERE entry=2000005404;
UPDATE dbscript_string SET content_loc3='Ihr wagt es, mir mein Auge zu nehmen? Ich werde Eure Seele zermalmen und an meine blinden, jammernden Wichtel verfüttern!' WHERE entry=2000005405;
UPDATE dbscript_string SET content_loc3='Kommt in mein Reich, Wurm! Kommt und bezahlt für Eure Anmaßung!' WHERE entry=2000005406;
UPDATE dbscript_string SET content_loc3='Steinmetze. . .ähm. . Defias seid gewarnt: Der rostige Anker sinkt heute Abend.' WHERE entry=2000005407;
UPDATE dbscript_string SET content_loc3='Der Boss will, das alle in höchster Alarmbereitschaft sind. Der rostige Anker sinkt heute Abend.' WHERE entry=2000005408;
UPDATE dbscript_string SET content_loc3='Der Priester verharrt im stillen Gebet.' WHERE entry=2000005409;
UPDATE dbscript_string SET content_loc3='Licht, gib mir Stärke.' WHERE entry=2000005410;
UPDATE dbscript_string SET content_loc3='Heiliges Licht, führe die Gefallen aus der Dunkelheit in das Licht und vom Tode in die Unsterblichkeit!' WHERE entry=2000005411;
UPDATE dbscript_string SET content_loc3='Was glaubt ihr, wohin ihr geht, Soldat? Ihr seid schwer verletzt. Seht zu, dass ihr zurück ins Zelt kommt und euch von der Schwester behandeln lasst!' WHERE entry=2000005412;
UPDATE dbscript_string SET content_loc3='Nun geht mir aus dem Weg! Es ist an der Zeit, die Brennende Legion wieder in den Nether zurückzuwerfen!!' WHERE entry=2000005413;
UPDATE dbscript_string SET content_loc3='Das ist die richtige Einstellung, Soldat! Ich wünschte nur, wir hätten mehr Soldaten wie Euch in dieser Armee. Nun geht schon und gebt ihnen Zunder!' WHERE entry=2000005414;
UPDATE dbscript_string SET content_loc3='Neeeeeeeeeeein!' WHERE entry=2000005415;
UPDATE dbscript_string SET content_loc3='Meine Brüder... ich habe versagt...' WHERE entry=2000005416;
UPDATE dbscript_string SET content_loc3='Oh nein, mein Freund... wir haben dich enttäuscht... wir haben dich enttäuscht.' WHERE entry=2000005417;
UPDATE dbscript_string SET content_loc3='Alles ist bereit, Kriegsrufer.' WHERE entry=2000005418;
UPDATE dbscript_string SET content_loc3='Verdammnislord Kazzak wird zufrieden sein. Ihr müsst die Häufigkeit Eurer Angriffe erhöhen. Zerstört die orcischen und zwergischen Festungen so schnell wie möglich!' WHERE entry=2000005419;
UPDATE dbscript_string SET content_loc3='Kriegshetzer, dafür werden wir all unsere Höllenbestien brauchen. Dann wären wir auf einen Gegenangriff nicht mehr gerüstet.' WHERE entry=2000005420;
UPDATE dbscript_string SET content_loc3='Keine Sorge. Ich habe die Produktion in der Todesschmiede steigern lassen. Ihr werdet alle Höllenbestien zu Verfügung haben, die Ihr für Eure Befehle braucht. Versagt nicht, Jovaan.' WHERE entry=2000005421;
UPDATE dbscript_string SET content_loc3='Es geschehe, wie Ihr es wünscht, Kriegsrufer. Eine letzte Frage noch, wenn Ihr erlaubt...' WHERE entry=2000005422;
UPDATE dbscript_string SET content_loc3='Ja?' WHERE entry=2000005423;
UPDATE dbscript_string SET content_loc3='Was ist in der Kiste?' WHERE entry=2000005424;
UPDATE dbscript_string SET content_loc3='Kiste? Ich habe Euch keine Kiste geschickt, Jovaan. Habt Ihr nicht Wichtigeres, worum Ihr Euch kümmern müsst? Dann kümmert Euch drum!' WHERE entry=2000005425;
UPDATE dbscript_string SET content_loc3='Ich habe keine Lust mehr, Euch nett zu fragen, Fischmann!' WHERE entry=2000005426;
UPDATE dbscript_string SET content_loc3='Wo ist die Litanei der Verdammnis? Ich weiß, dass sich ein Drittel davon in diesem Dorf befindet.' WHERE entry=2000005427;
UPDATE dbscript_string SET content_loc3='Wasss... wovon ssssprecht Ihr, Weichhaut?' WHERE entry=2000005428;
UPDATE dbscript_string SET content_loc3='Die Naga haben... diessse Litanei... nicht...' WHERE entry=2000005429;
UPDATE dbscript_string SET content_loc3='Nein... Natürlich nicht. Wie komme ich nur darauf. Warum solltet Ihr wohl ein Drittel des Worts der Verdammnis besitzen?' WHERE entry=2000005430;
UPDATE dbscript_string SET content_loc3='Ich bin ja so dumm. Vielleicht sollte ich Euch losketten und freilassen?' WHERE entry=2000005431;
UPDATE dbscript_string SET content_loc3='Ja... Ja... lasssst mich frei...' WHERE entry=2000005432;
UPDATE dbscript_string SET content_loc3='HALTET IHR MICH WIRKLICH FÜR SO DUMM, NAGA? JETZT FÜHLT DEN SCHMERZ!' WHERE entry=2000005433;
UPDATE dbscript_string SET content_loc3='%s spuckt verächtlich auf den Naga der Echsennarbe.' WHERE entry=2000005434;
UPDATE dbscript_string SET content_loc3='STEH AUF DU STINKENDES STÜCK DRECK! STEH AUF, BEVOR ICH DICH VERNICHTE!' WHERE entry=2000005435;
UPDATE dbscript_string SET content_loc3='%s kommt wieder zu sich.' WHERE entry=2000005436;
UPDATE dbscript_string SET content_loc3='Ich höre...' WHERE entry=2000005437;
UPDATE dbscript_string SET content_loc3='Schlagt den Kommandanten der Echsssennarbe nicht länger, Orc! Ich werde reden! Die Litanei... ist in einer der Truhen in der Echsssennarbe versssteckt... Die Naga dort haben die Schlüssssel...' WHERE entry=2000005438;
UPDATE dbscript_string SET content_loc3='ECHSSSENNARBE!' WHERE entry=2000005439;
UPDATE dbscript_string SET content_loc3='Eine tiefe, schauderhafte Stimme ertönt vom %s...' WHERE entry=2000005440;
UPDATE dbscript_string SET content_loc3='Wie könnt Ihr es wagen, mit meinen Puppen zu spielen, $N!' WHERE entry=2000005441;
UPDATE dbscript_string SET content_loc3='Die Orcs in meiner Zitadelle werden Euer Blut trinken und Euch verschlingen!' WHERE entry=2000005442;
UPDATE dbscript_string SET content_loc3='Was willst du, Schamane?' WHERE entry=2000005443;
UPDATE dbscript_string SET content_loc3='Hast du keine Augen? Hast du nicht gesehen das die Dämonen direkt vor unserer Haustür sind? Ich kann keine Soldaten entbehren!' WHERE entry=2000005444;
UPDATE dbscript_string SET content_loc3='Bete zu deinen Göttern, Schamane. Vielleicht werden sie dir helfen. Die Horde jedoch kann nicht...' WHERE entry=2000005445;
UPDATE dbscript_string SET content_loc3='Seid still! Die Zersplitterung wird bald beginnen!' WHERE entry=2000005446;
UPDATE dbscript_string SET content_loc3='Werdet Zeugen der unbestreitbaren Macht unseres dunklen Meisters!' WHERE entry=2000005447;
UPDATE dbscript_string SET content_loc3='Mit seinem Geschenk werde ich dieses Land auslöschen und neu formen!' WHERE entry=2000005448;
UPDATE dbscript_string SET content_loc3='Seht! Seht, wie der Boden vor uns zerbricht! Seht, wie die Energie strömt! Sie wird unsere Armeen stärken...' WHERE entry=2000005449;
UPDATE dbscript_string SET content_loc3='Wir werden niemals wieder ohne Macht sein! Ich habe unsere Zukunft gesichert! Nehmt teil an meinem Ruhm!' WHERE entry=2000005450;
UPDATE dbscript_string SET content_loc3='Es... ist... vollbracht...' WHERE entry=2000005451;
UPDATE dbscript_string SET content_loc3='Zerreißt das Land in Stücke, Gul\'dan! Durchtrennt die Verbindung!' WHERE entry=2000005452;
UPDATE dbscript_string SET content_loc3='Tut es nicht, Gul\'dan! Die Elemente werden uns niemals vergeben!' WHERE entry=2000005453;
UPDATE dbscript_string SET content_loc3='Denkt an unsere Kinder, Gul\'dan! Ihr werdet uns alle zerstören!' WHERE entry=2000005454;
UPDATE dbscript_string SET content_loc3='Sie haben uns im Stich gelassen! Vergesst das nicht!' WHERE entry=2000005455;
UPDATE dbscript_string SET content_loc3='Wir werden uns vom Land nehmen, was es sich weigert, uns zu geben!' WHERE entry=2000005456;
UPDATE dbscript_string SET content_loc3='Aufhören! Ihr sollt aufhören!' WHERE entry=2000005457;
UPDATE dbscript_string SET content_loc3='Ihr habt uns alle verdammt... Wir sind verloren.' WHERE entry=2000005458;
UPDATE dbscript_string SET content_loc3='Ich habe ein paar Steuerungskonsolen den Hang hinunter manipuliert. Begebt Euch schnell zu einer und übernehmt die Kontrolle über einen Teufelshäscher! Ihr habt nur eine begrenzte Zeit zur Verfügung.' WHERE entry=2000005459;
UPDATE dbscript_string SET content_loc3='Wenn Ihr den Schlüssel haben wollt, müsst Ihr mich schon fangen!' WHERE entry=2000005460;
UPDATE dbscript_string SET content_loc3='Verfolgt mich wenn Ihr wollt! Rennt wenn Ihr Euch schert.' WHERE entry=2000005461;
UPDATE dbscript_string SET content_loc3='Warum würdet Ihr mir je schaden wollen? Komm! Freunde könnten wir sein!' WHERE entry=2000005462;
UPDATE dbscript_string SET content_loc3='STIRB! Du bringst Pusillin zum weinen!' WHERE entry=2000005463;
UPDATE dbscript_string SET content_loc3='Sagt \'Hallo!\' zu meinen kleinen Freunden!' WHERE entry=2000005464;
UPDATE dbscript_string SET content_loc3='Wie Ihr befehlt, Herr!' WHERE entry=2000005465;
UPDATE dbscript_string SET content_loc3='Gewährt uns die Stärke der Riesen von Schattenmond, Corok. Der Meister ist ein gerechter Herrscher. Er wird sicher für diejenigen sorgen, die ihm geholfen haben, die Aufrührer dieser Welt zu unterwerfen.' WHERE entry=2000005466;
UPDATE dbscript_string SET content_loc3='Und... wenn nicht?' WHERE entry=2000005467;
UPDATE dbscript_string SET content_loc3='Dann werdet Ihr zu Feinden meines Fürsten, Illidan. Feinde von Illidan werden vernichtet. Sind Eure Riesen in der Lage, einem Angriff aus der Stätte der Mondfinsternis standzuhalten?' WHERE entry=2000005468;
UPDATE dbscript_string SET content_loc3='Nein... ich glaube nicht...' WHERE entry=2000005469;
UPDATE dbscript_string SET content_loc3='Macht weiter so. Arbeitet gut, dann wird der Meister euch belohnen.' WHERE entry=2000005470;
UPDATE dbscript_string SET content_loc3='Wir werden uns eines Tages wiedersehen.' WHERE entry=2000005471;
UPDATE dbscript_string SET content_loc3='Die Verkörperung des Todes wurde ein weiteres Mal auf die Welt losgelassen! Teron Blutschatten ist zurückgekehrt...' WHERE entry=2000005472;
UPDATE dbscript_string SET content_loc3='Verschwendet keine Zeit, Narr! Benutzt unsere Macht, um die zu beseitigen, die uns im Weg stehen! Vernichtet Karsius, bevor Ihr unterworfen werdet.' WHERE entry=2000005473;
UPDATE dbscript_string SET content_loc3='Lasst mich sofort frei, oder Ihr bekommt meinen Zorn zu spüren!' WHERE entry=2000005474;
UPDATE dbscript_string SET content_loc3='Diese Zelle wird mich nicht lange aufhalten. Ich werde mir Illidans Kopf holen, so oder so.' WHERE entry=2000005475;
UPDATE dbscript_string SET content_loc3='Betrachtet das als Aufwärmübung! Die wahre Ausbildung wird noch viel härter!' WHERE entry=2000005476;
UPDATE dbscript_string SET content_loc3='Bei solch einer Belohnung kann doch keiner widerstehen, Mann.' WHERE entry=2000005477;
UPDATE dbscript_string SET content_loc3='Versteckt Euch! Versteckt Euch, bevor sie Euch sehen können! Zurück!' WHERE entry=2000005478;
UPDATE dbscript_string SET content_loc3='Halt!' WHERE entry=2000005479;
UPDATE dbscript_string SET content_loc3='Was ist, mein Herr?' WHERE entry=2000005480;
UPDATE dbscript_string SET content_loc3='Zarath, Ihr müsst sofort zum Schwarzen Tempel zurückkehren! Ich... Ich habe wie es scheint Fürst Illidans Befehle verlegt. Schnell!' WHERE entry=2000005481;
UPDATE dbscript_string SET content_loc3='Mein Herr, Ihr erwartet doch nicht, dass ich Euch unbewacht lasse. Fürst Illidan würde mich köpfen lassen, wenn Euch etwas zustieße.' WHERE entry=2000005482;
UPDATE dbscript_string SET content_loc3='Zarath, ich bin sehr wohl in der Lage, die Straße zur Stätte der Mondfinsternis zu bewältigen. Wenn wir den Brief nicht abliefern, wird Fürst Illidan uns beide töten lassen! Und nun fort mit Euch!' WHERE entry=2000005483;
UPDATE dbscript_string SET content_loc3='Wie Ihr wünscht, mein Herr.' WHERE entry=2000005484;
UPDATE dbscript_string SET content_loc3='Ah, süße, süße Blutdistel... Wahrscheinlich hat sie einer der verdammten Abhängigen von der Stätte der Mondfinsternis verloren.' WHERE entry=2000005485;
UPDATE dbscript_string SET content_loc3='Ihr Verlust ist mein Gewinn...' WHERE entry=2000005486;
UPDATE dbscript_string SET content_loc3='Es ist an der Zeit zu handeln. Vernichtet Icarius.' WHERE entry=2000005487;
UPDATE dbscript_string SET content_loc3='%s verstaut das Blutdistelbündel in seiner Robe.' WHERE entry=2000005488;
UPDATE dbscript_string SET content_loc3='Tretet zurück, $N. Die Geister könnten um sich schlagen.' WHERE entry=2000005489;
UPDATE dbscript_string SET content_loc3='Warum habt Ihr uns gefangen?' WHERE entry=2000005490;
UPDATE dbscript_string SET content_loc3='Was hofft Ihr dadurch zu erreichen?' WHERE entry=2000005491;
UPDATE dbscript_string SET content_loc3='%s verneigt sich.' WHERE entry=2000005492;
UPDATE dbscript_string SET content_loc3='Wir wollen Euch helfen, Geist. Wir suchen Eure Weisheit.' WHERE entry=2000005493;
UPDATE dbscript_string SET content_loc3='Weisheit? Es war Euer Volk, das den unauslöschlichen Zorn von Cyrukh erregte.' WHERE entry=2000005494;
UPDATE dbscript_string SET content_loc3='Warum sollten wir Euch helfen? Die Welt liegt in Trümmern. Zerstört.' WHERE entry=2000005495;
UPDATE dbscript_string SET content_loc3='%s nickt.' WHERE entry=2000005496;
UPDATE dbscript_string SET content_loc3='Ihr habt Recht, Geist. Es war mein Volk, welches Euch dies angetan hat. Erlaubt mir, es wiedergutzumachen.' WHERE entry=2000005497;
UPDATE dbscript_string SET content_loc3='Bitte sagt uns: Wer oder was ist Cyrukh?' WHERE entry=2000005498;
UPDATE dbscript_string SET content_loc3='Narr! Cyrukh der Feuerlord! Als die Verbindung durchtrennt wurde, wachte Cyrukh auf. Es sind die Tränen von Cyrukh, die dieses Land mit geschmolzenem Stein füllen.' WHERE entry=2000005499;
UPDATE dbscript_string SET content_loc3='Der... der Feuerlord? Ein Elementarlord wurde empor gerufen? Natürlich... Gul\'dan, Ihr wart ein verdammter Narr!' WHERE entry=2000005500;
UPDATE dbscript_string SET content_loc3='Er ist verrückt. Die Zersplitterung hat ihn in den Wahnsinn getrieben. Es gibt keine Ruhe für die Elemente.' WHERE entry=2000005501;
UPDATE dbscript_string SET content_loc3='Es muss einen Weg geben.' WHERE entry=2000005502;
UPDATE dbscript_string SET content_loc3='Ja, einen Weg. Zerstört Cyrukh. Zerstört einen Elementarlord.' WHERE entry=2000005503;
UPDATE dbscript_string SET content_loc3='Zweifelt nicht an mir und meinen Kameraden, Geist. Sagt uns, wie wir den Feuerlord beschwören können. Meine Verbündeten werden ihn zerstören.' WHERE entry=2000005504;
UPDATE dbscript_string SET content_loc3='Ihr müsst die Worte sagen...' WHERE entry=2000005505;
UPDATE dbscript_string SET content_loc3='Ja, ja... die Worte. Die Worte der Verdammnis. Die Worte des Dunklen. Eures Meisters.' WHERE entry=2000005506;
UPDATE dbscript_string SET content_loc3='Ja... Gul\'dan...' WHERE entry=2000005507;
UPDATE dbscript_string SET content_loc3='Wir werden diese Worte finden. Wir werden Euren Meister beschwören und ihn zerstören.' WHERE entry=2000005508;
UPDATE dbscript_string SET content_loc3='Gerede... nur Gerede. Lasst uns frei, Sterblicher.' WHERE entry=2000005509;
UPDATE dbscript_string SET content_loc3='Und nun zur Wiederentdeckung der verlorenen Worte!' WHERE entry=2000005510;
UPDATE dbscript_string SET content_loc3='...es soll ein Fond für den Kauf von Haargel eingerichtet werden? Vielen Dank, aber ich mag meine Haare auch so!' WHERE entry=2000005511;
UPDATE dbscript_string SET content_loc3='...vergnügt Euch als Deserteur im Kinderplanschbecken oder bei der Arbeit im Hundesalon? Wie lächerlich!' WHERE entry=2000005512;
UPDATE dbscript_string SET content_loc3='Ich würde mich einer Sache wie dieser niemals anschließen. Behaltet das mal lieber. Es könnte Euch auf dem stillen Örtchen noch von Nutzen sein...' WHERE entry=2000005513;
UPDATE dbscript_string SET content_loc3='...die Deserteure wünschen sich ein Theramore, in dem die Bürger die Freiheit besitzen, jederzeit und überall zu jonglieren? Was ist das für ein Quatsch?' WHERE entry=2000005514;
UPDATE dbscript_string SET content_loc3='Das ist das witzigste was ich seit langem gelesen habe. Ihr versüßt mir den Tag!' WHERE entry=2000005515;
UPDATE dbscript_string SET content_loc3='Das... das ist ein Scherz, nicht wahr?' WHERE entry=2000005516;
UPDATE dbscript_string SET content_loc3='Hey, danke.' WHERE entry=2000005517;
UPDATE dbscript_string SET content_loc3='%s liest den Handzettel.' WHERE entry=2000005518;
UPDATE dbscript_string SET content_loc3='...bekommt Unterwäsche der Deserteure jetzt zum halben Preis? Was ist das für ein Mist?' WHERE entry=2000005519;
UPDATE dbscript_string SET content_loc3='Was für ein Haufen Irrer! Glaubt Ihr denn wirklich an das?' WHERE entry=2000005520;
UPDATE dbscript_string SET content_loc3='Seid bedankt, $R! Euren Dienst an den Mag\'har werden wir nicht vergessen.' WHERE entry=2000005521;
UPDATE dbscript_string SET content_loc3='Vielen Dank, $GHeld:Heldin;.' WHERE entry=2000005522;
UPDATE dbscript_string SET content_loc3='Die Geister mögen über Euch wachen, $R!' WHERE entry=2000005523;
UPDATE dbscript_string SET content_loc3='Tod den Ogern!' WHERE entry=2000005524;
