UPDATE dbscript_string SET content_loc3='Danke, ich hätte den Silberwald niemals verlassen sollen.' WHERE entry=2000005543;
UPDATE dbscript_string SET content_loc3='Da... Dank Euch. Ich war mir sicher, dass ich sterben werde.' WHERE entry=2000005544;
UPDATE dbscript_string SET content_loc3='Ich da... danke Euch. Ich dachte, ich müsste sterben.' WHERE entry=2000005545;
UPDATE dbscript_string SET content_loc3='%s jagt Euch! Lauft!' WHERE entry=2000005546;
UPDATE dbscript_string SET content_loc3='Oh nein! Hier kommen sie!' WHERE entry=2000005547;
UPDATE dbscript_string SET content_loc3='Gut, ich bin so weit!' WHERE entry=2000005548;
UPDATE dbscript_string SET content_loc3='Was? Ihr wagt es, so mit mir zu sprechen?' WHERE entry=2000005549;
UPDATE dbscript_string SET content_loc3='Nach allem, was wir zusammen durchgemacht haben? Tja, ich konnte Euch eh noch nie leiden!' WHERE entry=2000005550;
UPDATE dbscript_string SET content_loc3='Ich hau ab!' WHERE entry=2000005551;
UPDATE dbscript_string SET content_loc3='Wer wagt es, mein Domizil zu betreten! Kommt! Kommt und werdet verzehrt!' WHERE entry=2000005552;
UPDATE dbscript_string SET content_loc3='Gehen wir weiter!' WHERE entry=2000005553;
