UPDATE dbscript_string SET content_loc3='Sachte, sachte, Drachling.' WHERE entry=2000005623;
UPDATE dbscript_string SET content_loc3='Ein wundervolles Exemplar.' WHERE entry=2000005624;
UPDATE dbscript_string SET content_loc3='Schlaf nun, mein Kind...' WHERE entry=2000005625;
UPDATE dbscript_string SET content_loc3='Nein! Bleibt mir vom Leib! Hilfe!!!' WHERE entry=2000005626;
UPDATE dbscript_string SET content_loc3='Moment... habt Ihr das gehört? Irgendetwas nähert sich aus Westen!' WHERE entry=2000005627;
UPDATE dbscript_string SET content_loc3='Ich habe keine Pfeile mehr! Ich fürchte, wenn noch mehr kommen, seid Ihr auf euch alleine gestellt, $GFreund:Freundin;.' WHERE entry=2000005628;
UPDATE dbscript_string SET content_loc3='Gesegnet sei Elune! Ich weiß nicht, ob ich den Tag ohne Euch überlebt hätte, $GFreund:Freundin;.' WHERE entry=2000005629;
UPDATE dbscript_string SET content_loc3='Meinen Beinen gehts schon besser, das Gegengift wirkt schon. Wenn Ihr mich jetzt entschuldigen würdet, ich muss Meldung an meine Vorgesetzten machen über das was hier passiert ist.' WHERE entry=2000005630;
UPDATE dbscript_string SET content_loc3='Bitte sprecht mit Schildwache Onaeya in Maestras Posten im Eschental, sie wird sich darum kümmern, das Ihr angemessen entlohnt werdet für Eure heutige Tapferkeit.' WHERE entry=2000005631;
UPDATE dbscript_string SET content_loc3='Alle Mann auf Kampfposition! Naga im Anmarsch!' WHERE entry=2000005632;
UPDATE dbscript_string SET content_loc3='Wenn wir sie jetzt festhalten können, dann haben wir es fast geschafft.' WHERE entry=2000005633;
UPDATE dbscript_string SET content_loc3='Feuer auf mein Kommando! Legt an, zielt...' WHERE entry=2000005634;
UPDATE dbscript_string SET content_loc3='FEUER NACH EIGENEM ERMESSEN!' WHERE entry=2000005635;
UPDATE dbscript_string SET content_loc3='Schützen, formiert Euch und nehmt Eure Position ein!' WHERE entry=2000005636;
UPDATE dbscript_string SET content_loc3='Wir haben es geschafft! Wir haben Tethyr besiegt!' WHERE entry=2000005637;
UPDATE dbscript_string SET content_loc3='Wir haben das Meeresungeheuer besiegt!' WHERE entry=2000005638;
