UPDATE dbscript_string SET content_loc3='Vielen Dank, dass Ihr mich befreit habt, $N! Ich werde es alleine nach Shattrath schaffen.' WHERE entry=2000020108;
UPDATE dbscript_string SET content_loc3='Gut gemacht! Beeilt Euch, lasst sie uns nicht einfangen!' WHERE entry=2000020109;
UPDATE dbscript_string SET content_loc3='Alles was Ihr wisst, wird ungeschehen.' WHERE entry=2000020110;
UPDATE dbscript_string SET content_loc3='Wir haben alle Zeit der Welt...' WHERE entry=2000020111;
UPDATE dbscript_string SET content_loc3='Ihr könnt uns nicht entkommen!' WHERE entry=2000020112;
UPDATE dbscript_string SET content_loc3='Denkt nicht, Ihr könntet gewinnen!' WHERE entry=2000020113;
UPDATE dbscript_string SET content_loc3='Ihr dort, holt schnell Wasser! Löscht die Flammen, bevor sie auf den Rest der Festung übergreifen! Macht schon, verdammt!' WHERE entry=2000020114;
UPDATE dbscript_string SET content_loc3='Huch! Da bist du ja!' WHERE entry=2000020115;
UPDATE dbscript_string SET content_loc3='Hier, miez, miez...' WHERE entry=2000020116;
UPDATE dbscript_string SET content_loc3='Wo steckst du? Ihr listigen Kätzchen, ihr!' WHERE entry=2000020117;
UPDATE dbscript_string SET content_loc3='Du hast mich gerettet, Mann! Ich war überzeugt, dass man mich ertränken wird.' WHERE entry=2000020118;
UPDATE dbscript_string SET content_loc3='Sag einfach Bescheid, wenn ich dir irgendwie helfen kann, Mann.' WHERE entry=2000020119;
UPDATE dbscript_string SET content_loc3='Möge das Licht Euch segnen.' WHERE entry=2000020120;
UPDATE dbscript_string SET content_loc3='Eure Verletzungen sind ernst, doch Ihr werdet leben.' WHERE entry=2000020121;
UPDATE dbscript_string SET content_loc3='Ruhet nun - Ihr habt heute gut gekämpft.' WHERE entry=2000020122;
UPDATE dbscript_string SET content_loc3='Eindringlinge! Sorgt dafür, dass sie das Sanktum nicht betreten!' WHERE entry=2000020123;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000020124; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Ich werde niemals aufhören. Niemals...' WHERE entry=2000020125;
UPDATE dbscript_string SET content_loc3='Die Kraft! Mehr, mehr, mehr!' WHERE entry=2000020126;
UPDATE dbscript_string SET content_loc3='Es siedet und brennt...' WHERE entry=2000020127;
UPDATE dbscript_string SET content_loc3='Die ewig gleiche Arbeit langweilt mich langsam.' WHERE entry=2000020128;
UPDATE dbscript_string SET content_loc3='Ihr habt mir mein Herz genommen, jetzt nehme ich das Eure!' WHERE entry=2000020129;
UPDATE dbscript_string SET content_loc3='Warum willste in meinem Inneren herumfummeln, Mann?' WHERE entry=2000020130;
UPDATE dbscript_string SET content_loc3='Hey, das ist meine Leber, auf der du da rumdrückst, Mann!' WHERE entry=2000020131;
UPDATE dbscript_string SET content_loc3='Kommt sofort mit meinen Eingeweiden zurück, $R!' WHERE entry=2000020132;
UPDATE dbscript_string SET content_loc3='Endlich frei, Mann.' WHERE entry=2000020133;
UPDATE dbscript_string SET content_loc3='Ich könnte ewig schlafen, Mann...' WHERE entry=2000020134;
UPDATE dbscript_string SET content_loc3='Wo is\' meine Mumie?' WHERE entry=2000020135;
UPDATE dbscript_string SET content_loc3='Endlich kann ich ruhen...' WHERE entry=2000020136;
UPDATE dbscript_string SET content_loc3='Nun endlich, eine würdige Opfergabe!' WHERE entry=2000020137;
UPDATE dbscript_string SET content_loc3='Ahhh! Was passiert mit mir, Mann?' WHERE entry=2000020138;
UPDATE dbscript_string SET content_loc3='Junge, Junge... Jones und ich haben schon so ein paar Abenteuer zusammen erlebt...' WHERE entry=2000020139;
UPDATE dbscript_string SET content_loc3='%s holt tief Luft und drückt die Brust raus.' WHERE entry=2000020140;
UPDATE dbscript_string SET content_loc3='Wo Jones ist, da bin auch ich. Wo er hingeht, da gehe auch ich hin. Aber dieses Mal hat er mir befohlen, zurückzubleiben.' WHERE entry=2000020141;
UPDATE dbscript_string SET content_loc3='Ich hoffe, Jones ist in Ordnung.' WHERE entry=2000020142;
UPDATE dbscript_string SET content_loc3='%s schaut sich nervös um.' WHERE entry=2000020143;
UPDATE dbscript_string SET content_loc3='Wenn Jones auf mich hören würde, dann würde er länger leben.' WHERE entry=2000020144;
UPDATE dbscript_string SET content_loc3='Ich hoffe, Jones schafft es in einem Stück zurück.' WHERE entry=2000020145;
UPDATE dbscript_string SET content_loc3='%s schaut in die Ferne, genervt.' WHERE entry=2000020146;
UPDATE dbscript_string SET content_loc3='Jones schuldet mir immer noch Geld von unserem letzten Kartenspiel. Kaum zu glauben, aber er hat doch tatsächlich versucht, mich zu betrügen! Ich bin vielleicht klein, aber er ist ein Riesenbetrüger!' WHERE entry=2000020147;
UPDATE dbscript_string SET content_loc3='Junge, Junge... Ich kann gar nicht glauben, dass ich mich gerade daran erinnert habe...' WHERE entry=2000020148;
UPDATE dbscript_string SET content_loc3='%s kann sein Lachen kaum zurückhalten. Seine Geschichte wird beständig von seinem eigenen Lachen unterbrochen.' WHERE entry=2000020149;
UPDATE dbscript_string SET content_loc3='... also hat er die Kartoffeln fallenlassen, obwohl ich ihm gesagt hatte, sie nicht loszulassen.' WHERE entry=2000020150;
UPDATE dbscript_string SET content_loc3='Dieser Jones...' WHERE entry=2000020151;
UPDATE dbscript_string SET content_loc3='Er hat nicht nur eine kleine Schraube locker, er ist vollkommen verrückt! Immer unterwegs, auf der Jagd nach dem nächsten Schatz und dem großen Ruhm...' WHERE entry=2000020152;
