UPDATE script_texts SET content_loc3='Ich werde jetzt anfangen, Energie aus dem Stein zu ziehen. Eure Aufgabe, $N, ist es, mich zu verteidigen. Dieser Ort ist verflucht... glaubt mir.' WHERE entry=-1000266;
