UPDATE script_texts SET content_loc3='Ich bezahle! Ich bezahle! Uff! Bitte tut mir nich weh!' WHERE entry=-1000604;
UPDATE script_texts SET content_loc3='Lasst uns wissen, wenn Ihr soweit seid.' WHERE entry=-1000605;
UPDATE script_texts SET content_loc3='Nach Hause gehen!' WHERE entry=-1000606;
