UPDATE script_texts SET content_loc3='Lasst mich nicht hier allein! Denn wenn Ihr das tut, werde ich Euch finden...' WHERE entry=-1000631;
UPDATE script_texts SET content_loc3='Gut, lasst uns hier verschwinden!' WHERE entry=-1000632;
UPDATE script_texts SET content_loc3='Seid Ihr sicher, dass Ihr bereit seid? Lasst Euch Zeit.' WHERE entry=-1000633;
UPDATE script_texts SET content_loc3='Fein, dann los!' WHERE entry=-1000634;
UPDATE script_texts SET content_loc3='Also sind wir auch verflucht?' WHERE entry=-1000635;
UPDATE script_texts SET content_loc3='%s zeigt auf den Säugling.' WHERE entry=-1000636;
UPDATE script_texts SET content_loc3='%s schluchzt.' WHERE entry=-1000637;
UPDATE script_texts SET content_loc3='Die Götter haben uns verlassen! Wir müssen es beseitigen, bevor Ymiron davon erfährt!' WHERE entry=-1000638;
UPDATE script_texts SET content_loc3='NEIN! Das dürft Ihr nicht! Ich flehe Euch an! Es ist doch unser Kind!' WHERE entry=-1000639;
UPDATE script_texts SET content_loc3='Und was sollen wir stattdessen tun, Weib? Die anderen dürfen nichts davon wissen. Sollten sie von dieser Missgeburt erfahren, werden wir alle hingerichtet werden.' WHERE entry=-1000640;
UPDATE script_texts SET content_loc3='Ich... ich suche ein Versteck. Ich verstecke es, bis ich weit entfernt von hier eine neue Heimat dafür finde...' WHERE entry=-1000641;
UPDATE script_texts SET content_loc3='Es ist ein Weibchen!' WHERE entry=-1000642;
UPDATE script_texts SET content_loc3='Es ist ein wildes Männchen!' WHERE entry=-1000643;
UPDATE script_texts SET content_loc3='Autsch! Jetzt reicht\'s, ich bin raus aus dem Zielgeschäft!' WHERE entry=-1000644;
UPDATE script_texts SET content_loc3='Mein Ohr! Ihr habt mein Ohr getroffen!' WHERE entry=-1000645;
UPDATE script_texts SET content_loc3='Nicht der Schnurrbart! Jetzt bin ich asymmetrisch!' WHERE entry=-1000646;
UPDATE script_texts SET content_loc3='Netter Schuss!' WHERE entry=-1000647;
UPDATE script_texts SET content_loc3='Hört auf rumzuheulen. Ihr habt doch noch immer Glück.' WHERE entry=-1000648;
UPDATE script_texts SET content_loc3='Bah, es ist eine Verbesserung.' WHERE entry=-1000649;
UPDATE script_texts SET content_loc3='Ganz ruhig, Junge, es ist doch nur ein Vogelschuss!' WHERE entry=-1000650;
UPDATE script_texts SET content_loc3='Das Einzige was schmerzt ist dein Stolz, Junge. Bleib munter!' WHERE entry=-1000651;
UPDATE script_texts SET content_loc3='Ich so hungrig! MJAM!' WHERE entry=-1000652;
UPDATE script_texts SET content_loc3='He... mir nicht gehen so gut.' WHERE entry=-1000653;
UPDATE script_texts SET content_loc3='Ihr sein böser Orc... böööser... Or... argh!' WHERE entry=-1000654;
UPDATE script_texts SET content_loc3='Zeit zum Fressen?!' WHERE entry=-1000655;
UPDATE script_texts SET content_loc3='Ich tun Hammel in Magen!' WHERE entry=-1000656;
UPDATE script_texts SET content_loc3='Wir sollten verdammt noch mal von hier verschwinden.' WHERE entry=-1000657;
UPDATE script_texts SET content_loc3='Zuhören, Trottel der Venture Co.! Regel Nr. 1: Sperrt Gefangene niemals in der Nähe von Sprengstoff ein.' WHERE entry=-1000658;
UPDATE script_texts SET content_loc3='Oder DAS erwartet Euch.' WHERE entry=-1000659;
-- UPDATE script_texts SET content_loc3='' WHERE entry=-1000660;
UPDATE script_texts SET content_loc3='Oh, schaut mal, eine weitere Wagenladung Sprengstoff! Helfen wir ihnen, den los zu werden.' WHERE entry=-1000661;
UPDATE script_texts SET content_loc3='Ihr solltet wirklich nicht mit diesem Zeug spielen. Jemand könnte dabei verletzt werden.' WHERE entry=-1000662;
UPDATE script_texts SET content_loc3='Wir haben es geschafft! Habt Dank dafür, dass Ihr mich aus diesem Höllenloch geholt habt. Sagt Hemet Bescheid, dass ich komme!' WHERE entry=-1000663;
UPDATE script_texts SET content_loc3='Die zerstörerische Barriere gewinnt an Kraft.' WHERE entry=-1000664;
UPDATE script_texts SET content_loc3='Die zerstörerische Barriere ist vollständig aufgeladen!' WHERE entry=-1000665;
