UPDATE script_texts SET content_loc3='Erhebt euch, Darion, und hört zu...' WHERE entry=-1609260;
UPDATE script_texts SET content_loc3='Wir sind alle Zeugen einer schrecklichen Tragödie geworden. Das Blut guter Krieger ist auf diesem Boden vergossen worden! Ehrenhafte Ritter erschlagen, während sie ihr Leben verteidigten, unsere Leben!' WHERE entry=-1609261;
