UPDATE script_texts SET content_loc3='Niedere Wesen wagen es, hier einzudringen?! Eine Schande, dass euer großer Mut nicht eure Dummheit kompensiert!' WHERE entry=-1616000;
UPDATE script_texts SET content_loc3='Niemand außer dem blauen Drachenschwarm ist hier willkommen. Ist das vielleicht das Werk von Alexstrasza? Nun, dann hat sie euch in den Tod geschickt!' WHERE entry=-1616001;
UPDATE script_texts SET content_loc3='Was erhofftet Ihr zu erreichen? Hier dreist einzudringen, in mein Reich... Magie einzusetzen... gegen MICH? <Er lacht.>' WHERE entry=-1616002;
