UPDATE script_texts SET content_loc3='Ich gebe euch eine Chance. Schwört mir die Treue und vielleicht schlachte ich euch dann nicht für eure Anmaßung ab.' WHERE entry=-1616004;
UPDATE script_texts SET content_loc3='Meine Geduld ist am Ende. Ich werde mich eurer entledigen!' WHERE entry=-1616005;
