UPDATE script_texts SET content_loc3='Mehr Artefakte für meine Sammlung.' WHERE entry=-1616009;
UPDATE script_texts SET content_loc3='<Er lacht.> Oh... wie naiv...' WHERE entry=-1616010;
UPDATE script_texts SET content_loc3='Ich hatte gehofft, eure Leben schnell zu beenden, doch ihr zeigt euch... hartnäckiger als erwartet. Nichtsdestotrotz sind eure Bemühungen vergebens. Ihr törichten, leichtfertigen Sterblichen tragt die Schuld an diesem Krieg. Ich tue, was ich tun muss, und wenn das eure Auslöschung bedeutet... dann SOLL ES SO SEIN!' WHERE entry=-1616011;
