UPDATE script_texts SET content_loc3='Solange ich atme, werdet ihr nicht obsiegen!' WHERE entry=-1616013;
UPDATE script_texts SET content_loc3='Ich werde Euch dummen Kindern zeigen, wie wenig Ihr von Magie versteht!' WHERE entry=-1616014;
