UPDATE script_texts SET content_loc3='Liladris wartet bei Maestras Posten schon auf mich... wir sollten uns besser beeilen, $N.' WHERE entry=-1000434;
UPDATE script_texts SET content_loc3='%s sieht sehr schläfrig aus...' WHERE entry=-1000435;
UPDATE script_texts SET content_loc3='%s schläft plötzlich ein.' WHERE entry=-1000436;
UPDATE script_texts SET content_loc3='%s nickt langsam ein...' WHERE entry=-1000437;
UPDATE script_texts SET content_loc3='Das könnte der perfekte Platz sein, um ein kleines Nickerchen zu halten...' WHERE entry=-1000438;
UPDATE script_texts SET content_loc3='Gääääähhhhhnnnn...' WHERE entry=-1000439;
UPDATE script_texts SET content_loc3='Ich bin ja so müde...' WHERE entry=-1000440;
UPDATE script_texts SET content_loc3='Es stört Euch doch nicht, wenn ich einen Moment hier bleibe, oder?' WHERE entry=-1000441;
UPDATE script_texts SET content_loc3='Seid auf der Hut! In dieser Gegend gibt es äußerst viele Furbolgs der Schwarzfelle...' WHERE entry=-1000442;
UPDATE script_texts SET content_loc3='Es ist still... Zu still...' WHERE entry=-1000443;
UPDATE script_texts SET content_loc3='Oh, ich kann Liladris von hier aus schon sehen... Sagt Ihr, dass ich hier bin, ja?' WHERE entry=-1000444;
UPDATE script_texts SET content_loc3='%s wacht auf!' WHERE entry=-1000445;
UPDATE script_texts SET content_loc3='A-Me. Gut, A-Me. Folgen... A-Me folgen. Heim. A-Me geht heim.' WHERE entry=-1000446;
UPDATE script_texts SET content_loc3='Gut... gut, A-Me. A-Me gut. Heim. Heim suchen.' WHERE entry=-1000447;
UPDATE script_texts SET content_loc3='A-Me daheim! A-Me, gut! Gute A-Me. Daheim. Daheim. Daheim.' WHERE entry=-1000448;
UPDATE script_texts SET content_loc3='$N A-Me nicht wehtun. A-Me gut.' WHERE entry=-1000449;
UPDATE script_texts SET content_loc3='Gefahr. Gefahr! $N will A-Me wehtun.' WHERE entry=-1000450;
UPDATE script_texts SET content_loc3='Böser $N. $N böse!' WHERE entry=-1000451;
UPDATE script_texts SET content_loc3='Mir ist ein Feuer auf der Insel dort drüben aufgefallen. Auch ein Mensch. Lasst uns nachschauen, $N.' WHERE entry=-1000452;
UPDATE script_texts SET content_loc3='Das ist Reethe. Mal sehen, was er zu sagen hat, ja?' WHERE entry=-1000453;
UPDATE script_texts SET content_loc3='W...was wollt Ihr? Lasst mich einfach in Ruhe...' WHERE entry=-1000454;
UPDATE script_texts SET content_loc3='Ich schwöre, ich habe Euch nichts gestohlen! Hier, nehmt ein paar meiner Vorräte, aber geht bloß fort!' WHERE entry=-1000455;
UPDATE script_texts SET content_loc3='Sagt uns einfach, was Ihr über das Gasthaus Zur süßen Ruh wisst, und ich werde Euch nicht den Schädel einschlagen.' WHERE entry=-1000456;
UPDATE script_texts SET content_loc3='Ich... nun, ich habe vielleicht ein oder zwei Sachen aus dem Gasthaus mitgenommen... aber was kümmert das einen Oger?' WHERE entry=-1000457;
UPDATE script_texts SET content_loc3='Nun, wenn Ihr mir nichts über das Feuer erzählen wollt...' WHERE entry=-1000458;
UPDATE script_texts SET content_loc3='Keinen Schritt näher, Oger!' WHERE entry=-1000459;
UPDATE script_texts SET content_loc3='Und ich weiß absolut nichts über Euer Feuer...' WHERE entry=-1000460;
UPDATE script_texts SET content_loc3='Was war das? Habt Ihr etwas gehört?' WHERE entry=-1000461;
UPDATE script_texts SET content_loc3='Paval Reethe! Endlich habe ich Euch gefunden. Verkehrt Ihr jetzt schon mit Ogern? Keine Angst, selbst Deserteuren und Verrätern wird ein wenig Gnade gewährt.' WHERE entry=-1000462;
UPDATE script_texts SET content_loc3='Gefreiter, zeigt gegenüber Leutnant Reethe ein wenig Mitleid.' WHERE entry=-1000463;
UPDATE script_texts SET content_loc3='Mit Vergnügen, Sire.' WHERE entry=-1000464;
UPDATE script_texts SET content_loc3='%s taumelt zurück, als sich der Pfeil tief in seine Brust bohrt.' WHERE entry=-1000465;
UPDATE script_texts SET content_loc3='Uh... Hallan, das hätte ich Euch gar nicht zugetraut...' WHERE entry=-1000466;
UPDATE script_texts SET content_loc3='Räumen wir jetzt den Rest dieses Abfalls auf, Männer!' WHERE entry=-1000467;
UPDATE script_texts SET content_loc3='Verflucht! Ihr sterbt jetzt doch, Mensch!' WHERE entry=-1000468;
UPDATE script_texts SET content_loc3='Seid Ihr noch unter uns, Reethe?' WHERE entry=-1000469;
UPDATE script_texts SET content_loc3='Heute muss Euer Glückstag sein. Gut, ich rede. Lasst mich nur in Ruhe. Schaut, Ihr werdet mir nicht glauben, aber... oh, beim Licht, offensichtlich konnte das Mädchen schießen...' WHERE entry=-1000470;
UPDATE script_texts SET content_loc3='Danke übrigens, dass Ihr mir den Rücken freigehalten habt.' WHERE entry=-1000471;
UPDATE script_texts SET content_loc3='Der %s fällt nach einem guten Kampf auseinander.' WHERE entry=-1000472;
UPDATE script_texts SET content_loc3='Das mechanische Herz des %ss beginnt, sanft zu schlagen.' WHERE entry=-1000473;
UPDATE script_texts SET content_loc3='Die Notabschaltung der Naaru wird die Manaschmiede dauerhaft zerstören! Sie muss abgebrochen werden!' WHERE entry=-1000474;
UPDATE script_texts SET content_loc3='Hört sofort damit auf!' WHERE entry=-1000475;
UPDATE script_texts SET content_loc3='Ihr werdet meine Manaschmiede nicht abschalten, Abschaum!' WHERE entry=-1000476;
UPDATE script_texts SET content_loc3='Lasst uns diesen Ort verlassen. Ich habe genug von diesen Irren!' WHERE entry=-1000477;
UPDATE script_texts SET content_loc3='Ihr werdet nirgendwo hingehen, Narr!' WHERE entry=-1000478;
UPDATE script_texts SET content_loc3='Seht Euch vor! Dort kommen mehr Kultisten!' WHERE entry=-1000479;
UPDATE script_texts SET content_loc3='Ihr werdet uns nicht so leicht entkommen!' WHERE entry=-1000480;
UPDATE script_texts SET content_loc3='Von hier aus finde ich den Weg alleine. Mein Freund Mekeda wird Euch für Eure gute Tat belohnen.' WHERE entry=-1000481;
UPDATE script_texts SET content_loc3='Achtung!' WHERE entry=-1000482;
UPDATE script_texts SET content_loc3='Lasst sie nicht entfliehen! Tötet den starken zuerst!' WHERE entry=-1000483;
UPDATE script_texts SET content_loc3='Da kommen mehr von ihnen! Passt auf!' WHERE entry=-1000484;
UPDATE script_texts SET content_loc3='Was denkt Ihr wohin Ihr geht? Tötet sie alle!' WHERE entry=-1000485;
UPDATE script_texts SET content_loc3='Der Blitz möge Euch treffen, Abschaum!' WHERE entry=-1000486;
UPDATE script_texts SET content_loc3='FROSTSCHOCK!' WHERE entry=-1000487;
UPDATE script_texts SET content_loc3='Es ist besser, wenn wir uns jetzt aufteilen, falls sie noch mehr hinter uns her schicken. Hoffentlich schafft es einer von zurück zu Garrosh. Lebt wohl, $GFremder:Fremde;.' WHERE entry=-1000488;
UPDATE script_texts SET content_loc3='Führt unseren Gast in Shattrath herum, ja? Gebt auf die Taschendiebe im unteren Viertel acht.' WHERE entry=-1000489;
UPDATE script_texts SET content_loc3='Ein Neuankömmling in Shattrath! Kommt doch später einmal vorbei. Wir können immer Hilfe bei den Verletzten brauchen.' WHERE entry=-1000490;
UPDATE script_texts SET content_loc3='Passt auf, was Ihr sagt, der hier wird von Khadgars Schoßtierchen herumgeführt.' WHERE entry=-1000491;
UPDATE script_texts SET content_loc3='Macht ihr Witze? Ich bin ein Seher, ich achte immer darauf, was ich sage. Es gibt wohl mehr Feinde als Freunde hier in dieser Stadt.' WHERE entry=-1000492;
UPDATE script_texts SET content_loc3='Möge das Licht mit Euch sein, $N. Und willkommen in Shattrath.' WHERE entry=-1000493;
UPDATE script_texts SET content_loc3='Wir sind sehr wählerisch, wenn es um unsere Freunde geht, $N. Denkt Ihr, Ihr habt, was es dazu braucht?' WHERE entry=-1000494;
UPDATE script_texts SET content_loc3='Khadgar selbst führt Euch herrum? Ihr müsst einen guten Eindruck hinterlassen haben, $N.' WHERE entry=-1000495;
UPDATE script_texts SET content_loc3='%s hebt lauschend den Kopf in die Höhe.' WHERE entry=-1000496;
UPDATE script_texts SET content_loc3='%s lässt ein Heulen in Richtung der nördlichen Berge los und gibt Euch ein Zeichen zu folgen.' WHERE entry=-1000497;
UPDATE script_texts SET content_loc3='Willkommen. Was hat Euch zu uns geführt?' WHERE entry=-1000498;
UPDATE script_texts SET content_loc3='Mickriger $R will gegen Anführer Mok\'Morokk kämpfen? Ich Euch schlagen. Ich hier Boss!' WHERE entry=-1000499;
UPDATE script_texts SET content_loc3='Ich haben Angst! Ich rennen jetzt weg!' WHERE entry=-1000500;
UPDATE script_texts SET content_loc3='Seid Ihr sicher, dass Ihr bereit seid? Falls uns keine Gruppe Eurer Verbündeten hilft, werden wir mit Sicherheit verlieren.' WHERE entry=-1000501;
UPDATE script_texts SET content_loc3='Das wird ein harter Kampf, $N. Bleibt nahe bei mir!' WHERE entry=-1000502;
UPDATE script_texts SET content_loc3='Das ist die Kohlenpfanne, $N. Löscht sie. Vorsha ist eine Bestie, die von niemandem Lob verdient!' WHERE entry=-1000503;
UPDATE script_texts SET content_loc3='Jetzt müssen wir warten. Es wird nicht lange dauern, bis die Naga erkennen, was wir getan haben.' WHERE entry=-1000504;
UPDATE script_texts SET content_loc3='Seid auf der Hut, $N!' WHERE entry=-1000505;
UPDATE script_texts SET content_loc3='Vielleicht können wir uns einen Moment lang ausruhen. Aber ich werde nicht aufgeben, bis wir Vorsha gegenübergetreten sind!' WHERE entry=-1000506;
UPDATE script_texts SET content_loc3='Wir haben es geschafft!' WHERE entry=-1000507;
UPDATE script_texts SET content_loc3='Ihr habt meine tiefe Anerkennung. Ich danke Euch.' WHERE entry=-1000508;
UPDATE script_texts SET content_loc3='Ich werde noch eine Weile in diesem Bereich patrouillieren, damit wirklich alles sicher ist.' WHERE entry=-1000509;
UPDATE script_texts SET content_loc3='Kehrt bitte nach Zoram\'gar zurück und meldet dem Läufer des Kriegshymnenklans unseren Erfolg.' WHERE entry=-1000510;
UPDATE script_texts SET content_loc3='So, hört zu! Formt einen Kreis um mich, dann geht es los!' WHERE entry=-1000511;
UPDATE script_texts SET content_loc3='Nun gut, $R. beschützt uns während wir arbeiten, und wir werden Euch bezahlen, wenn wir zurück sind.' WHERE entry=-1000512;
UPDATE script_texts SET content_loc3='Die Ausgrabungsstätte ist direkt nördlich von hier.' WHERE entry=-1000513;
UPDATE script_texts SET content_loc3='Wir sind da! Fangt mit dem Graben an!' WHERE entry=-1000514;
UPDATE script_texts SET content_loc3='Ich glaube hier ist etwas vergraben, unter dem Sand!' WHERE entry=-1000515;
UPDATE script_texts SET content_loc3='Ich habe es fast!' WHERE entry=-1000516;
UPDATE script_texts SET content_loc3='Bei Branns brüchigen Bananen! Was ist das? Es sieht aus wie eine Trommel...' WHERE entry=-1000517;
UPDATE script_texts SET content_loc3='Toll. Eine Trommel.' WHERE entry=-1000518;
UPDATE script_texts SET content_loc3='Diese Entdeckung wird die moderne Archäologie in ihren Gunsten erschüttern.' WHERE entry=-1000519;
UPDATE script_texts SET content_loc3='Ja, super. Können wir jetzt gehen? In der Wüste kriege ich eine Gänsehaut...' WHERE entry=-1000520;
UPDATE script_texts SET content_loc3='Seid Ihr wahnsinnig? Ihr erwartet von mir, dass ich eine Trommel zurücklasse, ohne sie zuvor zu schlagen? Nicht dieser Sohn von Eisenschmiede! Nein, mein Herr!' WHERE entry=-1000521;
UPDATE script_texts SET content_loc3='Das erinnert mich an die Zeit, als wir für Euch in Silithus nach Spuren von Sandgnomen suchen mussten.' WHERE entry=-1000522;
UPDATE script_texts SET content_loc3='Oder als Ihr uns erzählt habt, Ihr hättet ein Heilmittel für die Seuche des 20. Jahrhundert gefunden. Was ist das überhaupt, ein 20. Jahrhundert?' WHERE entry=-1000523;
UPDATE script_texts SET content_loc3='Nichts geht über das Mal, als er uns erzählt hat, unter einem Gletscher in Winterquell wäre ein Skelett des \"Cousins\" von Arthas eingefroren. Ich bitte Euch, Letoll. Ich meine, ehrlich... ein Cousin von Arthas?' WHERE entry=-1000524;
UPDATE script_texts SET content_loc3='Ich weiß nicht. Noch besser war doch die Geschichte, als er uns überzeugen wollte, wir hätten nur Hirngespinste von irgendjemandem, der uns für sein persönliches Vergnügen benutzt. Das ist beim Abendessen mit der Familie ganz toll angekommen.' WHERE entry=-1000525;
UPDATE script_texts SET content_loc3='Haltet die Klappe! Ich werde auf die Trommel schlagen, und keine Widerrede!' WHERE entry=-1000526;
UPDATE script_texts SET content_loc3='Sagt, habt Ihr das gehört?' WHERE entry=-1000527;
UPDATE script_texts SET content_loc3='NA, WAS HAB ICH GESAGT? Ich sagte Euch doch, dass hier etwas ist!' WHERE entry=-1000528;
UPDATE script_texts SET content_loc3='Steht da nicht rum! Helft, sie da rauszuholen!' WHERE entry=-1000529;
UPDATE script_texts SET content_loc3='%s hebt die Trommel auf.' WHERE entry=-1000530;
UPDATE script_texts SET content_loc3='Ihr wart eine enorme Hilfe, $R! Lasst uns hier verschwinden, bevor mehr von diesen Dingern auftauchen! Ich lasse Zwergowitz wissen, dass Ihr Eure Aufgabe bewundernswert erfüllt habt.' WHERE entry=-1000531;
UPDATE script_texts SET content_loc3='Wie Ihr befehlt, mein Herr...' WHERE entry=-1000532;
UPDATE script_texts SET content_loc3='Wie Ihr wünscht, Fürst Illidan.' WHERE entry=-1000533;
UPDATE script_texts SET content_loc3='Für Fürst Illidan würde ich selbst diesen großartigen Körperbau opfern. An diesem Tag werdet ihr fallen... ein weiteres Opfer von Torloth...' WHERE entry=-1000534;
UPDATE script_texts SET content_loc3='Zerstört sie, Torloth. Lasst sie auf diesem geheiligten Boden ihr Blut in Strömen vergießen.' WHERE entry=-1000535;
UPDATE script_texts SET content_loc3='Welcher Narr wagt es, sich Illidan Sturmgrimm entgegenzustellen? Soldaten, vernichtet dieses Gewürm!' WHERE entry=-1000536;
UPDATE script_texts SET content_loc3='Ihr seid keine Herausforderung für das Purpursiegel. Willenbrecher, macht diesem Unsinn ein Ende' WHERE entry=-1000537;
UPDATE script_texts SET content_loc3='Lauft, solange Ihr noch könnt. Bald kommen die Hochlords...' WHERE entry=-1000538;
UPDATE script_texts SET content_loc3='Torloth, Euer Meister ruft Euch!' WHERE entry=-1000539;
UPDATE script_texts SET content_loc3='Ihr habt also das Purpursiegel besiegt. Nun wollt Ihr meine Herrschaft herausfordern? Nicht einmal Arthas konnte mich besiegen! Wie könnt Ihr es wagen, nur daran zu denken? Dann kommt doch! Kommt, $N! Der Schwarze Tempel erwartet Euch...' WHERE entry=-1000540;
UPDATE script_texts SET content_loc3='%s springt in den Mondbrunnen und versinkt im Wasser...' WHERE entry=-1000541;
UPDATE script_texts SET content_loc3='%s folgt gehorsam $N.' WHERE entry=-1000542;
UPDATE script_texts SET content_loc3='Warum seid Ihr hierher gekommen, Außenseiter? Ihr werdet nichts als Schmerzen finden! Unser Schicksal wird das Eure sein...' WHERE entry=-1000543;
UPDATE script_texts SET content_loc3='Es war... furchtbar... der Dämon...' WHERE entry=-1000544;
UPDATE script_texts SET content_loc3='Die Dunkelheit... die Verderbnis... alles geschah so schnell, niemand hat damit gerechnet...' WHERE entry=-1000545;
UPDATE script_texts SET content_loc3='Die Dunkelheit wird alles verzehren... alles, was lebt...' WHERE entry=-1000546;
UPDATE script_texts SET content_loc3='Es ist zu spät für uns, Lebender. Ihr solltet mit Eurem Freund von hier verschwinden, bevor man Euch auch noch... einfordert...' WHERE entry=-1000547;
UPDATE script_texts SET content_loc3='Es ist zu spät für Jarl... er war schon zu lange an diesem Ort...' WHERE entry=-1000548;
UPDATE script_texts SET content_loc3='Wer immer Ihr auch sein mögt, verschwindet! Der Hexenhügel gehört mir... mir!' WHERE entry=-1000549;
UPDATE script_texts SET content_loc3='Das Anwesen... ein anderer... wird bald verzehrt werden...' WHERE entry=-1000550;
UPDATE script_texts SET content_loc3='Euer Ansinnen hat %s verärgert. Angriff!' WHERE entry=-1000551;
UPDATE script_texts SET content_loc3='Bevor %s Euch verlässt, erhaltet Ihr ein Stück aus der Borke, welches gerade herausgebrochen wurde.' WHERE entry=-1000552;
UPDATE script_texts SET content_loc3='Seid bereit, $N. Ich höre den Rat zurückkommen. Hinterhalt vorbereiten!' WHERE entry=-1000553;
UPDATE script_texts SET content_loc3='Gute Arbeit. Mit Sicherheit ein schwerer Schlag für Arugal!' WHERE entry=-1000554;
UPDATE script_texts SET content_loc3='Zurück... an die Arbeit...' WHERE entry=-1000555;
UPDATE script_texts SET content_loc3='Ihr behandelt uns schlimmer als das letzte Vieh!' WHERE entry=-1000556;
UPDATE script_texts SET content_loc3='Wir werden Rache üben... eines Tages.' WHERE entry=-1000557;
UPDATE script_texts SET content_loc3='Verflucht seid Ihr! Ihr werdet mich nicht wie Vieh behandeln!' WHERE entry=-1000558;
UPDATE script_texts SET content_loc3='Ich sterbe lieber im Kampf, als wie ein Sklave zu leben!' WHERE entry=-1000559;
UPDATE script_texts SET content_loc3='Genug! Ich werde Euch Manieren beibringen, Weib!' WHERE entry=-1000560;
UPDATE script_texts SET content_loc3='Meine Wunden sind tief. Vergebt mir, dass ich so langsam bin, aber ich bin zu schwer verletzt, um schneller zu gehen.' WHERE entry=-1000561;
UPDATE script_texts SET content_loc3='Ah, endlich frische Luft! Ich muss mich einen Moment ausruhen.' WHERE entry=-1000562;
UPDATE script_texts SET content_loc3='Die Schwarzfelsplage hat sich in dieser Gegend stark ausgebreitet. Ich werde mein Bestes tun, um das Tempo zu halten. Los, gehen wir!' WHERE entry=-1000563;
UPDATE script_texts SET content_loc3='Marschall Marris, Sire. Korporal Keeshan vom 12. Säbelregiment zurück vom Kampf und meldet sich zum Dienst!' WHERE entry=-1000564;
UPDATE script_texts SET content_loc3='$GMutiger Abenteurer:Mutige Abenteurerin;, habt Dank für meine Rettung! Ich bin mir sicher, dass Marschall Marris Eure gute Tat belohnen wird.' WHERE entry=-1000565;
UPDATE script_texts SET content_loc3='Bleibt zurück! Macht Platz! Die Höllenbestie braucht viel Raum!' WHERE entry=-1000566;
UPDATE script_texts SET content_loc3='KNIET NIEDER VOR DEM ALLMÄCHTIGEN! VERNEIGT EUCH VOR MEINEM UNBESIEGBAREN HÖLLEN... Huhn?' WHERE entry=-1000567;
UPDATE script_texts SET content_loc3='%s kugelt sich vor lachen.' WHERE entry=-1000568;
UPDATE script_texts SET content_loc3='Niby, du bist ein Idiot.' WHERE entry=-1000569;
UPDATE script_texts SET content_loc3='Schweige, Diener! Die Rache ist mein! Tod für Sturmwind! Tod durch Hühnchen!' WHERE entry=-1000570;
UPDATE script_texts SET content_loc3='Hilfe! Ich habe nur eine Hand, um mich zu verteidigen.' WHERE entry=-1000571;
UPDATE script_texts SET content_loc3='Spürt die Macht der Dunkeleisenzwerge!' WHERE entry=-1000572;
UPDATE script_texts SET content_loc3='Schickt sie nur her! Ich habe keine Angst vor diesen dürren Bestien!' WHERE entry=-1000573;
UPDATE script_texts SET content_loc3='Ah, endlich hier! Ich fühle micht bestimmt viel besser, wenn ich diese Fässer los bin.' WHERE entry=-1000574;
UPDATE script_texts SET content_loc3='Zusammen kämpfen wir uns hier raus. Seid Ihr bereit?' WHERE entry=-1000575;
UPDATE script_texts SET content_loc3='Dann brechen wir auf!' WHERE entry=-1000576;
UPDATE script_texts SET content_loc3='Das ist weit genug, von hier aus schaffe ich es alleine.' WHERE entry=-1000577;
UPDATE script_texts SET content_loc3='Danke, dass Ihr mich gerettet habt, $R. Bitte sagt dem König, dass ich zurückgekehrt bin.' WHERE entry=-1000578;
UPDATE script_texts SET content_loc3='Dort! Vernichtet ihn! Das Wort muss gefunden werden!' WHERE entry=-1000579;
UPDATE script_texts SET content_loc3='Schlaf nun, mein Kind...' WHERE entry=-1000580;
UPDATE script_texts SET content_loc3='Ein wundervolles Exemplar.' WHERE entry=-1000581;
UPDATE script_texts SET content_loc3='Hilfe! Bitte, Ihr müsst mir helfen!' WHERE entry=-1000582;
UPDATE script_texts SET content_loc3='Lasst uns von hier fortgehen.' WHERE entry=-1000583;
UPDATE script_texts SET content_loc3='Passt auf! $C greift an!' WHERE entry=-1000584;
UPDATE script_texts SET content_loc3='Hilfe! Ich werde angegriffen!' WHERE entry=-1000585;
UPDATE script_texts SET content_loc3='Danke, $N. Ich werde Euch nie vergessen! Ihr findet meine Geldkassette in meinem Lager nördlich von Steinard.' WHERE entry=-1000586;
UPDATE script_texts SET content_loc3='%s flüstert $N das Geheimnis zu, wie man die Geldkassette öffnet.' WHERE entry=-1000587;
UPDATE script_texts SET content_loc3='%s verschwindet im Sumpf.' WHERE entry=-1000588;
UPDATE script_texts SET content_loc3='Kroshius leben? Kroshius zermalmen!' WHERE entry=-1000589;
UPDATE script_texts SET content_loc3='Die maßlose Verderbnis hat ein Ende! Dank des Heldenmutes von $N und $Gseinen:ihren; Verbündeten! Seid Zeuge der Macht der Allianz! Ein Kommandant der Grubenlords wurde vernichtet!' WHERE entry=-1000590;
UPDATE script_texts SET content_loc3='Hört mich an, Waffenbrüder und -schwestern! Die Zeit unserer Feinde hat ein Ende! Jetzt ist die Gelegenheit für einen Schlag gegen die verbleibenden Höllenorcs! Zerquetscht sie wie Ungeziefer! Die Gunst der Söhne Lothars ist mit euch!' WHERE entry=-1000591;
UPDATE script_texts SET content_loc3='Die Höllenfeuerzitadelle gehört uns! Die Quelle von Illidans Höllenorcs gibt es nicht mehr! Magtheridon wurde besiegt von $Gdem:der; mächtigen $N und $Gseinen:ihren; Verbündeten.' WHERE entry=-1000592;
UPDATE script_texts SET content_loc3='Es ist Zeit, dass wir uns erheben und gegen die übrig gebliebenen Höllenorcs ziehen! Für unseren Kriegshäuptling! Für die Horde!' WHERE entry=-1000593;
UPDATE script_texts SET content_loc3='Endlich... nun kann ich ruhen.' WHERE entry=-1000594;
UPDATE script_texts SET content_loc3='Ich bin so müde. Lasst mich für einen Moment ruhen.' WHERE entry=-1000595;
UPDATE script_texts SET content_loc3='Ich kann die Schreie nicht mehr hören. Ist dies das Ende?' WHERE entry=-1000596;
UPDATE script_texts SET content_loc3='Mein Alptraum... ist er endlich vorbei?' WHERE entry=-1000597;
UPDATE script_texts SET content_loc3='Es war fürchterlich... ich träumte, ich würde gegen meine Freunde kämpfen.' WHERE entry=-1000598;
UPDATE script_texts SET content_loc3='Es ist ein Wunder! Die Bestie hat sich selbst die Haut abgezogen!' WHERE entry=-1000599;
UPDATE script_texts SET content_loc3='Jackpot!' WHERE entry=-1000600;
UPDATE script_texts SET content_loc3='Das ist der Letzte, den ich für das Nesingwary-Steakmesserset brauche!' WHERE entry=-1000601;
UPDATE script_texts SET content_loc3='Blöde Bestien!' WHERE entry=-1000602;
UPDATE script_texts SET content_loc3='Stellt meine Geduld nicht auf die Probe, Ihr gemeiner Hund! Ich wurde in der Kunst der Blutritter unterrichtet!' WHERE entry=-1000603;
UPDATE script_texts SET content_loc3='Ich bezahle! Ich bezahle! Uff! Bitte tut mir nich weh!' WHERE entry=-1000604;
UPDATE script_texts SET content_loc3='Lasst uns wissen, wenn Ihr soweit seid.' WHERE entry=-1000605;
UPDATE script_texts SET content_loc3='Nach Hause gehen!' WHERE entry=-1000606;
UPDATE script_texts SET content_loc3='Danke!' WHERE entry=-1000607;
UPDATE script_texts SET content_loc3='Oh nein! Ein paar Wauwauleute sin\' hinterhergekommen!' WHERE entry=-1000608;
UPDATE script_texts SET content_loc3='Dummer Großzungenliebhaber! Ihr nich\' mehr Freund der Wildherzen sein. Wildherzen werden Euch ordentlich eins draufgeben.' WHERE entry=-1000609;
UPDATE script_texts SET content_loc3='Das Mooswandleropfer stöhnt vor Schmerzen.' WHERE entry=-1000610;
UPDATE script_texts SET content_loc3='Vielleicht du machen Wetter auch besser?' WHERE entry=-1000611;
UPDATE script_texts SET content_loc3='Wir sicher. Ihr nett, Trockenhaut.' WHERE entry=-1000612;
UPDATE script_texts SET content_loc3='Du retten uns! Hurra für dich!' WHERE entry=-1000613;
UPDATE script_texts SET content_loc3='Danke sehr! Du gut!' WHERE entry=-1000614;
UPDATE script_texts SET content_loc3='Benutzen mein Glitzer-Glitzer... mach Wetter wieder gut... mach untote Dinger wieder verschwinden.' WHERE entry=-1000615;
UPDATE script_texts SET content_loc3='Wir haben Schrein Glitzer-Glitzer gegeben... wir nicht gierig... warum das passieren?' WHERE entry=-1000616;
UPDATE script_texts SET content_loc3='Ich was falsch gemacht? Tut mir traurig...' WHERE entry=-1000617;
UPDATE script_texts SET content_loc3='Wir nix getan... ihnen... Ich nix verstehen das.' WHERE entry=-1000618;
UPDATE script_texts SET content_loc3='Danke... schön.' WHERE entry=-1000619;
UPDATE script_texts SET content_loc3='Bitte nehmen... mein Glitzer-Glitzer. Alles fertig...' WHERE entry=-1000620;
UPDATE script_texts SET content_loc3='Alle Systeme startklar. Bereitmachen, es geht in Kürze los.' WHERE entry=-1000621;
UPDATE script_texts SET content_loc3='Gebt da drinnen auf Euch acht und kehrt in einem Stück zurück!' WHERE entry=-1000622;
UPDATE script_texts SET content_loc3='Weiter.' WHERE entry=-1000623;
UPDATE script_texts SET content_loc3='Ihr seid zurück! Konntet Ihr alle Maschinen bekommen?' WHERE entry=-1000624;
UPDATE script_texts SET content_loc3='%s sammelt das Blut des Sphärenhetzers.' WHERE entry=-1000625;
UPDATE script_texts SET content_loc3='Energiesammlung einleiten.' WHERE entry=-1000626;
UPDATE script_texts SET content_loc3='Energiesammlung abgeschlossen.' WHERE entry=-1000627;
UPDATE script_texts SET content_loc3='%s frisst von dem frisch erlegten Sphärenhetzer.' WHERE entry=-1000628;
UPDATE script_texts SET content_loc3='<räusper> <räusper> Jungfer in Bedrängnis!' WHERE entry=-1000629;
UPDATE script_texts SET content_loc3='Hallo? Hilfe?' WHERE entry=-1000630;
UPDATE script_texts SET content_loc3='Lasst mich nicht hier allein! Denn wenn Ihr das tut, werde ich Euch finden...' WHERE entry=-1000631;
UPDATE script_texts SET content_loc3='Gut, lasst uns hier verschwinden!' WHERE entry=-1000632;
UPDATE script_texts SET content_loc3='Seid Ihr sicher, dass Ihr bereit seid? Lasst Euch Zeit.' WHERE entry=-1000633;
UPDATE script_texts SET content_loc3='Fein, dann los!' WHERE entry=-1000634;
UPDATE script_texts SET content_loc3='Also sind wir auch verflucht?' WHERE entry=-1000635;
UPDATE script_texts SET content_loc3='%s zeigt auf den Säugling.' WHERE entry=-1000636;
UPDATE script_texts SET content_loc3='%s schluchzt.' WHERE entry=-1000637;
UPDATE script_texts SET content_loc3='Die Götter haben uns verlassen! Wir müssen es beseitigen, bevor Ymiron davon erfährt!' WHERE entry=-1000638;
UPDATE script_texts SET content_loc3='NEIN! Das dürft Ihr nicht! Ich flehe Euch an! Es ist doch unser Kind!' WHERE entry=-1000639;
UPDATE script_texts SET content_loc3='Und was sollen wir stattdessen tun, Weib? Die anderen dürfen nichts davon wissen. Sollten sie von dieser Missgeburt erfahren, werden wir alle hingerichtet werden.' WHERE entry=-1000640;
UPDATE script_texts SET content_loc3='Ich... ich suche ein Versteck. Ich verstecke es, bis ich weit entfernt von hier eine neue Heimat dafür finde...' WHERE entry=-1000641;
UPDATE script_texts SET content_loc3='Es ist ein Weibchen!' WHERE entry=-1000642;
UPDATE script_texts SET content_loc3='Es ist ein wildes Männchen!' WHERE entry=-1000643;
UPDATE script_texts SET content_loc3='Autsch! Jetzt reicht\'s, ich bin raus aus dem Zielgeschäft!' WHERE entry=-1000644;
UPDATE script_texts SET content_loc3='Mein Ohr! Ihr habt mein Ohr getroffen!' WHERE entry=-1000645;
UPDATE script_texts SET content_loc3='Nicht der Schnurrbart! Jetzt bin ich asymmetrisch!' WHERE entry=-1000646;
UPDATE script_texts SET content_loc3='Netter Schuss!' WHERE entry=-1000647;
UPDATE script_texts SET content_loc3='Hört auf rumzuheulen. Ihr habt doch noch immer Glück.' WHERE entry=-1000648;
UPDATE script_texts SET content_loc3='Bah, es ist eine Verbesserung.' WHERE entry=-1000649;
UPDATE script_texts SET content_loc3='Ganz ruhig, Junge, es ist doch nur ein Vogelschuss!' WHERE entry=-1000650;
UPDATE script_texts SET content_loc3='Das Einzige was schmerzt ist dein Stolz, Junge. Bleib munter!' WHERE entry=-1000651;
UPDATE script_texts SET content_loc3='Ich so hungrig! MJAM!' WHERE entry=-1000652;
UPDATE script_texts SET content_loc3='He... mir nicht gehen so gut.' WHERE entry=-1000653;
UPDATE script_texts SET content_loc3='Ihr sein böser Orc... böööser... Or... argh!' WHERE entry=-1000654;
UPDATE script_texts SET content_loc3='Zeit zum Fressen?!' WHERE entry=-1000655;
UPDATE script_texts SET content_loc3='Ich tun Hammel in Magen!' WHERE entry=-1000656;
UPDATE script_texts SET content_loc3='Wir sollten verdammt noch mal von hier verschwinden.' WHERE entry=-1000657;
UPDATE script_texts SET content_loc3='Zuhören, Trottel der Venture Co.! Regel Nr. 1: Sperrt Gefangene niemals in der Nähe von Sprengstoff ein.' WHERE entry=-1000658;
UPDATE script_texts SET content_loc3='Oder DAS erwartet Euch.' WHERE entry=-1000659;
UPDATE script_texts SET content_loc3='Es wird langsam etwas heiß hier. Sollen wir weiter gehen?' WHERE entry=-1000660;
UPDATE script_texts SET content_loc3='Oh, schaut mal, eine weitere Wagenladung Sprengstoff! Helfen wir ihnen, den los zu werden.' WHERE entry=-1000661;
UPDATE script_texts SET content_loc3='Ihr solltet wirklich nicht mit diesem Zeug spielen. Jemand könnte dabei verletzt werden.' WHERE entry=-1000662;
UPDATE script_texts SET content_loc3='Wir haben es geschafft! Habt Dank dafür, dass Ihr mich aus diesem Höllenloch geholt habt. Sagt Hemet Bescheid, dass ich komme!' WHERE entry=-1000663;
UPDATE script_texts SET content_loc3='Die zerstörerische Barriere gewinnt an Kraft.' WHERE entry=-1000664;
UPDATE script_texts SET content_loc3='Die zerstörerische Barriere ist vollständig aufgeladen!' WHERE entry=-1000665;
UPDATE script_texts SET content_loc3='Ich fühle die SCHATTEN in Euren Herzen. Niemals darf das Böse Ruhe finden!' WHERE entry=-1000666;
UPDATE script_texts SET content_loc3='Eure verdorbenen Seelen werden meine Kräfte stärken!' WHERE entry=-1000667;
UPDATE script_texts SET content_loc3='%s überträgt die noch verbleibende Energie auf den nahegelegenen Runenstein, was diesen erfolgreich reaktiviert.' WHERE entry=-1000668;
UPDATE script_texts SET content_loc3='Wir werden die Quelle des Alptraums über die von Euch gesammelten Fragmente ausfindig machen, $N. Von dort aus, werden wir Eranikus durch einen Spalt im Traum in unsere Welt ziehen, Bereitet Euch vor, $C. Wir holen jetzt die Verkörperung des Alptraums in diese Welt.' WHERE entry=-1000669;
UPDATE script_texts SET content_loc3='Nach Nachthafen! Sorgt dafür, dass Eure Armee mit Euch Schritt hält, Held!' WHERE entry=-1000670;
UPDATE script_texts SET content_loc3='Der Spalt wird sich hier über dem Elune\'ara See öffnen. Macht Euch bereit, $N. Eranikus\' Übergang in diese Welt wird von Chaos und Hass gekennzeichnet sein.' WHERE entry=-1000671;
UPDATE script_texts SET content_loc3='Er wird sich von Nichts und Niemanden aufhalten lassen, um Malfurions körperliche Manifestation zu zerstören. Das darf auf keinen Fall geschehen... Wir müssen das Biest solange beschäftigen, bis Tyrande eingetroffen ist.' WHERE entry=-1000672;
UPDATE script_texts SET content_loc3='Verteidigt Nachthafen, Held...' WHERE entry=-1000673;
UPDATE script_texts SET content_loc3='%s hat unsere Welt betreten.' WHERE entry=-1000674;
UPDATE script_texts SET content_loc3='Erbärmliche, berechenbare Sterbliche... Ihr wisst nicht, was Ihr getan habt! Der Wunsch des Meisters hat sich erfüllt. Mondlichtung wird zerstört werden und Malfurion gleich mit!' WHERE entry=-1000675;
UPDATE script_texts SET content_loc3='Dämon! Spürt die Macht von Cenarius!' WHERE entry=-1000676;
UPDATE script_texts SET content_loc3='%s lacht teuflisch.' WHERE entry=-1000677;
UPDATE script_texts SET content_loc3='In der Tat seid ihr nicht Eurer Vater, Insekt. Wenn ich wollte, könnte ich Euch mit einem einzigen Schlag vernichten. Übergebt mir Shan\'do Sturmgrimm und ich werde Euer erbärmliches Leben und das Leben Eurer Männer verschonen.' WHERE entry=-1000678;
UPDATE script_texts SET content_loc3='Wer ist hier der Narr, Untier? Ihr habt doch nicht wirklich geglaubt, dass wir Euch in Malfurions Nähe beschwören würden? Eure Erlösung naht, Eranikus. Ihr werdet entgültig von Eurem Wahnsinn befreit werden.' WHERE entry=-1000679;
UPDATE script_texts SET content_loc3='Meine Erlösung? Ihr habt Mut, kleines Wesen. Nur der Wille Gottes kann mir Erlösung verschaffen.' WHERE entry=-1000680;
UPDATE script_texts SET content_loc3='%s brüllt vor Wut.' WHERE entry=-1000681;
UPDATE script_texts SET content_loc3='Beeilt Euch, $C! Wir müssen Schutz finden!' WHERE entry=-1000682;
UPDATE script_texts SET content_loc3='Bitte oh Held, beschützt Eure Gefolgsleute.' WHERE entry=-1000683;
UPDATE script_texts SET content_loc3='Erhebt Euch, ihr Diener des Alptraums! Zeigt Euch und zerstört diese Welt! Niemand soll überleben...' WHERE entry=-1000684;
UPDATE script_texts SET content_loc3='Wer werden diese Dämonen gemeinsam bekämpfen! Die Verteidiger von Nachthafen stehen uns zur Seite, wenn es sein muss, bis in den Tod. Schnell jetzt! Wir müssen diese Kreaturen in den Alptraum zurücktreiben. Vernichtet sie alle!' WHERE entry=-1000685;
UPDATE script_texts SET content_loc3='Wo ist Euer Retter jetzt? Wie lange könnt Ihr noch gegen meine Angriffe bestehen?' WHERE entry=-1000686;
UPDATE script_texts SET content_loc3='Ihr habt meine Wächter besiegt? Nun werdet Ihr gegen mich bestehen müssen, Sterbliche!' WHERE entry=-1000687;
UPDATE script_texts SET content_loc3='Remulos, seht wie schnell sie durch meine Hand fallen! Ihr könnt all dem ein Ende bereiten. Übergebt mir den Druiden und alles wird vorbei sein...' WHERE entry=-1000688;
UPDATE script_texts SET content_loc3='Elune, erhöre meine Gebete. Verleihe uns Klarheit! Behüte unsere Gefallen...' WHERE entry=-1000689;
UPDATE script_texts SET content_loc3='Kümmert euch um die Verwundeten, Schwestern!' WHERE entry=-1000690;
UPDATE script_texts SET content_loc3='Befreie dich von deinen Sünden, Eranikus. Alles wird vergeben werden...' WHERE entry=-1000691;
UPDATE script_texts SET content_loc3='Ihr werdet Vergebung erfahren. Eranikus. Elune wird Euch immer lieben. Befreit Euch von den Fesseln, die sich Euer bemächtigt haben.' WHERE entry=-1000692;
UPDATE script_texts SET content_loc3='Die Umklammerung der alten Götter ist unlösbar. Er wird von ihren dunklen Gedanken verzehrt... ich... Ich kann nicht... Kann nicht länger durchhalten... Elune steh mir bei...' WHERE entry=-1000693;
UPDATE script_texts SET content_loc3='ES BRENNT! DER SCHMERZ... SENGEND...' WHERE entry=-1000694;
UPDATE script_texts SET content_loc3='Wieso? Wie konnte all das mit mir geschehen? Wo wart Ihr, Tyrande? Wo wart Ihr, als ich vor Elune in Ungnade fiel?' WHERE entry=-1000695;
UPDATE script_texts SET content_loc3='Ich... Ich kann Elunes Berührung erneut auf meiner Seele spüren... Sie lächelt mich an... Ja... ich...' WHERE entry=-1000696;
UPDATE script_texts SET content_loc3='Eranikus, der Tyrann des Traums, ist vollständig von Elunes Licht eingehüllt. Eine nie dagewesene Stille legt sich über Mondlichtung.' WHERE entry=-1000697;
UPDATE script_texts SET content_loc3='%s beugt das Knie.' WHERE entry=-1000698;
UPDATE script_texts SET content_loc3='Gepriesen sei Elune... Eranikus ist erlöst.' WHERE entry=-1000699;
UPDATE script_texts SET content_loc3='So lange war ich verloren... Die Verderbnis des Alptraums hatte sich meiner bemächtigt... Und jetzt habt Ihr... Ihr alle... Ihr alle habt mich erlöst. Mich aus meiner Umklammerung befreit.' WHERE entry=-1000700;
UPDATE script_texts SET content_loc3='Aber... Malfurion, Cenarius, Ysera... Sie kämpfen weiter. Sie brauchen mich. Ich muss mich sofort zurück in den Traum begeben.' WHERE entry=-1000701;
UPDATE script_texts SET content_loc3='Ich bin Eurer Gebete nicht würdig. Ihr seid wahrhaft ein Engel des Lichts. Bitte lasst mich Eure Unterstützung zuteil werden, auf dass ich zum Grabhügel zurückkehren und erneut in den Traum gelangen kann. Auch ich, wie Malfurion, habe eine Liebste, die mich erwartet... ich muss zu ihr zurückkehren... Ich muss sie beschützen...' WHERE entry=-1000702;
UPDATE script_texts SET content_loc3='Und Helden... Ich habe das, wonach es Euch begehrt. Auf dass es erneut das Böse vernichten möge. Remulos, kümmere dich darum, dass $G unser Held : unsere Heldin; den Splitter des grünen Schwarms erhält.' WHERE entry=-1000703;
UPDATE script_texts SET content_loc3='Es wird geschehen, Eranikus. Lebt wohl, oh Weiser!' WHERE entry=-1000704;
UPDATE script_texts SET content_loc3='Lasst uns Nachthafen verlassen, $G Held : Heldin;. Sucht mich am Hain auf.' WHERE entry=-1000705;
UPDATE script_texts SET content_loc3='Eure Welt soll ein unbarmherziges Ende erleiden. Der Alptraum erwartet Euch!' WHERE entry=-1000706;
UPDATE script_texts SET content_loc3='Dieses blaue Licht... Es ist seltsam. Was glaubt Ihr, könnte es bedeuten?' WHERE entry=-1000707;
UPDATE script_texts SET content_loc3='Wir haben es gefunden!' WHERE entry=-1000708;
UPDATE script_texts SET content_loc3='Bitte zündet dies an, während ich kanalisiere.' WHERE entry=-1000709;
UPDATE script_texts SET content_loc3='Hier ist es. Dann lassen wir mal ein Licht aufgehen.' WHERE entry=-1000710;
UPDATE script_texts SET content_loc3='Suchen wir das nächste.' WHERE entry=-1000711;
UPDATE script_texts SET content_loc3='Gut, gehen wir!' WHERE entry=-1000712;
UPDATE script_texts SET content_loc3='Es ist Zeit für den letzten Schritt. Wir müssen den Altar aktivieren.' WHERE entry=-1000713;
UPDATE script_texts SET content_loc3='Ich werde die Wörter vorlesen, die in den Stein gemeißelt sind, und Ihr müsst einen Weg finden, ihn zu entzünden.' WHERE entry=-1000714;
UPDATE script_texts SET content_loc3='Der Altar leuchtet! Wir haben es geschafft!' WHERE entry=-1000715;
UPDATE script_texts SET content_loc3='Was geschieht hier? Seht!' WHERE entry=-1000716;
UPDATE script_texts SET content_loc3='Es ist so viele Jahre her...' WHERE entry=-1000717;
UPDATE script_texts SET content_loc3='Wer wagt es, den Altar der Göttin zu stören?' WHERE entry=-1000718;
UPDATE script_texts SET content_loc3='Bitte, vergebt uns unser Eindringen, Priesterinnen. Wir wollen niemandem Schaden zuzufügen.' WHERE entry=-1000719;
UPDATE script_texts SET content_loc3='Wir wollen lediglich wissen, warum die Wildekin diese Gegend bewachen...' WHERE entry=-1000720;
UPDATE script_texts SET content_loc3='Enu thora\'serador. Dies ist ein hochheiliger Ort.' WHERE entry=-1000721;
UPDATE script_texts SET content_loc3='Wir werden es Euch zeigen...' WHERE entry=-1000722;
UPDATE script_texts SET content_loc3='Seht nach oben; thara dormil dorah...' WHERE entry=-1000723;
UPDATE script_texts SET content_loc3='Dieser Edelstein ermöglichte einst die direkte Kommunikation mit Elune selbst.' WHERE entry=-1000724;
UPDATE script_texts SET content_loc3='Durch diesen Edelstein kanalisierte Elune ihre unendliche Weisheit...' WHERE entry=-1000725;
UPDATE script_texts SET content_loc3='Als wir erkannten, dass der Edelstein beschützt werden musste, wandten wir uns an die Göttin persönlich.' WHERE entry=-1000726;
UPDATE script_texts SET content_loc3='Bald darauf begannen wir, Visionen von einer Kreatur zu haben... Einer Kreatur mit den Federn einer Eule, aber der Willenskraft und der Stärke eines Bären...' WHERE entry=-1000727;
UPDATE script_texts SET content_loc3='An jenem Tag wurden uns die Wildekin gegeben. Die Göttin berief die Wildekin, diese grimmigen Wächter, dazu, all ihre heiligen Orte zu beschützen.' WHERE entry=-1000728;
UPDATE script_texts SET content_loc3='Anu\'dorini Talah, Ru shallora enudoril' WHERE entry=-1000729;
UPDATE script_texts SET content_loc3='Doch nun, viele Jahre später, sind die Wildekin nicht mehr so friedfertig wie früher, und ohne die Führung der Göttin sind sie verwirrt...' WHERE entry=-1000730;
UPDATE script_texts SET content_loc3='Sie wandern herum ohne jedes Ziel, ohne jeden Zweck... Doch viele verschlägt es zurück zu den heiligen Gebieten, von denen sie einst geschworen hatten, sie zu beschützen.' WHERE entry=-1000731;
UPDATE script_texts SET content_loc3='Wildekin sind von Natur aus magisch, diese Macht wurde ihnen von der Göttin verliehen.' WHERE entry=-1000732;
UPDATE script_texts SET content_loc3='Eines müsst Ihr wissen: Ganz egal, wo auf der Welt Ihr auf sie stoßt, sie bewachen etwas Wichtiges, wie es ihnen vor langer Zeit anbefohlen wurde.' WHERE entry=-1000733;
UPDATE script_texts SET content_loc3='Bitte erinnert Euch daran, was wir Euch gezeigt haben...' WHERE entry=-1000734;
UPDATE script_texts SET content_loc3='Lebt wohl.' WHERE entry=-1000735;
UPDATE script_texts SET content_loc3='Danke für Eure Hilfe, $N. Ich wünsche Euch viel Glück bei Euren Abenteuern.' WHERE entry=-1000736;
UPDATE script_texts SET content_loc3='Ich möchte hier bleiben und darüber nachdenken, was wir gesehen haben. Bitte sucht Erelas auf und sagt ihm, was wir erfahren haben.' WHERE entry=-1000737;
UPDATE script_texts SET content_loc3='%s beginnt, einen seltsamen Zauber zu singen...' WHERE entry=-1000738;
UPDATE script_texts SET content_loc3='Und vergesst nicht, ich brauche Eure Hilfe, damit ich richtig kanalisieren kann. Auf unserem Weg werde ich Euch mehrmals bitten, mir zu helfen, also seid bitte bereit.' WHERE entry=-1000739;
UPDATE script_texts SET content_loc3='Wir müssen schnell handeln, oder alles ist verloren!' WHERE entry=-1000740;
UPDATE script_texts SET content_loc3='Meine Streitkräfte können die Verteidigung der Qiraji nicht überwinden. Nun, Drache, wie es aussieht, werden wir nicht nahe genug an sie herankommen, um Eure kostbare Barriere zu platzieren.' WHERE entry=-1000741;
UPDATE script_texts SET content_loc3='Es gibt einen Weg...' WHERE entry=-1000742;
UPDATE script_texts SET content_loc3='%s nickt wissend.' WHERE entry=-1000743;
UPDATE script_texts SET content_loc3='Fandral, denkt immer an diese Worte: Lasst Eurer Schicksal nicht von Eurem Kummer leiten. Diese Gedanken, die Ihr in Euch tragt... sie werden Euch an dunkle Orte führen, Nachtelf. Erlösung erlangt man nicht durch fehlgeleitete Rache.' WHERE entry=-1000744;
UPDATE script_texts SET content_loc3='%s wirft einen flüchtigen Blick auf ihre Verbündeten.' WHERE entry=-1000745;
UPDATE script_texts SET content_loc3='Wir werden sie zurückdrängen, Anachronos. Das schwöre ich. Haltet Ihr Euren Teil der Vereinbarung ein. Lasset Eure Hände nicht zögern, wenn Ihr unser Schicksal hinter der Barriere entscheidet.' WHERE entry=-1000746;
UPDATE script_texts SET content_loc3='Gebt euch dem endlosen Traum hin, Winzlinge. Auf dass er euch verzehren möge!' WHERE entry=-1000747;
UPDATE script_texts SET content_loc3='Anachronos, diese Ableckung sollte Euch und dem jungen Druiden genug Zeit geben, das Portal wieder zu versiegeln. Zögert nicht! Jetzt lasst uns sehen, wie sich unser Feind gegen die Magie des Chaos schlägt!' WHERE entry=-1000748;
UPDATE script_texts SET content_loc3='Lasst sie den Zorn des blauen Drachenschwarms spüren! Möge Malygos mir zur Seite stehen!' WHERE entry=-1000749;
UPDATE script_texts SET content_loc3='Niemals dürft Ihr die am heutigen Tage dargebrachten Opfer vergessen, Nachtelf. Wir alle haben durch diese Untiere endloses Leid erfahren.' WHERE entry=-1000750;
UPDATE script_texts SET content_loc3='Alexstrasza, gewähre mir die Kraft, unsere Feinde zurückzutreiben!' WHERE entry=-1000751;
UPDATE script_texts SET content_loc3='JETZT, HIRSCHHAUPT! WIR MÜSSEN LOS! Bereitet Eure Magie vor!' WHERE entry=-1000752;
UPDATE script_texts SET content_loc3='Es ist getan, Drache. Zeigt mir den Weg.' WHERE entry=-1000753;
UPDATE script_texts SET content_loc3='Bleibt zusammen...' WHERE entry=-1000754;
UPDATE script_texts SET content_loc3='Der Sand der Zeit verharrt für einen kurzen Moment! Ich werde jetzt die Barriere beschwören.' WHERE entry=-1000755;
UPDATE script_texts SET content_loc3='WIRKT DEN ZAUBER, HIRSCHHAUPT! ICH KANN DIE GLYPHEN DES ZAUBERSCHUTZES NICHT LÄNGER HALTEN! BESCHWÖRT DIE WURZELN!' WHERE entry=-1000756;
UPDATE script_texts SET content_loc3='Ihr Uralten, leitet meine Hand... Erwacht aus Eurem tiefen Schlaf! ERWACHT UND VERSIEGELT DIESEN VERFLUCHTEN ORT!' WHERE entry=-1000757;
UPDATE script_texts SET content_loc3='%s fällt völlig erschöpft auf die Knie.' WHERE entry=-1000758;
UPDATE script_texts SET content_loc3='Es... es ist vorbei, Lord Hirschhaupt. Wir sind siegreich. Doch die Kosten für diesen Sieg waren hoch.' WHERE entry=-1000759;
UPDATE script_texts SET content_loc3='Es gilt noch eine letzte Pflicht zu erfüllen...' WHERE entry=-1000760;
UPDATE script_texts SET content_loc3='Bevor ich diesen Ort verlassen werde, gibt es nur noch eines zu klären, Lord Hirschhaupt. Sollte der Tag kommen, an dem Ihr diese verfluchte Festung erneut betreten müsst, benutzt das Szepter der Sandstürme mit dem heiligen Gong. Die Magie, welche diese Barriere aufrecht erhält, wird sich verflüchtigen und die Schrecken Ahn\'Qirajs werden erneut auf die Welt losgelassen werden.' WHERE entry=-1000761;
UPDATE script_texts SET content_loc3='%s übergibt Fandral Hirschhaupt das Szepter der Sandstürme.' WHERE entry=-1000762;
UPDATE script_texts SET content_loc3='Nach all\' dem Leid, das mein Volk mit ansehen und erdulden musste, erwartet Ihr von mir, dass ich eine weitere Bürde auf mich nehme, Drache? Ihr müsst wahrlich verrückt sein.' WHERE entry=-1000763;
UPDATE script_texts SET content_loc3='Ich will nichts mit Silithus, den Qiraji oder irgendwelchen verdammten Drachen zu tun haben!' WHERE entry=-1000764;
UPDATE script_texts SET content_loc3='%s schleudert das Szepter der Sandstürme in die Barriere, welche sofort zusammenbricht.' WHERE entry=-1000765;
UPDATE script_texts SET content_loc3='Lord Hirschhaupt, wohin wollt Ihr gehen? Ihr würdet unser Bündnis Eurem Stolz opfern?' WHERE entry=-1000766;
UPDATE script_texts SET content_loc3='Durch diesen bitteren Sieg wird die Seele meines Sohnes keinen Frieden finden, Drache. Ich werde ihn zurückbekommen. Und wenn es tausend Jahre dauert, ich WERDE meinen Sohn zurückbekommen!' WHERE entry=-1000767;
UPDATE script_texts SET content_loc3='%s schüttelt voller Enttäuschung seinen Kopf.' WHERE entry=-1000768;
UPDATE script_texts SET content_loc3='%s kniet sich hin, um die Fragmente des zerschlagenen Szepters aufzusammeln.' WHERE entry=-1000769;
UPDATE script_texts SET content_loc3='Und nun wisst Ihr alles, was es zu wissen gibt, $G Sterblicher:Sterbliche;...' WHERE entry=-1000770;
-- UPDATE script_texts SET content_loc3='' WHERE entry=-1000771;
UPDATE script_texts SET content_loc3='Sieht aus, als hätten wir Schwierigkeiten. Ein bisschen mehr Schwung bitte, da kommen sie!' WHERE entry=-1000772;
UPDATE script_texts SET content_loc3='Auftragsmörder von diesem Kult, den Ihr gefunden habt... Wir sollten uns in Bewegung setzen, bevor uns hier noch jemand anders aufspürt.' WHERE entry=-1000773;
UPDATE script_texts SET content_loc3='Halt! Ich spüre etwas Böses... Untote!' WHERE entry=-1000774;
UPDATE script_texts SET content_loc3='Ein Paladin! Ihn zu töten, würde den Meister erfreuen. Angriff!' WHERE entry=-1000775;
UPDATE script_texts SET content_loc3='Sie kommen heute ans Licht. Lasst uns weitergehen, sonst finden wir vielleicht noch mehr Dinge, die meinen Tod wollen.' WHERE entry=-1000776;
UPDATE script_texts SET content_loc3='Die drei schon wieder?' WHERE entry=-1000777;
UPDATE script_texts SET content_loc3='Ohne den Läuterer seid Ihr nicht ganz so selbstsicher, wie?' WHERE entry=-1000778;
UPDATE script_texts SET content_loc3='Dieses Mal mache ich Euch endgültig fertig!' WHERE entry=-1000779;
UPDATE script_texts SET content_loc3='Gut gemacht! Jetzt müsste ich allein zurechtkommen. Denkt daran und sprecht mit Delgren, wenn Ihr wieder in Maestras Posten in Eschental seid.' WHERE entry=-1000780;
UPDATE script_texts SET content_loc3='Ich wusste, Lurielle würde Hilfe schicken! Habt Dank, $G mein Freund:meine Freundin;, und richtet auch Lurielle meinen Dank aus!' WHERE entry=-1000781;
UPDATE script_texts SET content_loc3='Wo bin ich? Was ist passiert? Habt Ihr... mich befreit?' WHERE entry=-1000782;
UPDATE script_texts SET content_loc3='Habt Dank. Ich glaubte schon, ich müsste sterben, ohne meine Schwestern noch einmal zu sehen!' WHERE entry=-1000783;
UPDATE script_texts SET content_loc3='Danke, $N. Und jetzt verschwinden wir besser von hier!' WHERE entry=-1000784;
UPDATE script_texts SET content_loc3='Wir haben es geschafft! Nochmals danke! Ich laufe voraus.' WHERE entry=-1000785;
UPDATE script_texts SET content_loc3='He, Hornizz! Ich bin wieder da! Und bei mir sind ein paar Leute, die mir mal aus der Patsche geholfen haben.' WHERE entry=-1000786;
UPDATE script_texts SET content_loc3='Wir müssen die Maraudines wohl von unserer Liste streichen. Eine Zusammenarbeit mit denen ist einfach zu problematisch...' WHERE entry=-1000787;
UPDATE script_texts SET content_loc3='Also, ich geh jetzt mal zu den Gelkis. Die sind nicht so dämlich wie die Maraudines und man kann besser mit ihnen reden.' WHERE entry=-1000788;
UPDATE script_texts SET content_loc3='Nun, jetzt oder nie, nehme ich an. Vergesst nicht, sobald wir die Straße sicher erreicht haben, zu Terenthis zurückzukehren und ihn wissen lassen, das wir entkommen sind.' WHERE entry=-1000789;
UPDATE script_texts SET content_loc3='Wir haben es geschafft, mein Freund. Vergesst nicht zu Terenthis zu gehen und ihn wissen zu lassen, das wir in Sicherheit sind. Vielen Dank nochmal.' WHERE entry=-1000790;
UPDATE script_texts SET content_loc3='Da kommen sie.' WHERE entry=-1000791;
UPDATE script_texts SET content_loc3='Wir können diese verderbten Kreaturen besiegen.' WHERE entry=-1000792;
UPDATE script_texts SET content_loc3='Wir verdienen zumindest unseren Tod!' WHERE entry=-1000793;
UPDATE script_texts SET content_loc3='Gebt nicht auf! Kämpft, bis zum Tod!' WHERE entry=-1000794;
UPDATE script_texts SET content_loc3='Au! In Ordnung, ich gehe zurück an die Arbeit, $N!' WHERE entry=-1000795;
-- UPDATE script_texts SET content_loc3='' WHERE entry=-1000796; -- ungenutzt
UPDATE script_texts SET content_loc3='%s krächzt und macht sich auf in Richtung Shalasversteck. Beeilt Euch, zu folgen!' WHERE entry=-1000797;
UPDATE script_texts SET content_loc3='%s wartet kurz vor dem Baum und begibt sich dann hinein.' WHERE entry=-1000798;
UPDATE script_texts SET content_loc3='%s scheint nach etwas zu suchen, und möchte, dass Ihr folgt.' WHERE entry=-1000799;
UPDATE script_texts SET content_loc3='%s fliegt nach unten zur Plattform! Ihr solltet springen, wenn Ihr den Anschluss nicht verlieren wollt. Beeilt Euch!' WHERE entry=-1000800;
UPDATE script_texts SET content_loc3='%s krächzt laut!' WHERE entry=-1000801;
UPDATE script_texts SET content_loc3='Endlich wurde ich von diesem schrecklichen Fluch befreit! Vielen Dank! Bitte berichtet Rilak dem Erlösten, dass es mir gut geht. Meine Mission führt nach Skettis. Terokk muss besiegt werden!' WHERE entry=-1000802;
UPDATE script_texts SET content_loc3='Ihr kämpft nicht allein, $N! Zusammen werden wir diese Ausgeburt des Höllenfeuers verbannen!' WHERE entry=-1000803;
UPDATE script_texts SET content_loc3='Wir werden kämpfen, wenn Ihr bereit seid.' WHERE entry=-1000804;
UPDATE script_texts SET content_loc3='Wir werden die Elemente mit Gewalt aus Eurem Griff befreien!' WHERE entry=-1000805;
UPDATE script_texts SET content_loc3='Was sagen die Elemente, Torlok? Alles, was ich höre, ist Stille.' WHERE entry=-1000806;
UPDATE script_texts SET content_loc3='Ich höre was Ihr hört, Bruder. Schaut hinter Euch...' WHERE entry=-1000807;
UPDATE script_texts SET content_loc3='Sie sind erlöst! Haben wir gewonnen?' WHERE entry=-1000808;
UPDATE script_texts SET content_loc3='Alles ist nun, wie es sein sollte, Schamane. Gute Arbeit.' WHERE entry=-1000809;
UPDATE script_texts SET content_loc3='Ja... Gut für die Elemente hier, aber die Litanei ist jemandem anderem bekannt... Die Geister sind in Aufruhr... Wenn diese Macht nicht aufgehalten wird, ist die Welt der Sterblichen bedroht.' WHERE entry=-1000810;
UPDATE script_texts SET content_loc3='Lebt wohl, Sterbliche... Der Erdheiler kennt das Gefühl des Feuers...' WHERE entry=-1000811;
UPDATE script_texts SET content_loc3='Wir werden jetzt gehen, Torlok. Ich habe nur eine Bitte...' WHERE entry=-1000812;
UPDATE script_texts SET content_loc3='Die Männer des Herzeleid geben Erdheiler Torlok ihre Waffen.' WHERE entry=-1000813;
UPDATE script_texts SET content_loc3='Gebt sie denen, die dies möglich gemacht haben.' WHERE entry=-1000814;
UPDATE script_texts SET content_loc3='Seid geheilt, $G Priester:Priesterin;.' WHERE entry=-1000815;
UPDATE script_texts SET content_loc3='Wir sind gerettet! Die Arbeiter konnten der Geißel entkommen!' WHERE entry=-1000816;
UPDATE script_texts SET content_loc3='Wieder einmal habe ich versagt...' WHERE entry=-1000817;
UPDATE script_texts SET content_loc3='Ich kehre nun dorthin zurück, woher ich kam, nur um hier ein weiteres Mal die gleiche dramatische Tragödie zu durchleben.' WHERE entry=-1000818;
UPDATE script_texts SET content_loc3='Die Geißel ist da! Rennt! Rennt um euer Leben!' WHERE entry=-1000819;
UPDATE script_texts SET content_loc3='Bitte helft uns! Der Prinz ist wahnsinnig geworden!' WHERE entry=-1000820;
UPDATE script_texts SET content_loc3='Sucht Schutz in Herdweiler! Das ist unsere einzige Hoffnung!' WHERE entry=-1000821;
UPDATE script_texts SET content_loc3='Man hat Bescheid gegeben. Er sollte in Kürze hier sein.' WHERE entry=-1000822;
UPDATE script_texts SET content_loc3='Jaaaaa!' WHERE entry=-1000823;
UPDATE script_texts SET content_loc3='Ich wusste, Ihr würdet kommen, $N. Schön, Euch wiederzusehen.' WHERE entry=-1000824;
UPDATE script_texts SET content_loc3='Seid vorsichtig. Die Großdrachin wird nicht kampflos aufgeben.' WHERE entry=-1000825;
UPDATE script_texts SET content_loc3='Wie es vor langer Zeit in Karazhan vorherbestimmt wurde, Monster - ich komme - und ich bringe Gerechtigkeit.' WHERE entry=-1000826;
UPDATE script_texts SET content_loc3='Ergreift ihn! Ergreift den wertlosen Verbrecher und seine Verbündeten!' WHERE entry=-1000827;
UPDATE script_texts SET content_loc3='Reginald, Ihr wisst, dass ich Euch nicht vorbeilassen kann.' WHERE entry=-1000828;
UPDATE script_texts SET content_loc3='Ihr müsst das tun, was Ihr für richtig haltet, Marcus. Wir haben zusammen unter Turalyon gedient. Er hat uns beide zu den Männern gemacht, die wir heute sind. Hat er sich bei mir geirrt? Glaubt Ihr wirklich, dass ich unserer Allianz schaden würde? Dass ich unsere Helden beschämen würde?' WHERE entry=-1000829;
UPDATE script_texts SET content_loc3='Mich hier festzuhalten, ist nicht die richtige Entscheidung, Marcus.' WHERE entry=-1000830;
UPDATE script_texts SET content_loc3='%s scheint in Gedanken versunken zu sein.' WHERE entry=-1000831;
UPDATE script_texts SET content_loc3='Ich bin beschämt. Ich weiß nicht mehr, was ich tue. Nicht Ihr bringt Schande über die Helden der Legende, sondern ich. Ich und der Rest dieser verdorbenen Politiker. Sie füllen unser Leben mit leeren Versprechungen, nicht endenden Lügen.' WHERE entry=-1000832;
UPDATE script_texts SET content_loc3='Wir beschämen unsere Vorfahren. Wir beschämen die, die wir verloren haben... vergebt mir, Reginald.' WHERE entry=-1000833;
UPDATE script_texts SET content_loc3='Ihr ehrt sie mit Eurer aufmerksamen Wache. Ihr seid standhaft in Eurer Loyalität. Ich bezweifle keinen Moment, dass Ihr nicht ein genau so großes Opfer für Eure Leute bringen würdet, wie die Helden, denen Ihr untersteht.' WHERE entry=-1000834;
UPDATE script_texts SET content_loc3='Jetzt ist es an der Zeit, ihrer Herrschaft ein Ende zu bereiten, Marcus. Tretet zurück, alter Freund.' WHERE entry=-1000835;
UPDATE script_texts SET content_loc3='Zurück! Könnt Ihr nicht sehen, dass Helden unter uns weilen?' WHERE entry=-1000836;
UPDATE script_texts SET content_loc3='Zur Seite! Lasst sie durch!' WHERE entry=-1000837;
UPDATE script_texts SET content_loc3='Reginald Windsor darf nichts geschehen! Er soll unberührt passieren!' WHERE entry=-1000838;
UPDATE script_texts SET content_loc3='Geht, Reginald. Möge das Licht Eure Hand leiten.' WHERE entry=-1000839;
UPDATE script_texts SET content_loc3='Ich danke Euch. Ihr habt das Richtige getan.' WHERE entry=-1000840;
UPDATE script_texts SET content_loc3='Folgt mir, Freunde. Zur Burg Stormwind!' WHERE entry=-1000841;
UPDATE script_texts SET content_loc3='Das Licht sei mit Euch.' WHERE entry=-1000842;
UPDATE script_texts SET content_loc3='Wir sind nur Staub unter Euren Füßen.' WHERE entry=-1000843;
UPDATE script_texts SET content_loc3='... Nerven aus Thorium.' WHERE entry=-1000844;
UPDATE script_texts SET content_loc3='Macht Platz!' WHERE entry=-1000845;
UPDATE script_texts SET content_loc3='Eine lebende Legende...' WHERE entry=-1000846;
UPDATE script_texts SET content_loc3='Ein Moment, an den ich mich auf ewig erinnern werde.' WHERE entry=-1000847;
UPDATE script_texts SET content_loc3='Ihr seid eine Inspiration für uns alle.' WHERE entry=-1000848;
UPDATE script_texts SET content_loc3='Seid mutig, Freunde. Das Reptil wird sich sehr wehren. Es ist ein Akt der Verzweiflung. Wenn Ihr bereit seid, gebt mir Bescheid.' WHERE entry=-1000849;
UPDATE script_texts SET content_loc3='Vorwärts!' WHERE entry=-1000850;
UPDATE script_texts SET content_loc3='Majestät, flieht, solange Ihr noch könnt. Sie ist nicht, wofür Ihr sie haltet...' WHERE entry=-1000851;
UPDATE script_texts SET content_loc3='Zur sicheren Halle, Eure Majestät.' WHERE entry=-1000852;
UPDATE script_texts SET content_loc3='Die Maskerade ist vorüber, Lady Prestor. Oder soll ich Euch mit Eurem richtigen Namen ansprechen... Onyxia...' WHERE entry=-1000853;
UPDATE script_texts SET content_loc3='%s lacht.' WHERE entry=-1000854;
UPDATE script_texts SET content_loc3='Ihr werdet eingekerkert und man wird Euch wegen Verrats den Prozess machen, Windsor. Ich werde freudig zusehen, wie sie Euch für schuldig befinden und Euch zum Tode durch Hängen verurteilen...' WHERE entry=-1000855;
UPDATE script_texts SET content_loc3='Und wenn Euer schlaffer Körper am Galgen baumelt, werde ich mich an der Gewissheit erfreuen, dass ein Verrückter hingerichtet wurde. Welchen Beweis habt Ihr denn schon? Habt Ihr gedacht, Ihr könntet hierher kommen, mit dem Finger auf königliche Personen zeigen und ungeschoren davonkommen?' WHERE entry=-1000856;
UPDATE script_texts SET content_loc3='Ihr werdet Eurem Schicksal nicht entkommen, Onyxia. Es wurde vorhergesagt - eine Vision, die in den großen Hallen von Karazhan widerhallt. Das alles endet nun...' WHERE entry=-1000857;
UPDATE script_texts SET content_loc3='%s greift in seine Tasche und zieht die verschlüsselten Schrifttafeln heraus.' WHERE entry=-1000858;
UPDATE script_texts SET content_loc3='Die Dunkeleisenzwerge dachten, diese Schrifttafeln wären verschlüsselt. Das hier ist keine Form der Verschlüsselung, es ist die Sprache der uralten Großdrachen.' WHERE entry=-1000859;
UPDATE script_texts SET content_loc3='Hört zu, Drache. Lasst die Wahrheit durch diese Hallen tönen.' WHERE entry=-1000860;
UPDATE script_texts SET content_loc3='%s liest die Schrifttafeln vor. Unbekannte, ungehörte Klänge fließen durch Euer Bewusstsein.' WHERE entry=-1000861;
UPDATE script_texts SET content_loc3='%s schnappt nach Luft.' WHERE entry=-1000862;
UPDATE script_texts SET content_loc3='Seltsam... Windsor, habt Ihr in dieser Vision überlebt? Ich frage nur deshalb, weil ich für Euren Tod sorgen will und werde. Hier und jetzt.' WHERE entry=-1000863;
UPDATE script_texts SET content_loc3='Drachenabschaum! Wachen! Wachen! Ergreift dieses Monster!' WHERE entry=-1000864;
UPDATE script_texts SET content_loc3='Ja... Wachen, kommt Eurem Lord zu Hilfe!' WHERE entry=-1000865;
UPDATE script_texts SET content_loc3='LASST SIE NICHT ENTKOMMEN!' WHERE entry=-1000866;
UPDATE script_texts SET content_loc3='War das hier vorherbestimmt, Windsor? Wenn Ihr gekommen seid, um zu sterben, dann wurde die Prophezeiung erfüllt. Möge Euer Bewusstsein im wirbelnden Nether verrotten. Erledigt den Rest dieser lästigen Insekten, meine Kinder. Bolvar, Ihr wart ein nettes Spielzeug.' WHERE entry=-1000867;
UPDATE script_texts SET content_loc3='Ihr habt versssagt, sssterbliche Wesssssssen... Lebt wohl!' WHERE entry=-1000868;
UPDATE script_texts SET content_loc3='Reginald... Ich... Es tut mir Leid.' WHERE entry=-1000869;
UPDATE script_texts SET content_loc3='Bol... Bolvar... das Medaillon... benutzen...' WHERE entry=-1000870;
UPDATE script_texts SET content_loc3='%s stirbt.' WHERE entry=-1000871;
UPDATE script_texts SET content_loc3='%s faucht.' WHERE entry=-1000872;
UPDATE script_texts SET content_loc3='Ich kenne den Weg, Insekt. Es gibt keinen Grund mich rumzustoßen wie Vieh.' WHERE entry=-1000873;
UPDATE script_texts SET content_loc3='Ihr glaubt doch sicherlich nicht das Ihr mit diesem Einfall davon kommt. Sie werden wegen mir herkommen und Ihr werdet bezahlen für diese Frechheit.' WHERE entry=-1000874;
UPDATE script_texts SET content_loc3='RENNT SIE UM BRÜDER!' WHERE entry=-1000875;
UPDATE script_texts SET content_loc3='Ich bezweifle das Ihr, wenn Ihr das nächste Mal auf meine Brüder trefft, soviel Glück haben werdet.' WHERE entry=-1000876;
UPDATE script_texts SET content_loc3='Sie kommen wegen Euch, Narr!' WHERE entry=-1000877;
UPDATE script_texts SET content_loc3='Was glaubt Ihr damit zu erreichen, Narr? Genau in diesem Moment trifft die Armee von Schwarzfels Vorbereitungen um Eure Welt zu zerstören.' WHERE entry=-1000878;
UPDATE script_texts SET content_loc3='Auf dunklen Schwingen kommen sie geflogen. Bereitet Euch darauf vor Euer Ende zu sehen!' WHERE entry=-1000879;
UPDATE script_texts SET content_loc3='Das schlimmste kommt noch!' WHERE entry=-1000880;
UPDATE script_texts SET content_loc3='%s lacht.' WHERE entry=-1000881;
UPDATE script_texts SET content_loc3='Zeit um Euch ein Ende zu bereiten, Insekt.' WHERE entry=-1000882;
UPDATE script_texts SET content_loc3='Kniet, Grark!' WHERE entry=-1000883;
UPDATE script_texts SET content_loc3='Grark Lorkrub, ihr wurdet bezichtigt und schuldig befunden des Hochverrats gegen die Horde. Eure Einwände sind unwichtig. Oberhenker Nuzrak, tretet vor.' WHERE entry=-1000884;
UPDATE script_texts SET content_loc3='%s hebt seine massive Axt über Grark.' WHERE entry=-1000885;
UPDATE script_texts SET content_loc3='%s erhebt seine Hand und senkt sie wieder.' WHERE entry=-1000886;
UPDATE script_texts SET content_loc3='Beendet es...' WHERE entry=-1000887;
UPDATE script_texts SET content_loc3='Ihr, Soldat, erstattet sofort in Kargath Bericht!' WHERE entry=-1000888;
UPDATE script_texts SET content_loc3='%s unterwirft sich.' WHERE entry=-1000889;
UPDATE script_texts SET content_loc3='Seid Ihr hier um zu spielen? Dann lasst uns spielen!' WHERE entry=-1000890;
UPDATE script_texts SET content_loc3='Los geht\'s... Gebt mir einfach Deckung und ich erledige den Rest.' WHERE entry=-1000891;
UPDATE script_texts SET content_loc3='Ich werde angegriffen! Ich wiederhole, ich werde angegriffen!' WHERE entry=-1000892;
UPDATE script_texts SET content_loc3='Ich muss mir einen neuen Job suchen.' WHERE entry=-1000893;
UPDATE script_texts SET content_loc3='Bei der zweiten Sonnen K\'areshs, schaut Euch diesen Ort an! Ich kann mir nur vorstellen, was Salhadaar plant. Kommt, lasst uns weitergehen.' WHERE entry=-1000894;
UPDATE script_texts SET content_loc3='Bei all dem Leerenabschaum und dem Abfall kann ein giftiger Leerenschrecken nicht allzu weit sein.' WHERE entry=-1000895;
UPDATE script_texts SET content_loc3='Schaut her, Fleischling! Salhadaars Energieleitung! Er wird gut versorgt...' WHERE entry=-1000896;
UPDATE script_texts SET content_loc3='In Ordnung, schützt mich, während ich diesen Störer anbringe. Es sollte nicht allzu lange dauern.' WHERE entry=-1000897;
UPDATE script_texts SET content_loc3='Schützt die Energiezufuhr! Haltet die Eindringlinge auf!' WHERE entry=-1000898;
UPDATE script_texts SET content_loc3='Fertig! Zurück! Zurück!' WHERE entry=-1000899;
UPDATE script_texts SET content_loc3='Sieht aus, als wäre meine Arbeit hier beendet. Meldet Euch bei dem Bild von Ameer beim Transponder.' WHERE entry=-1000900;
UPDATE script_texts SET content_loc3='Danke, Freund. Werdet Ihr mir hier raushelfen?' WHERE entry=-1000901;
UPDATE script_texts SET content_loc3='Wir sind nicht allzu weit vom Wachposten des Protektorats entfernt, $N. Hier entlang!' WHERE entry=-1000902;
UPDATE script_texts SET content_loc3='Kommandant! Der Fleischling hat mich gerettet!' WHERE entry=-1000903;
UPDATE script_texts SET content_loc3='%s grüßt Kommandant Ameer.' WHERE entry=-1000904;
UPDATE script_texts SET content_loc3='Ok, lasst uns gehen!!' WHERE entry=-1000905;
UPDATE script_texts SET content_loc3='Den Rest des Weges schaff ich alleine, $N. DANKE!' WHERE entry=-1000906;
UPDATE script_texts SET content_loc3='%s schnüffelt. Es ist eine Knolle in der Nähe!' WHERE entry=-1000907;
UPDATE script_texts SET content_loc3='%s beginnt zu graben.' WHERE entry=-1000908;
UPDATE script_texts SET content_loc3='%s quietscht vor Freude über seine Entdeckung.' WHERE entry=-1000909;
UPDATE script_texts SET content_loc3='Können wir los, mein Freund?' WHERE entry=-1000910;
UPDATE script_texts SET content_loc3='Dieses Gebiet ist bekanntermaßen voll mit bösartiger Geißel. Ihr solltet Euch einen Moment nehmen, um Euch auf eine mögliche Verteidigung vorzubereiten.' WHERE entry=-1000911;
UPDATE script_texts SET content_loc3='Sehr gut, lasst uns weiter.' WHERE entry=-1000912;
UPDATE script_texts SET content_loc3='Vorsicht! Wir werden angegriffen!' WHERE entry=-1000913;
UPDATE script_texts SET content_loc3='Es muss die Reinheit vom Mal des Lichtbringers sein, welches die Geißel zu uns zieht. Wir müssen vorsichtig vorgehen, damit wir nicht überwältigt werden!' WHERE entry=-1000914;
UPDATE script_texts SET content_loc3='Wir werden wieder bedrängt! Verteidigt Euch!' WHERE entry=-1000915;
UPDATE script_texts SET content_loc3='Das Land benötigt wahrlich Reinigung durch das Licht! Lasst uns weiter zum Grabmal. Es ist jetzt nicht mehr weit.' WHERE entry=-1000916;
UPDATE script_texts SET content_loc3='Seid willkommen, Freunde!' WHERE entry=-1000917;
UPDATE script_texts SET content_loc3='Danke das Ihr in Erinnerung an mich gekommen seid. Eure Anstrengungen das Symbol wiederzubeschaffen, auch wenn es nicht notwendig war, rühren sichtlich das Herz eines alten Mannes.' WHERE entry=-1000918;
UPDATE script_texts SET content_loc3='Bitte, erhebt Euch mein Freund. Erhaltet den Segen als Symbol der Stärke des Lichts und sowie Helden längst vergangener Macht als Inspiration in jedem von uns einmal wieder auferstehen werden.' WHERE entry=-1000919;
UPDATE script_texts SET content_loc3='%s wendet sich Euch zu.' WHERE entry=-1000920;
UPDATE script_texts SET content_loc3='Schamanismus hat Euch hergeführt... Der Gestank durchdringt die Luft. <Der Lichkönig lacht.> Einst war ich ein Schamane...' WHERE entry=-1000921;
UPDATE script_texts SET content_loc3='Dürfen wir es für Euch vorbereiten, mein Fürst?' WHERE entry=-1000922;
UPDATE script_texts SET content_loc3='Nein, Diener. Dies hier ist noch nicht bereit.' WHERE entry=-1000923;
UPDATE script_texts SET content_loc3='Fühlt Ihr es, sterbliches Wesen? Der Tod durchfließt mich und umschließt, was ich berühre. Ein simples Fingerschnippen und Eure Seele verfällt auf ewig der Verdammnis.' WHERE entry=-1000924;
UPDATE script_texts SET content_loc3='Doch... noch seid Ihr nicht bereit, dem Lichkönig zu dienen. Ja, Euch erwartet ein größeres Schicksal. Mehr Macht... Ihr müsst mächtiger werden, bevor Ihr mir dient.' WHERE entry=-1000925;
UPDATE script_texts SET content_loc3='Schaut zu, Val\'kyr. Seht, wie ich Druck anwende. Seht Ihr, dass es noch nicht reif ist? Schaut, wie es platzt und leblos zu Boden fällt.' WHERE entry=-1000926;
UPDATE script_texts SET content_loc3='Hartnäckig oder dumm? Es spielt keine Rolle. Lasst Euch dies eine Lehre sein, sterbliches Wesen!' WHERE entry=-1000927;
UPDATE script_texts SET content_loc3='%s bittet mit einer Handbewegung um Ruhe.' WHERE entry=-1000928;
UPDATE script_texts SET content_loc3='Vrykul, Euer König beschwört Euch - hört zu!' WHERE entry=-1000929;
UPDATE script_texts SET content_loc3='Die Götter haben uns im Stich gelassen!' WHERE entry=-1000930;
UPDATE script_texts SET content_loc3='Der Menge stockt vor Schreck der Atem.' WHERE entry=-1000931;
UPDATE script_texts SET content_loc3='Selbst jetzt, in unserer dunkelsten Stunde, spotten sie über uns!' WHERE entry=-1000932;
UPDATE script_texts SET content_loc3='Wo sind die Titanen, wenn man sie am dringendsten braucht? Unsere Frauen gebären Missgeburten, verunstaltete Kümmerlinge, die nicht einmal alleine stehen können! Schwach und hässlich... Zu nichts zu gebrauchen...' WHERE entry=-1000933;
UPDATE script_texts SET content_loc3='Ymiron hat hart gearbeitet. Lange saß ich auf meinem Thron und habe über unsere missliche Lage gegrübelt. Es gibt nur eine Antwort... einen Grund...' WHERE entry=-1000934;
UPDATE script_texts SET content_loc3='Denn wer sonst außer den Titanen selbst könnte solch einen Fluch wirken? Was könnte über solch eine Macht verfügen?' WHERE entry=-1000935;
UPDATE script_texts SET content_loc3='Und die Antwort ist: Nichts... Denn es sind die Titanen, die uns verflucht haben!' WHERE entry=-1000936;
UPDATE script_texts SET content_loc3='Die Menge schreit auf.' WHERE entry=-1000937;
UPDATE script_texts SET content_loc3='An diesem Tage werden alle Vrykul ihren alten Glauben ablegen! Wir denunzieren unsere alten Götter! Alle Vrykul werden Ymiron ihre Treue geloben! Ymiron wird unser edles Volk beschützen!' WHERE entry=-1000938;
UPDATE script_texts SET content_loc3='Die Menge jubelt.' WHERE entry=-1000939;
UPDATE script_texts SET content_loc3='Und nun meine erste Anordnung für die Vrykul! Alle missgebildeten Neugeborenen sind nach der Geburt sofort zu vernichten! Unser Blut muss für immer rein bleiben! Diejenigen, die Ymirons Anordnung missachten, werden nach Gjalerbron gebracht und exekutiert!' WHERE entry=-1000940;
UPDATE script_texts SET content_loc3='Vrykul müssen rein bleiben!' WHERE entry=-1000941;
UPDATE script_texts SET content_loc3='Gewährt den Missgeburten keine Gnade, Ymiron!' WHERE entry=-1000942;
UPDATE script_texts SET content_loc3='Gewährt ihnen Gnade, Mein König! Sie sind von unserem Fleisch und Blut!' WHERE entry=-1000943;
UPDATE script_texts SET content_loc3='Sie schwächen uns! Unsere Stärke wird durch ihre bloße Existenz verwässert! Zerstört sie alle!' WHERE entry=-1000944;
UPDATE script_texts SET content_loc3='Grüßt unseren glorreichen König Ymiron!' WHERE entry=-1000945;
UPDATE script_texts SET content_loc3='Der König wird sprechen!' WHERE entry=-1000946;
UPDATE script_texts SET content_loc3='Lasst ihn sprechen! Seid still!' WHERE entry=-1000947;
UPDATE script_texts SET content_loc3='Gut, dann lasst uns beginnen. Je länger wir hier sind, desto mehr Schaden können die Untoten im Hügelland anrichten.' WHERE entry=-1000948;
UPDATE script_texts SET content_loc3='Gut, hier müssen wir wirklich achtsam sein. Haltet Euch bereit!' WHERE entry=-1000949;
