UPDATE script_texts SET content_loc3='Zu lange habt Ihr mich in dieser Zelle festgehalten, Ihr Ungeheuer!' WHERE entry=-1001157;
UPDATE script_texts SET content_loc3='Nein! Mein Freund... was ist geschehen? Das ist alles meine Schuld...' WHERE entry=-1001158;
UPDATE script_texts SET content_loc3='Bitte helft mir, diesen verfluchten Wald zu durchqueren, $R.' WHERE entry=-1001159;
UPDATE script_texts SET content_loc3='Diese Kreatur leidet an den Folgen der teuflischen Verderbnis... Wir müssen ihrem Leid ein Ende machen.' WHERE entry=-1001160;
UPDATE script_texts SET content_loc3='Die teuflische Verderbnis hat keine der Kreaturen im Teufelswald unberührt gelassen, $N. Bitte seht Euch vor.' WHERE entry=-1001161;
UPDATE script_texts SET content_loc3='Ich spüre die Besudelung der Verderbnis daran, $N. Helft mir, es zu zerstören!' WHERE entry=-1001162;
UPDATE script_texts SET content_loc3='Dass ich gegen meine eigene Art kämpfen muss, betrübt mich zutiefst.' WHERE entry=-1001163;
UPDATE script_texts SET content_loc3='Jetzt sehe ich es, $N. Eschental liegt direkt voraus!' WHERE entry=-1001164;
UPDATE script_texts SET content_loc3='Ich fühle... etwas Seltsames...' WHERE entry=-1001165;
UPDATE script_texts SET content_loc3='$N, meine Gestalt hat sich verändert! Die wahre Kraft meines Geistes kehrt zu mir zurück... die faulige Macht des Waldes schwindet.' WHERE entry=-1001166;
UPDATE script_texts SET content_loc3='Danke, $N. Jetzt wird mein Geist endlich Frieden finden.' WHERE entry=-1001167;
UPDATE script_texts SET content_loc3='Die Naga quälen die Wassergeister. Sie beschwören Chaos und Zerstörung herauf!' WHERE entry=-1001168;
UPDATE script_texts SET content_loc3='Die Naga respektieren die Natur nicht. Sie verdrehen und verderben sie, damit sie ihren Zwecken dient. Sie bringen die Geister auf.' WHERE entry=-1001169;
UPDATE script_texts SET content_loc3='Zeit ist nur für Sterbliche von Bedeutung, Insekt. Dimensius ist zeitlos!' WHERE entry=-1001170;
UPDATE script_texts SET content_loc3='Ich hungere! Ich brauche die Kraft dieser Schmiede als Nahrung. Gebt sie mir, meine Kinder!' WHERE entry=-1001171;
UPDATE script_texts SET content_loc3='Verschont mein Leben! Ich werde Euch alles über Arelions Geheimnis erzählen.' WHERE entry=-1001172;
UPDATE script_texts SET content_loc3='Bist du bereit, Mr. Floppy? Bleib in meiner Nähe und nimm dich vor den Wölfen in Acht!' WHERE entry=-1001173;
UPDATE script_texts SET content_loc3='Äh... ich glaube, einer der Wölfe ist zurück...' WHERE entry=-1001174;
UPDATE script_texts SET content_loc3='Er will Mr. Floppy!' WHERE entry=-1001175;
UPDATE script_texts SET content_loc3='Ein großer Widerling vergreift sich an Mr. Floppy! Hilfe!' WHERE entry=-1001176;
UPDATE script_texts SET content_loc3='Lasst uns von hier verschwinden, bevor noch mehr Wölfe auftauchen!' WHERE entry=-1001177;
UPDATE script_texts SET content_loc3='Oh nein! Noch ein Wolf, und ein richtig groooßer dazu!' WHERE entry=-1001178;
UPDATE script_texts SET content_loc3='Er will Mr. Floppy fressen! Ihr müsst Mr. Floppy helfen! Ihr müsst einfach!' WHERE entry=-1001179;
