UPDATE script_texts SET content_loc3='Mr. Floppy, es geht dir gut! Danke, dass Ihr Mr. Floppy gerettet habt!' WHERE entry=-1001181;
UPDATE script_texts SET content_loc3='Ich glaube, ich sehe das Lager! Wir sind fast zu Hause, Mr. Floppy! Gehen wir!' WHERE entry=-1001182;
UPDATE script_texts SET content_loc3='Habt Dank, dass Ihr mir geholfen habt, ins Lager zurückzukommen. Geht und sagt Walter, dass ich jetzt in Sicherheit bin!' WHERE entry=-1001183;
UPDATE script_texts SET content_loc3='Wie habt ihr mich gefunden? Durch Landgren?' WHERE entry=-1001184;
UPDATE script_texts SET content_loc3='Ihr dachtet, ich ließe mich von Euch einfach töten?' WHERE entry=-1001185;
UPDATE script_texts SET content_loc3='WAS?! Egal. Auch ohne meine Sphäre werde ich Euch zermalmen! Erblickt meine wahre Identität und erzittert!' WHERE entry=-1001186;
UPDATE script_texts SET content_loc3='Arg! Ich habe zuviel Zeit in dieser schwachen Hülle verbracht.' WHERE entry=-1001187;
UPDATE script_texts SET content_loc3='Kirel narak! Ich bin Mal\'Ganis. ICH BIN UNSTERBLICH!' WHERE entry=-1001188;
UPDATE script_texts SET content_loc3='GENUG! Ich verschwende hier meine Zeit. Ich muss in der Heimatwelt meine Kräfte sammeln.' WHERE entry=-1001189;
UPDATE script_texts SET content_loc3='Ohne meine Streitkräfte werdet Ihr den Lichkönig nie besiegen. Ich werde Rache nehmen... an ihm UND an Euch!' WHERE entry=-1001190;
