UPDATE script_texts SET content_loc3='He, $GHerr:Fräulein; Leibwache, wollt Ihr Euch Euer Geld verdienen oder was?' WHERE entry=-1001192;
UPDATE script_texts SET content_loc3='Ihr seid gefeuert! <Hust... Cork räuspert sich.> Ähm, ich meine natürlich: Hilfe!' WHERE entry=-1001193;
