UPDATE script_texts SET content_loc3='Wow! Wir haben es geschafft... Keine Ahnung, warum wir dachten, wir hätten jemanden wie Euch nötig. Na, egal, sprecht mit Smeed Schraubenbuddel, der wird Euch Euren Verdienst auszahlen.' WHERE entry=-1001197;
