UPDATE script_texts SET content_loc3='Gebt mir Deckung, wir müssen jetzt schnell handeln. Sobald ich die Versorgung auf dieser Seite weggesprengt habe, werden die Energieströme unterbrochen und sie wird daraufhin loswüten! Ich bezweifle, dass sie sich über unseren Besuch freuen wird.' WHERE entry=-1001208;
