UPDATE script_texts SET content_loc3='Du dumme alte Vettel... Warum sollte ich das tun, wenn ich Euch beide haben kann?' WHERE entry=-1001270;
UPDATE script_texts SET content_loc3='Nein! Was habe ich getan?' WHERE entry=-1001271;
UPDATE script_texts SET content_loc3='Licht, gibt mir Stärke!' WHERE entry=-1001272;
