UPDATE script_texts SET content_loc3='Das hätte besser laufen können, $N. Kommt, ich habe etwas für Euch.' WHERE entry=-1001277;
UPDATE script_texts SET content_loc3='Wer ruft mich in diese Welt? Die Sterne sind noch nicht richtig ausgerichtet... meine Kräfte verlassen mich! Dafür werdet Ihr bezahlen!' WHERE entry=-1001278;
UPDATE script_texts SET content_loc3='Zeigt mir, aus welchem Holz Ihr geschnitzt seid, $N!' WHERE entry=-1001279;
UPDATE script_texts SET content_loc3='Krraa! Ihr könnt mich nicht töten, ich bin unsterblich!' WHERE entry=-1001280;
UPDATE script_texts SET content_loc3='%s wird wütend, als sein Schild zerbirst.' WHERE entry=-1001281;
