UPDATE script_texts SET content_loc3='Schnell! Nutzt die Flammen und unterstützt die Bodentruppen. Die alte Magie sollte Terokks Schild reinigen.' WHERE entry=-1001283;
UPDATE script_texts SET content_loc3='Sie haben es geschafft! Der Feind ist erledigt! Kehrt zum Stützpunkt zurück!' WHERE entry=-1001284;
