UPDATE script_texts SET content_loc3='Meine... meine Zeit ist abgelaufen. Ich bin erledigt. Macht mir ein Ende, $N. Tut es oder sie werden uns beide töten. $N... Denkt an Elwynn. Diese Welt ist es wert, gerettet zu werden.' WHERE entry=-1609072;
UPDATE script_texts SET content_loc3='Meine... meine Zeit ist abgelaufen. Ich bin erledigt. Macht mir ein Ende, $N. Tut es oder sie werden uns beide töten. $N... Denkt an Teldrassil, unsere geliebte Heimat. Diese Welt ist es wert, gerettet zu werden.' WHERE entry=-1609073;
UPDATE script_texts SET content_loc3='Meine... meine Zeit ist abgelaufen. Ich bin erledigt. Macht mir ein Ende, $N. Tut es oder sie werden uns beide töten. $N... Für die Horde! Diese Welt ist es wert, gerettet zu werden.' WHERE entry=-1609074;
UPDATE script_texts SET content_loc3='Meine... meine Zeit ist abgelaufen. Ich bin erledigt. Macht mir ein Ende, $N. Tut es oder sie werden uns beide töten. $N... Denkt an Mulgore! Diese Welt ist es wert, gerettet zu werden.' WHERE entry=-1609075;
UPDATE script_texts SET content_loc3='Meine... Meine Zeit is\' abgelauf\'n. Ich bin erledigt. Mach mir ein Ende, $N. Tu es oder sie werd\'n uns beide töten. $N... Denk an Sen\'jin, Mann! Diese Welt is\' es wert, gerettet zu werd\'n!' WHERE entry=-1609076;
UPDATE script_texts SET content_loc3='Tut es, $N! Erlöst mich von meinen Qualen!' WHERE entry=-1609077;
UPDATE script_texts SET content_loc3='%s erliegt $Gseinen:ihren; Verletzungen.' WHERE entry=-1609078;
UPDATE script_texts SET content_loc3='Ich muss meine Runenklinge und meine Rüstung holen... Brauche nur ein wenig mehr Zeit.' WHERE entry=-1609079;
UPDATE script_texts SET content_loc3='Ich bin noch immer schwach, doch ich glaube, dass ich eine antimagische Barriere hochziehen kann. Bleibt innerhalb derselben oder Ihr werdet von ihren Zaubern vernichtet.' WHERE entry=-1609080;
UPDATE script_texts SET content_loc3='Diese Barriere aufrechtzuerhalten wird all meiner Konzentration bedürfen. Tötet sie alle.' WHERE entry=-1609081;
UPDATE script_texts SET content_loc3='Es kommen noch mehr. Verteidigt Euch! Fallt nicht aus dem Antimagiefeld! Ohne seinen Schutz reißen sie Euch in Stücke!' WHERE entry=-1609082;
UPDATE script_texts SET content_loc3='Ich kann diese Barriere nicht länger aufrechterhalten... Wo ist dieser Feigling?' WHERE entry=-1609083;
UPDATE script_texts SET content_loc3='Der Hochinquisitor kommt! Macht Euch bereit Todesritter! Lasst ihn Euch nicht aus der Schutzzone meines antimagischen Felds ziehn! Tötet ihn und nehmt seinen Kopf!' WHERE entry=-1609084;
UPDATE script_texts SET content_loc3='Bleibt im antimagischen Feld! Lasst ihn zu Euch kommen!' WHERE entry=-1609085;
UPDATE script_texts SET content_loc3='Der Tod des Hochinquisitors von Neu-Avalon wird nicht unbemerkt bleiben. Ihr müsst hier sofort verschwinden! geht, bevor noch mehr von ihnen auftauchen. ich komme schon alleine klar.' WHERE entry=-1609086;
UPDATE script_texts SET content_loc3='Ich werde ihr Feuer auf mich ziehen, auf dass Ihr hinter mir entkommen könnt.' WHERE entry=-1609087;
UPDATE script_texts SET content_loc3='Euer Hochinquisitor ist nicht mehr als ein Fleischstapel, Kreuzzügler! Niemand kann sich dem Griff der Geißel entziehen!' WHERE entry=-1609088;
UPDATE script_texts SET content_loc3='Das Auge von Acherus bewegt sich in Richtung Zielpunkt' WHERE entry=-1609089;
UPDATE script_texts SET content_loc3='Das Auge von Acherus ist nun unter Eurer Kontrolle' WHERE entry=-1609090;
UPDATE script_texts SET content_loc3='Mama?' WHERE entry=-1609091;
UPDATE script_texts SET content_loc3='GEBT MIR GEHIRNE!' WHERE entry=-1609092;
UPDATE script_texts SET content_loc3='Muss fressen...' WHERE entry=-1609093;
UPDATE script_texts SET content_loc3='So hungrig...' WHERE entry=-1609094;
UPDATE script_texts SET content_loc3='$GPapa:Mama;!' WHERE entry=-1609095;
UPDATE script_texts SET content_loc3='Es wirft den Ghul in die Grube oder sonst gibt es die Peitsche!' WHERE entry=-1609096;
UPDATE script_texts SET content_loc3='In die Grube, bevor ich dich in einen Haufen Asche und Exkremente verwandle!' WHERE entry=-1609097;
-- UPDATE script_texts SET content_loc3='' WHERE entry=-1609098; -- ungenutzt
UPDATE script_texts SET content_loc3='Soldaten der Geißel macht euch bereit! Macht euch bereit euren Zorn an der Argentumdämmerung zu kühlen!' WHERE entry=-1609201;
UPDATE script_texts SET content_loc3='Der Himmel weint über die glorreiche Verwüstung dieser Ländereien! Bald schon werden die vergeblichen Tränen Azeroths auf uns herabregnen!' WHERE entry=-1609202;
UPDATE script_texts SET content_loc3='Todesritter von Acherus, der Todesmarsch beginnt!' WHERE entry=-1609203;
UPDATE script_texts SET content_loc3='Soldaten der Geißel, Todesritter von Acherus, Diener der Dunkelheit, vernehmt den Ruf des Hochlords!' WHERE entry=-1609204;
UPDATE script_texts SET content_loc3='ERHEBT EUCH!' WHERE entry=-1609205;
UPDATE script_texts SET content_loc3='Der Himmel färbt sich rot vom Blut der Gefallenen! Der Lichkönig wacht über uns, Diener! Hinterlasst nur Asche und Verderben auf euren zerstörerischen Weg!' WHERE entry=-1609206;
UPDATE script_texts SET content_loc3='Die Geißelarmee ist eingetroffen!' WHERE entry=-1609207;
UPDATE script_texts SET content_loc3='Gebt nicht auf, Brüder und Schwestern! Das Licht wird siegen!' WHERE entry=-1609208;
UPDATE script_texts SET content_loc3='Kniet vor dem Hochlord!' WHERE entry=-1609209;
UPDATE script_texts SET content_loc3='Ihr habt keine Chance!' WHERE entry=-1609210;
UPDATE script_texts SET content_loc3='Die Geißel wird diesen Ort zerstören!' WHERE entry=-1609211;
UPDATE script_texts SET content_loc3='Euer Leben ist verwirkt.' WHERE entry=-1609212;
UPDATE script_texts SET content_loc3='Ein Leben ohne Leiden ist bedeutungslos.' WHERE entry=-1609213;
UPDATE script_texts SET content_loc3='Wie lange werden eure Streitkräfte wohl noch aushalten?' WHERE entry=-1609214;
UPDATE script_texts SET content_loc3='Die Argentumdämmerung ist erledigt!' WHERE entry=-1609215;
UPDATE script_texts SET content_loc3='Lasst keinen am Leben!' WHERE entry=-1609216;
UPDATE script_texts SET content_loc3='Was ist das?! Mein... Ich kann nicht zuschlagen...' WHERE entry=-1609217;
UPDATE script_texts SET content_loc3='Gehorche mir, Klinge!' WHERE entry=-1609218;
UPDATE script_texts SET content_loc3='Du wirst tun was ich dir befehle! Ich habe die Kontrolle!' WHERE entry=-1609219;
UPDATE script_texts SET content_loc3='Ich... ich kann nicht... die Klinge kämpft gegen mich an.' WHERE entry=-1609220;
UPDATE script_texts SET content_loc3='Was geschieht mit mir?' WHERE entry=-1609221;
UPDATE script_texts SET content_loc3='Meine Kraft... schwindet...' WHERE entry=-1609222;
UPDATE script_texts SET content_loc3='Aschenbringer wiedersetzt sich mir...' WHERE entry=-1609223;
UPDATE script_texts SET content_loc3='Diener, helft mir!' WHERE entry=-1609224;
UPDATE script_texts SET content_loc3='Ihr könnt nicht gewinnen, Darion!' WHERE entry=-1609225;
UPDATE script_texts SET content_loc3='Bringt sie zur Kapelle!' WHERE entry=-1609226;
UPDATE script_texts SET content_loc3='Gebt den Kampf auf, Todesritter. Wir haben verloren... das Licht... dieser Ort... keine Hoffnung...' WHERE entry=-1609227;
UPDATE script_texts SET content_loc3='Habt ihr nichts gelernt, Junge? Ihr seid zu all dem geworden was euer Vater immer bekämpft hat! Wie dieser Feigling Arthas, habt ihr zugelassen das Dunkelheit und Hass euch verschlingen... ihr labt euch am Elend derjenigen die ihr gefoltert und getötet habt!' WHERE entry=-1609228;
UPDATE script_texts SET content_loc3='Euer Meister weiß was unterhalb der Kapelle liegt. Deshalb wagt er es nicht sein Gesicht zu zeigen! Er hat euch und eure Todesritter in den Untergang geschickt, Darion.' WHERE entry=-1609229;
UPDATE script_texts SET content_loc3='Was ihr jetzt fühlt sind die Qualen von tausend verlorenen Seelen! Seelen die ihr und euer Meister hierher brachten! Das Licht wird euch zerreisen, Darion!' WHERE entry=-1609230;
UPDATE script_texts SET content_loc3='Spart euch die Worte, alter Mann. Es könnten eure letzten sein.' WHERE entry=-1609231;
UPDATE script_texts SET content_loc3='Mein Sohn! Mein lieber werter Junge!' WHERE entry=-1609232;
UPDATE script_texts SET content_loc3='Vater!' WHERE entry=-1609233;
UPDATE script_texts SET content_loc3='Argh... was... ist...' WHERE entry=-1609234;
UPDATE script_texts SET content_loc3='Vater, Ihr seid zurückgekehrt!' WHERE entry=-1609235;
UPDATE script_texts SET content_loc3='Ihr wart lange fort, Vater. Ich habe gedacht...' WHERE entry=-1609236;
UPDATE script_texts SET content_loc3='Nichts hätte mich davon abhalten können her zu kommen, Darion. Zu meinem Heim und meiner Familie.' WHERE entry=-1609237;
UPDATE script_texts SET content_loc3='Vater, ich möchte euch im Kampf gegen die Untoten beistehen. Ich will kämpfen! Ich kann nicht länger untätig herumsitzen!' WHERE entry=-1609238;
UPDATE script_texts SET content_loc3='Darion Mograine, du bist kaum alt genug ein Schwert zu halten, geschweige denn die untoten Horden von Lordaeron zu bekämpfen! Ich könnte es nicht ertragen dich zu verlieren. Allein der Gedanke daran...' WHERE entry=-1609239;
UPDATE script_texts SET content_loc3='Wenn ich sterbe, Vater, dann tue ich dies aufrecht und standhaft, die untoten Legionen herrausfordernd! Wenn ich sterbe, so lasst mich gemeinsam mit euch sterben!' WHERE entry=-1609240;
UPDATE script_texts SET content_loc3='Mein Sohn, der Tag wird kommen an dem du Aschenbringer führen und mit ihm für Gerechtigkeit im Land sorgen wirst. Ich zweifle nicht daran, das du an diesen Tag unser Volk mit Stolz erfüllen wirst, und das, dank dir Lordaeron zu einem besseren Ort wird. Aber mein Sohn, noch ist dieser Tag nicht gekommen.' WHERE entry=-1609241;
UPDATE script_texts SET content_loc3='Denke stets daran...' WHERE entry=-1609242;
UPDATE script_texts SET content_loc3='Rührend...' WHERE entry=-1609243;
UPDATE script_texts SET content_loc3='Ihr, habt mich betrogen! Ihr habt uns alle betrogen, Monster! Nun spürt die Macht von Mograine!' WHERE entry=-1609244;
UPDATE script_texts SET content_loc3='Er gehört jetzt mir...' WHERE entry=-1609245;
UPDATE script_texts SET content_loc3='Erbärmlich...' WHERE entry=-1609246;
UPDATE script_texts SET content_loc3='Ihr seid ein verdammtes Monster, Arthas!' WHERE entry=-1609247;
UPDATE script_texts SET content_loc3='Ihr hattet Recht, Fordring. Ich habe sie in den Tod geschickt. Ihre Leben waren wertlos, eures hingegen...' WHERE entry=-1609248;
UPDATE script_texts SET content_loc3='Wie leicht es war, den großen Tirion Fordring aus dem Versteck zu locken. Nun seid ihr schutzlos, Paladin. Nichts wird euch retten...' WHERE entry=-1609249;
UPDATE script_texts SET content_loc3='ZUM ANGRIFF!!!' WHERE entry=-1609250;
UPDATE script_texts SET content_loc3='APOKALYPSE!' WHERE entry=-1609251;
UPDATE script_texts SET content_loc3='Dieser Tag ist nicht heute...' WHERE entry=-1609252;
UPDATE script_texts SET content_loc3='Tirion!' WHERE entry=-1609253;
UPDATE script_texts SET content_loc3='ARTHAS!!!!' WHERE entry=-1609254;
UPDATE script_texts SET content_loc3='Was soll das?' WHERE entry=-1609255;
UPDATE script_texts SET content_loc3='Euer Ende!' WHERE entry=-1609256;
UPDATE script_texts SET content_loc3='Unmöglich...' WHERE entry=-1609257;
UPDATE script_texts SET content_loc3='Es ist... nicht... vorbei...' WHERE entry=-1609258;
UPDATE script_texts SET content_loc3='Unser nächstes Treffen wird nicht auf heiligen Boden stattfinden, Paladin.' WHERE entry=-1609259;
UPDATE script_texts SET content_loc3='Erhebt euch, Darion, und hört zu...' WHERE entry=-1609260;
UPDATE script_texts SET content_loc3='Wir sind alle Zeugen einer schrecklichen Tragödie geworden. Das Blut guter Krieger ist auf diesem Boden vergossen worden! Ehrenhafte Ritter erschlagen, während sie ihr Leben verteidigten, unsere Leben!' WHERE entry=-1609261;
UPDATE script_texts SET content_loc3='Auch wenn wir dies niemals vergessen werden können, so müssen wir in unserer Sache standhaft bleiben!' WHERE entry=-1609262;
UPDATE script_texts SET content_loc3='Der Lichkönig muss für das was er getan hat zur Rechenschaft gezogen werden. Er darf unserer Welt keinen weiteren Schaden zufügen.' WHERE entry=-1609263;
UPDATE script_texts SET content_loc3='Ich gebe euch jetzt ein Versprechen, Brüder und Schwestern: Der Lichkönig wird fallen! Heute fordere ich die Gründung eines Bundes.' WHERE entry=-1609264;
UPDATE script_texts SET content_loc3='Die Argentumdämmerung und der Orden der Silbernen Hand werden zusammenstehen! Wir werden obsiegen, wo so viele vor uns versagt haben!' WHERE entry=-1609265;
UPDATE script_texts SET content_loc3='Wir werden Arthas bekämpfen und wir werden die Mauern von Eiskrone niederreißen!' WHERE entry=-1609266;
UPDATE script_texts SET content_loc3='Der Argentumkreuzzug kommt euch zu holen, Arthas!' WHERE entry=-1609267;
UPDATE script_texts SET content_loc3='Ebenso die Ritter der Schwarzen Klinge... Obwohl unsere Art keinen Platz in euer Welt hat, werden wir dafür kämpfen, den Lichkönig zu Fall zu bringen. Das schwöre ich!' WHERE entry=-1609268;
UPDATE script_texts SET content_loc3='Tausende der Geißel erheben sich auf Befehl des Hochlords' WHERE entry=-1609269;
UPDATE script_texts SET content_loc3='Die Armee marschiert in Richtung Kapelle des hoffnungsvollen Lichts' WHERE entry=-1609270;
UPDATE script_texts SET content_loc3='Nachdem hunderte Verteidiger des Lichts gefallen sind, erscheint Hochlord Tirion Fordring' WHERE entry=-1609271;
UPDATE script_texts SET content_loc3='%s flieht.' WHERE entry=-1609272;
